    expect(startsWithW.map(e => e.id)).toEqual(['box']); // wooden box

    // Find entities with names containing 'key'
    const containsKey = builder
      .hasTrait(TraitType.IDENTITY)
      .where(TraitType.IDENTITY, 'name', QueryOperator.CONTAINS, 'key')
      .execute();

    expect(containsKey.map(e => e.id)).toEqual(['key']); // brass key
  });

// packages/stdlib/src/world-model/services/__tests__/query-builder.test.ts

import { QueryBuilder, QueryOperator } from '../query-builder';
import { EntityStore } from '../../traits/entity-store';
import { IFEntity } from '../../traits/if-entity';
import { IdentityTrait } from '../../traits/standard/identity';
import { PortableTrait } from '../../traits/standard/portable';
import { ContainerTrait } from '../../traits/standard/container';
import { LocationTrait } from '../../traits/standard/location';
import { OpenableTrait } from '../../traits/interactive/openable';
import { LightSourceTrait } from '../../traits/interactive/light-source';
import { TraitType } from '../../traits/trait-types';

describe('QueryBuilder', () => {
  let store: EntityStore;
  let builder: QueryBuilder;

  beforeEach(() => {
    store = new EntityStore();
    builder = new QueryBuilder(store);

    // Create test entities
    const room = new IFEntity('room');
    room.add(new IdentityTrait({ name: 'Living Room' }));
    room.add(new LocationTrait());
    store.set(room);

    const box = new IFEntity('box');
    box.add(new IdentityTrait({ name: 'wooden box', aliases: ['box', 'container'] }));
    box.add(new ContainerTrait({ capacity: 5, open: false }));
    box.add(new OpenableTrait({ open: false }));
    box.add(new LocationTrait({ location: 'room' }));
    store.set(box);

    const key = new IFEntity('key');
    key.add(new IdentityTrait({ name: 'brass key', aliases: ['key'] }));
    key.add(new PortableTrait({ weight: 0.1 }));
    key.add(new LocationTrait({ location: 'box' }));
    store.set(key);

    const lamp = new IFEntity('lamp');
    lamp.add(new IdentityTrait({ name: 'oil lamp' }));
    lamp.add(new PortableTrait({ weight: 2 }));
    lamp.add(new LightSourceTrait({ providing: true, intensity: 'normal' }));
    lamp.add(new LocationTrait({ location: 'room' }));
    store.set(lamp);
  });

  test('should find entities by trait', () => {
    const portable = builder.hasTrait(TraitType.PORTABLE).execute();
    expect(portable.map(e => e.id)).toEqual(['key', 'lamp']);

    const containers = builder.hasTrait(TraitType.CONTAINER).execute();
    expect(containers.map(e => e.id)).toEqual(['box']);
  });

  test('should find entities by multiple traits', () => {
    const openableContainers = builder
      .hasTrait(TraitType.CONTAINER)
      .hasTrait(TraitType.OPENABLE)
      .execute();

    expect(openableContainers.map(e => e.id)).toEqual(['box']);

    const portableLights = builder
      .hasTrait(TraitType.PORTABLE)
      .hasTrait(TraitType.LIGHT_SOURCE)
      .execute();

    expect(portableLights.map(e => e.id)).toEqual(['lamp']);
  });

  test('should find entities without traits', () => {
    const nonPortable = builder.notHasTrait(TraitType.PORTABLE).execute();
    expect(nonPortable.map(e => e.id)).toEqual(['room', 'box']);

    const nonContainers = builder.notHasTrait(TraitType.CONTAINER).execute();
    expect(nonContainers.map(e => e.id)).toEqual(['room', 'key', 'lamp']);
  });

  test('should filter by trait properties', () => {
    // Find closed containers
    const closed = builder
      .hasTrait(TraitType.CONTAINER)
      .where(TraitType.CONTAINER, 'open', QueryOperator.EQUALS, false)
      .execute();

    expect(closed.map(e => e.id)).toEqual(['box']);

    // Find light items (weight < 1)
    const lightItems = builder
      .hasTrait(TraitType.PORTABLE)
      .where(TraitType.PORTABLE, 'weight', QueryOperator.LESS_THAN, 1)
      .execute();

    expect(lightItems.map(e => e.id)).toEqual(['key']);
  });

  test('should use string operators', () => {
    // Find entities with names starting with 'w'
    const startsWithW = builder
      .hasTrait(TraitType.IDENTITY)
      .where(TraitType.IDENTITY, 'name', QueryOperator.STARTS_WITH, 'w')
      .execute();

    expect(startsWithW.map(e => e.id)).toEqual(['box']); // wooden box

    // Find entities with names containing 'key'
    const containsKey = builder
      .hasTrait(TraitType.IDENTITY)
      .where(TraitType.IDENTITY, 'name', QueryOperator.CONTAINS, 'key')
      .execute();

    expect(containsKey.map(e => e.id)).toEqual(['key']); // brass key
  });

  test('should use numeric operators', () => {
    // Add more test items
    const heavyBox = new IFEntity('heavy-box');
    heavyBox.add(new PortableTrait({ weight: 10 }));
    store.set(heavyBox);

    const feather = new IFEntity('feather');
    feather.add(new PortableTrait({ weight: 0.01 }));
    store.set(feather);

    // Less than
    const light = builder
      .hasTrait(TraitType.PORTABLE)
      .where(TraitType.PORTABLE, 'weight', QueryOperator.LESS_THAN, 1)
      .execute();
    expect(light.map(e => e.id).sort()).toEqual(['feather', 'key']);

    // Greater than
    const heavy = builder
      .hasTrait(TraitType.PORTABLE)
      .where(TraitType.PORTABLE, 'weight', QueryOperator.GREATER_THAN, 5)
      .execute();
    expect(heavy.map(e => e.id)).toEqual(['heavy-box']);

    // Less than or equal
    const mediumOrLight = builder
      .hasTrait(TraitType.PORTABLE)
      .where(TraitType.PORTABLE, 'weight', QueryOperator.LESS_THAN_OR_EQUAL, 2)
      .execute();
    expect(mediumOrLight.map(e => e.id).sort()).toEqual(['feather', 'key', 'lamp']);
  });

  test('should use array operators', () => {
    // Find by aliases
    const hasKeyAlias = builder
      .hasTrait(TraitType.IDENTITY)
      .where(TraitType.IDENTITY, 'aliases', QueryOperator.INCLUDES, 'key')
      .execute();

    expect(hasKeyAlias.map(e => e.id)).toEqual(['key']);

    const hasContainerAlias = builder
      .hasTrait(TraitType.IDENTITY)
      .where(TraitType.IDENTITY, 'aliases', QueryOperator.INCLUDES, 'container')
      .execute();

    expect(hasContainerAlias.map(e => e.id)).toEqual(['box']);
  });

  test('should handle spatial queries', () => {
    // Find entities in room
    const inRoom = builder.in('room').execute();
    expect(inRoom.map(e => e.id).sort()).toEqual(['box', 'lamp']);

    // Find entities in box
    const inBox = builder.in('box').execute();
    expect(inBox.map(e => e.id)).toEqual(['key']);

    // Find entities in non-existent location
    const inVoid = builder.in('void').execute();
    expect(inVoid).toEqual([]);
  });

  test('should combine spatial and trait queries', () => {
    // Find portable items in room
    const portableInRoom = builder
      .hasTrait(TraitType.PORTABLE)
      .in('room')
      .execute();

    expect(portableInRoom.map(e => e.id)).toEqual(['lamp']);

    // Find containers in room
    const containersInRoom = builder
      .hasTrait(TraitType.CONTAINER)
      .in('room')
      .execute();

    expect(containersInRoom.map(e => e.id)).toEqual(['box']);
  });

  test('should handle near queries', () => {
    // Near queries would check entities in same location or adjacent
    const nearBox = builder.near('box').execute();
    // In same location as box (room), excluding box itself
    expect(nearBox.map(e => e.id)).toEqual(['lamp']);

    const nearKey = builder.near('key').execute();
    // Nothing else in the box with the key
    expect(nearKey).toEqual([]);
  });

  test('should handle complex queries', () => {
    // Find all closed openable containers in the room
    const closedContainersInRoom = builder
      .hasTrait(TraitType.CONTAINER)
      .hasTrait(TraitType.OPENABLE)
      .where(TraitType.OPENABLE, 'open', QueryOperator.EQUALS, false)
      .in('room')
      .execute();

    expect(closedContainersInRoom.map(e => e.id)).toEqual(['box']);

    // Find all portable light sources that are on
    const activeLights = builder
      .hasTrait(TraitType.PORTABLE)
      .hasTrait(TraitType.LIGHT_SOURCE)
      .where(TraitType.LIGHT_SOURCE, 'providing', QueryOperator.EQUALS, true)
      .execute();

    expect(activeLights.map(e => e.id)).toEqual(['lamp']);
  });

  test('should handle OR queries with anyOf', () => {
    const hasEitherTrait = builder
      .anyOf(q => q
        .hasTrait(TraitType.CONTAINER)
        .hasTrait(TraitType.LIGHT_SOURCE)
      )
      .execute();

    expect(hasEitherTrait.map(e => e.id).sort()).toEqual(['box', 'lamp']);
  });

  test('should reset builder state', () => {
    // First query
    const containers = builder.hasTrait(TraitType.CONTAINER).execute();
    expect(containers.map(e => e.id)).toEqual(['box']);

    // Reset and new query
    builder.reset();
    const portable = builder.hasTrait(TraitType.PORTABLE).execute();
    expect(portable.map(e => e.id)).toEqual(['key', 'lamp']);
  });

  test('should handle empty results', () => {
    // No entities with both container and portable
    const result = builder
      .hasTrait(TraitType.CONTAINER)
      .hasTrait(TraitType.PORTABLE)
      .execute();

    expect(result).toEqual([]);

    // No entities in non-existent location
    const nowhere = builder.in('nowhere').execute();
    expect(nowhere).toEqual([]);
  });

  test('should filter by custom predicate', () => {
    const customFilter = builder
      .hasTrait(TraitType.PORTABLE)
      .filter(entity => {
        const portable = entity.get<PortableTrait>(TraitType.PORTABLE);
        return portable ? portable.weight < 1 : false;
      })
      .execute();

    expect(customFilter.map(e => e.id)).toEqual(['key']);
  });

  test('should handle not-equals operator', () => {
    const notClosed = builder
      .hasTrait(TraitType.OPENABLE)
      .where(TraitType.OPENABLE, 'open', QueryOperator.NOT_EQUALS, false)
      .execute();

    // Since our box is closed (open: false), this should return empty
    expect(notClosed).toEqual([]);

    // Open the box and test again
    const box = store.get('box');
    if (box) {
      const openable = box.get<OpenableTrait>(TraitType.OPENABLE);
      if (openable) openable.open = true;
    }

    const nowOpen = builder
      .hasTrait(TraitType.OPENABLE)
      .where(TraitType.OPENABLE, 'open', QueryOperator.NOT_EQUALS, false)
      .execute();

    expect(nowOpen.map(e => e.id)).toEqual(['box']);
  });

  test('should count results', () => {
    const portableCount = builder.hasTrait(TraitType.PORTABLE).count();
    expect(portableCount).toBe(2); // key and lamp

    const containerCount = builder.hasTrait(TraitType.CONTAINER).count();
    expect(containerCount).toBe(1); // box

    const inRoomCount = builder.in('room').count();
    expect(inRoomCount).toBe(2); // box and lamp
  });

  test('should check existence', () => {
    const hasPortable = builder.hasTrait(TraitType.PORTABLE).exists();
    expect(hasPortable).toBe(true);

    const hasPlayer = builder.hasTrait(TraitType.PLAYER).exists();
    expect(hasPlayer).toBe(false);
  });
});

// packages/stdlib/src/world-model/services/__tests__/scope-service.test.ts

import { ScopeService } from '../scope-service';
import { EntityStore } from '../../traits/entity-store';
import { IFEntity } from '../../traits/if-entity';
import { IdentityTrait } from '../../traits/standard/identity';
import { LocationTrait } from '../../traits/standard/location';
import { ContainerTrait } from '../../traits/standard/container';
import { PortableTrait } from '../../traits/standard/portable';
import { OpenableTrait } from '../../traits/interactive/openable';
import { LightSourceTrait } from '../../traits/interactive/light-source';
import { LightSensitiveTrait } from '../../traits/interactive/light-sensitive';
import { RoomTrait } from '../../traits/standard/room';
import { PlayerTrait } from '../../traits/advanced/player';

describe('ScopeService', () => {
  let store: EntityStore;
  let scope: ScopeService;

  beforeEach(() => {
    store = new EntityStore();
    scope = new ScopeService(store);

    // Create test world
    // Player
    const player = new IFEntity('player');
    player.add(new IdentityTrait({ name: 'You' }));
    player.add(new LocationTrait({ location: 'living-room' }));
    player.add(new PlayerTrait());
    player.add(new ContainerTrait({ capacity: 10 })); // Inventory
    store.set(player);

    // Rooms
    const livingRoom = new IFEntity('living-room');
    livingRoom.add(new IdentityTrait({ name: 'Living Room' }));
    livingRoom.add(new RoomTrait({ 
      description: 'A cozy living room.',
      exits: { north: 'kitchen' }
    }));
    store.set(livingRoom);

    const kitchen = new IFEntity('kitchen');
    kitchen.add(new IdentityTrait({ name: 'Kitchen' }));
    kitchen.add(new RoomTrait({
      description: 'A modern kitchen.',
      exits: { south: 'living-room' }
    }));
    store.set(kitchen);

    // Objects in living room
    const table = new IFEntity('table');
    table.add(new IdentityTrait({ name: 'wooden table' }));
    table.add(new LocationTrait({ location: 'living-room' }));
    table.add(new ContainerTrait({ capacity: 5, open: true }));
    store.set(table);

    const box = new IFEntity('box');
    box.add(new IdentityTrait({ name: 'small box' }));
    box.add(new LocationTrait({ location: 'table' }));
    box.add(new ContainerTrait({ capacity: 3, open: false }));
    box.add(new OpenableTrait({ open: false }));
    store.set(box);

    const key = new IFEntity('key');
    key.add(new IdentityTrait({ name: 'brass key' }));
    key.add(new LocationTrait({ location: 'box' }));
    key.add(new PortableTrait({ weight: 0.1 }));
    store.set(key);

    // Light source
    const lamp = new IFEntity('lamp');
    lamp.add(new IdentityTrait({ name: 'oil lamp' }));
    lamp.add(new LocationTrait({ location: 'living-room' }));
    lamp.add(new LightSourceTrait({ providing: false }));
    lamp.add(new PortableTrait({ weight: 2 }));
    store.set(lamp);
  });

  test('should calculate basic scope', () => {
    const playerScope = scope.calculateScope('player');

    expect(playerScope.observer).toBe('player');
    expect(playerScope.visible.has('living-room')).toBe(true);
    expect(playerScope.visible.has('table')).toBe(true);
    expect(playerScope.visible.has('lamp')).toBe(true);
    expect(playerScope.visible.has('box')).toBe(true); // On table

    // Key is not visible (inside closed box)
    expect(playerScope.visible.has('key')).toBe(false);

    // Kitchen is not visible (different room)
    expect(playerScope.visible.has('kitchen')).toBe(false);
  });

  test('should calculate reachability', () => {
    const playerScope = scope.calculateScope('player');

    // Can reach things in same room
    expect(playerScope.reachable.has('table')).toBe(true);
    expect(playerScope.reachable.has('lamp')).toBe(true);
    expect(playerScope.reachable.has('box')).toBe(true);

    // Cannot reach things in closed containers
    expect(playerScope.reachable.has('key')).toBe(false);

    // Cannot reach things in other rooms
    expect(playerScope.reachable.has('kitchen')).toBe(false);
  });

  test('should handle open containers', () => {
    // Open the box
    const box = store.get('box');
    if (box) {
      const openable = box.get<OpenableTrait>('openable');
      const container = box.get<ContainerTrait>('container');
      if (openable && container) {
        openable.open = true;
        container.open = true;
      }
    }

    const playerScope = scope.calculateScope('player');

    // Now key is visible and reachable
    expect(playerScope.visible.has('key')).toBe(true);
    expect(playerScope.reachable.has('key')).toBe(true);
  });

  test('should handle darkness', () => {
    // Add light-sensitive item
    const book = new IFEntity('book');
    book.add(new IdentityTrait({ name: 'ancient book' }));
    book.add(new LocationTrait({ location: 'living-room' }));
    book.add(new LightSensitiveTrait({ minimumLight: 'normal' }));
    store.set(book);

    // Calculate scope in darkness (lamp is off)
    const darkScope = scope.calculateScope('player');
    expect(darkScope.inDarkness).toBe(true);
    expect(darkScope.lightLevel).toBe('none');

    // Turn on lamp
    const lamp = store.get('lamp');
    if (lamp) {
      const light = lamp.get<LightSourceTrait>('lightSource');
      if (light) light.providing = true;
    }

    const litScope = scope.calculateScope('player');
    expect(litScope.inDarkness).toBe(false);
    expect(litScope.lightLevel).toBe('normal');
  });

  test('should handle inventory visibility', () => {
    // Pick up lamp (move to player's inventory)
    const lamp = store.get('lamp');
    if (lamp) {
      const location = lamp.get<LocationTrait>('location');
      if (location) location.location = 'player';
    }

    const playerScope = scope.calculateScope('player');

    // Carried items are visible and reachable
    expect(playerScope.visible.has('lamp')).toBe(true);
    expect(playerScope.reachable.has('lamp')).toBe(true);
    expect(playerScope.carried.has('lamp')).toBe(true);
  });

  test('should calculate visibility from different locations', () => {
    // Move player to kitchen
    const player = store.get('player');
    if (player) {
      const location = player.get<LocationTrait>('location');
      if (location) location.location = 'kitchen';
    }

    const kitchenScope = scope.calculateScope('player');

    // Can see kitchen, not living room contents
    expect(kitchenScope.visible.has('kitchen')).toBe(true);
    expect(kitchenScope.visible.has('living-room')).toBe(false);
    expect(kitchenScope.visible.has('table')).toBe(false);
    expect(kitchenScope.visible.has('lamp')).toBe(false);
  });

  test('should handle nested containers', () => {
    // Create nested container structure
    const chest = new IFEntity('chest');
    chest.add(new IdentityTrait({ name: 'treasure chest' }));
    chest.add(new LocationTrait({ location: 'living-room' }));
    chest.add(new ContainerTrait({ capacity: 10, open: true }));
    store.set(chest);

    const pouch = new IFEntity('pouch');
    pouch.add(new IdentityTrait({ name: 'leather pouch' }));
    pouch.add(new LocationTrait({ location: 'chest' }));
    pouch.add(new ContainerTrait({ capacity: 3, open: true }));
    store.set(pouch);

    const coin = new IFEntity('coin');
    coin.add(new IdentityTrait({ name: 'gold coin' }));
    coin.add(new LocationTrait({ location: 'pouch' }));
    coin.add(new PortableTrait({ weight: 0.05 }));
    store.set(coin);

    const playerScope = scope.calculateScope('player');

    // All are visible through open containers
    expect(playerScope.visible.has('chest')).toBe(true);
    expect(playerScope.visible.has('pouch')).toBe(true);
    expect(playerScope.visible.has('coin')).toBe(true);

    // All are reachable
    expect(playerScope.reachable.has('chest')).toBe(true);
    expect(playerScope.reachable.has('pouch')).toBe(true);
    expect(playerScope.reachable.has('coin')).toBe(true);
  });

  test('should check visibility between entities', () => {
    // Table and lamp can see each other (same room)
    expect(scope.isVisibleFrom('table', 'lamp')).toBe(true);
    expect(scope.isVisibleFrom('lamp', 'table')).toBe(true);

    // Key cannot be seen from outside closed box
    expect(scope.isVisibleFrom('key', 'player')).toBe(false);

    // Things in different rooms cannot see each other
    const kitchenItem = new IFEntity('pot');
    kitchenItem.add(new LocationTrait({ location: 'kitchen' }));
    store.set(kitchenItem);

    expect(scope.isVisibleFrom('pot', 'table')).toBe(false);
  });

  test('should check reachability between entities', () => {
    // Can reach things in same location
    expect(scope.isReachableFrom('table', 'player')).toBe(true);
    expect(scope.isReachableFrom('lamp', 'player')).toBe(true);

    // Cannot reach through closed containers
    expect(scope.isReachableFrom('key', 'player')).toBe(false);

    // Cannot reach across rooms
    expect(scope.isReachableFrom('kitchen', 'player')).toBe(false);
  });

  test('should handle light propagation', () => {
    // Create room with no light
    const darkRoom = new IFEntity('dark-room');
    darkRoom.add(new RoomTrait({ dark: true }));
    store.set(darkRoom);

    // Move player to dark room
    const player = store.get('player');
    if (player) {
      const location = player.get<LocationTrait>('location');
      if (location) location.location = 'dark-room';
    }

    let playerScope = scope.calculateScope('player');
    expect(playerScope.inDarkness).toBe(true);
    expect(playerScope.lightLevel).toBe('none');

    // Carry lit lamp
    const lamp = store.get('lamp');
    if (lamp) {
      const location = lamp.get<LocationTrait>('location');
      const light = lamp.get<LightSourceTrait>('lightSource');
      if (location && light) {
        location.location = 'player';
        light.providing = true;
      }
    }

    playerScope = scope.calculateScope('player');
    expect(playerScope.inDarkness).toBe(false);
    expect(playerScope.lightLevel).toBe('normal');
  });

  test('should handle multiple light sources', () => {
    // Add bright light
    const brightLamp = new IFEntity('bright-lamp');
    brightLamp.add(new LightSourceTrait({ 
      providing: true, 
      intensity: 'bright' 
    }));
    brightLamp.add(new LocationTrait({ location: 'living-room' }));
    store.set(brightLamp);

    // Add dim light
    const candle = new IFEntity('candle');
    candle.add(new LightSourceTrait({ 
      providing: true, 
      intensity: 'dim' 
    }));
    candle.add(new LocationTrait({ location: 'living-room' }));
    store.set(candle);

    const playerScope = scope.calculateScope('player');

    // Brightest light wins
    expect(playerScope.lightLevel).toBe('bright');
    expect(playerScope.inDarkness).toBe(false);
  });

  test('should handle scope options', () => {
    // Include all entities
    const allScope = scope.calculateScope('player', { includeAll: true });
    expect(allScope.visible.size).toBeGreaterThan(5); // Should include everything

    // Exclude dark check
    const noDarkCheck = scope.calculateScope('player', { checkDarkness: false });
    expect(noDarkCheck.inDarkness).toBe(false); // Darkness check skipped
  });
});

// packages/stdlib/src/world-model/services/__tests__/serialization.test.ts

import { WorldModelService } from '../world-model-service';
import { IFEntity } from '../../traits/if-entity';
import { IdentityTrait } from '../../traits/standard/identity';
import { LocationTrait } from '../../traits/standard/location';
import { ContainerTrait } from '../../traits/standard/container';
import { PortableTrait } from '../../traits/standard/portable';
import { OpenableTrait } from '../../traits/interactive/openable';
import { LockableTrait } from '../../traits/interactive/lockable';
import { PlayerTrait } from '../../traits/advanced/player';
import { NPCTrait } from '../../traits/advanced/npc';
import { ScriptableTrait } from '../../traits/advanced/scriptable';

describe('World Model Serialization', () => {
  let world: WorldModelService;

  beforeEach(() => {
    world = new WorldModelService({
      playerId: 'player',
      startingRoom: 'entrance'
    });
  });

  test('should serialize empty world', () => {
    const state = world.serialize();

    expect(state.version).toBe('1.0');
    expect(state.entities).toEqual([]);
    expect(state.metadata).toBeDefined();
    expect(state.metadata.playerId).toBe('player');
    expect(state.metadata.startingRoom).toBe('entrance');
  });

  test('should serialize entities with traits', () => {
    // Create complex entity
    const chest = world.createEntity('chest', [
      new IdentityTrait({ 
        name: 'treasure chest', 
        description: 'An ornate chest with gold trim.',
        aliases: ['chest', 'trunk']
      }),
      new LocationTrait({ location: 'dungeon' }),
      new ContainerTrait({ capacity: 10, open: false }),
      new OpenableTrait({ open: false }),
      new LockableTrait({ locked: true, keyId: 'gold-key' })
    ]);

    const state = world.serialize();

    expect(state.entities).toHaveLength(1);

    const serializedChest = state.entities[0];
    expect(serializedChest.id).toBe('chest');
    expect(serializedChest.traits).toHaveLength(5);

    // Check trait serialization
    const identityTrait = serializedChest.traits.find(t => t.type === 'identity');
    expect(identityTrait?.data.name).toBe('treasure chest');
    expect(identityTrait?.data.aliases).toEqual(['chest', 'trunk']);

    const lockableTrait = serializedChest.traits.find(t => t.type === 'lockable');
    expect(lockableTrait?.data.locked).toBe(true);
    expect(lockableTrait?.data.keyId).toBe('gold-key');
  });

  test('should deserialize world state', () => {
    // Create initial world
    world.createEntity('room', [
      new IdentityTrait({ name: 'Test Room' })
    ]);

    world.createEntity('player', [
      new IdentityTrait({ name: 'Hero' }),
      new LocationTrait({ location: 'room' }),
      new PlayerTrait({ score: 100, health: 80 })
    ]);

    world.createEntity('sword', [
      new IdentityTrait({ name: 'iron sword' }),
      new LocationTrait({ location: 'room' }),
      new PortableTrait({ weight: 5 })
    ]);

    // Serialize
    const state = world.serialize();

    // Create new world and deserialize
    const newWorld = new WorldModelService({
      playerId: 'player',
      startingRoom: 'void'
    });

    newWorld.deserialize(state);

    // Verify entities exist
    expect(newWorld.entityExists('room')).toBe(true);
    expect(newWorld.entityExists('player')).toBe(true);
    expect(newWorld.entityExists('sword')).toBe(true);

    // Verify traits
    const player = newWorld.getEntity('player');
    expect(player).toBeDefined();

    const identity = player?.get<IdentityTrait>('identity');
    expect(identity?.name).toBe('Hero');

    const playerTrait = player?.get<PlayerTrait>('player');
    expect(playerTrait?.score).toBe(100);
    expect(playerTrait?.health).toBe(80);

    // Verify location
    expect(newWorld.getLocation('player')).toBe('room');
    expect(newWorld.getLocation('sword')).toBe('room');
  });

  test('should preserve complex trait data', () => {
    // Create NPC with complex state
    const npc = world.createEntity('merchant', [
      new NPCTrait({
        mood: 'friendly',
        relationship: 50,
        memory: {
          firstMeeting: 42,
          questGiven: true,
          lastTrade: 100
        },
        goals: ['sell-items', 'gather-rumors'],
        conversationState: 'trading'
      })
    ]);

    // Create scriptable entity
    const artifact = world.createEntity('artifact', [
      new ScriptableTrait({
        scripts: {
          onExamine: 'return "It glows with inner light.";',
          onUse: 'player.addMagic(10); world.trigger("artifact-used");',
          onTake: 'if (!player.hasSpell("levitate")) return "It is too heavy!";'
        }
      })
    ]);

    // Serialize and deserialize
    const state = world.serialize();
    const newWorld = new WorldModelService({ playerId: 'p', startingRoom: 'r' });
    newWorld.deserialize(state);

    // Check NPC
    const deserializedNPC = newWorld.getEntity('merchant');
    const npcTrait = deserializedNPC?.get<NPCTrait>('npc');

    expect(npcTrait?.mood).toBe('friendly');
    expect(npcTrait?.relationship).toBe(50);
    expect(npcTrait?.memory.firstMeeting).toBe(42);
    expect(npcTrait?.memory.questGiven).toBe(true);
    expect(npcTrait?.goals).toEqual(['sell-items', 'gather-rumors']);

    // Check scriptable
    const deserializedArtifact = newWorld.getEntity('artifact');
    const scriptTrait = deserializedArtifact?.get<ScriptableTrait>('scriptable');

    expect(scriptTrait?.scripts.onExamine).toContain('glows with inner light');
    expect(scriptTrait?.scripts.onUse).toContain('addMagic');
    expect(scriptTrait?.scripts.onTake).toContain('levitate');
  });

  test('should handle circular references', () => {
    // Create circular container structure
    const box1 = world.createEntity('box1', [
      new IdentityTrait({ name: 'Box 1' }),
      new ContainerTrait({ capacity: 5 })
    ]);

    const box2 = world.createEntity('box2', [
      new IdentityTrait({ name: 'Box 2' }),
      new LocationTrait({ location: 'box1' }),
      new ContainerTrait({ capacity: 5 })
    ]);

    // This would create a circular reference if allowed
    // box1 -> contains box2 -> contains box1
    // The world model should prevent this

    const state = world.serialize();
    const newWorld = new WorldModelService({ playerId: 'p', startingRoom: 'r' });

    // Should deserialize without errors
    expect(() => newWorld.deserialize(state)).not.toThrow();

    // Verify structure
    expect(newWorld.getLocation('box2')).toBe('box1');
  });

  test('should preserve entity relationships', () => {
    // Create connected rooms
    world.createEntity('room1', [
      new IdentityTrait({ name: 'Room 1' })
    ]);

    world.createEntity('room2', [
      new IdentityTrait({ name: 'Room 2' })
    ]);

    world.createEntity('door', [
      new IdentityTrait({ name: 'wooden door' }),
      new LocationTrait({ location: 'room1' })
    ]);

    // Create container hierarchy
    world.createEntity('table', [
      new ContainerTrait({ capacity: 10 }),
      new LocationTrait({ location: 'room1' })
    ]);

    world.createEntity('box', [
      new ContainerTrait({ capacity: 5 }),
      new LocationTrait({ location: 'table' })
    ]);

    world.createEntity('key', [
      new PortableTrait({ weight: 0.1 }),
      new LocationTrait({ location: 'box' })
    ]);

    // Serialize and deserialize
    const state = world.serialize();
    const newWorld = new WorldModelService({ playerId: 'p', startingRoom: 'r' });
    newWorld.deserialize(state);

    // Verify relationships preserved
    expect(newWorld.getLocation('door')).toBe('room1');
    expect(newWorld.getLocation('table')).toBe('room1');
    expect(newWorld.getLocation('box')).toBe('table');
    expect(newWorld.getLocation('key')).toBe('box');

    // Verify containment
    const room1Contents = newWorld.getContents('room1');
    expect(room1Contents.map(e => e.id)).toContain('table');
    expect(room1Contents.map(e => e.id)).toContain('door');

    const tableContents = newWorld.getContents('table');
    expect(tableContents.map(e => e.id)).toContain('box');

    const boxContents = newWorld.getContents('box');
    expect(boxContents.map(e => e.id)).toContain('key');
  });

  test('should handle version compatibility', () => {
    const state = world.serialize();

    // Modify version
    state.version = '0.9';

    const newWorld = new WorldModelService({ playerId: 'p', startingRoom: 'r' });

    // Should handle version mismatch gracefully
    // In real implementation, might have migration logic
    expect(() => newWorld.deserialize(state)).not.toThrow();
  });

  test('should validate deserialized data', () => {
    const state = {
      version: '1.0',
      entities: [
        {
          id: 'invalid',
          traits: [
            {
              type: 'portable',
              data: {
                weight: -5, // Invalid weight
                bulk: 'large' // Invalid type
              }
            }
          ]
        }
      ],
      metadata: {
        playerId: 'player',
        startingRoom: 'start'
      }
    };

    const newWorld = new WorldModelService({ 
      playerId: 'player', 
      startingRoom: 'start',
      validateTraits: true 
    });

    // Should throw on invalid data if validation is enabled
    expect(() => newWorld.deserialize(state as any)).toThrow();
  });

  test('should support partial serialization', () => {
    // Create many entities
    for (let i = 0; i < 10; i++) {
      world.createEntity(`item${i}`, [
        new IdentityTrait({ name: `Item ${i}` }),
        new LocationTrait({ location: 'room' })
      ]);
    }

    // Full serialization
    const fullState = world.serialize();
    expect(fullState.entities).toHaveLength(10);

    // Could support filtered serialization in future
    // const partialState = world.serialize({ 
    //   filter: (e) => e.id.includes('item1') 
    // });
  });
});

// packages/stdlib/src/world-model/__tests__/integration.test.ts

import { WorldModelService } from '../services/world-model-service';
import { IdentityTrait } from '../traits/standard/identity';
import { LocationTrait } from '../traits/standard/location';
import { ContainerTrait } from '../traits/standard/container';
import { PortableTrait } from '../traits/standard/portable';
import { WearableTrait } from '../traits/standard/wearable';
import { OpenableTrait } from '../traits/interactive/openable';
import { LockableTrait } from '../traits/interactive/lockable';
import { LightSourceTrait } from '../traits/interactive/light-source';
import { PlayerTrait } from '../traits/advanced/player';
import { NPCTrait } from '../traits/advanced/npc';
import { DoorTrait } from '../traits/advanced/door';
import { RoomTrait } from '../traits/standard/room';
import { TraitType } from '../traits/trait-types';

describe('World Model Integration Tests', () => {
  let world: WorldModelService;

  beforeEach(() => {
    world = new WorldModelService({
      playerId: 'player',
      startingRoom: 'entrance-hall',
      enforceCapacity: true,
      validateTraits: true
    });
  });

  test('should create a complete game world', () => {
    // Create rooms
    const entranceHall = world.createEntity('entrance-hall', [
      new IdentityTrait({ 
        name: 'Entrance Hall',
        description: 'A grand entrance with marble floors.'
      }),
      new RoomTrait({
        exits: { north: 'library', east: 'garden' }
      })
    ]);

    const library = world.createEntity('library', [
      new IdentityTrait({
        name: 'Library',
        description: 'Shelves of ancient books line the walls.'
      }),
      new RoomTrait({
        exits: { south: 'entrance-hall', up: 'tower' },
        dark: true
      })
    ]);

    // Create player
    const player = world.createEntity('player', [
      new IdentityTrait({ name: 'You' }),
      new PlayerTrait({ health: 100, maxHealth: 100 }),
      new ContainerTrait({ capacity: 10 }) // Inventory
    ]);

    // Move player to entrance
    world.moveEntity('player', 'entrance-hall');

    // Create objects
    const torch = world.createEntity('torch', [
      new IdentityTrait({ 
        name: 'torch',
        description: 'A wooden torch wrapped in oil-soaked cloth.'
      }),
      new PortableTrait({ weight: 1 }),
      new LightSourceTrait({ providing: false, intensity: 'normal' }),
      new LocationTrait({ location: 'entrance-hall' })
    ]);

    const chest = world.createEntity('chest', [
      new IdentityTrait({ name: 'ornate chest' }),
      new ContainerTrait({ capacity: 20, open: false }),
      new OpenableTrait({ open: false }),
      new LockableTrait({ locked: true, keyId: 'golden-key' }),
      new LocationTrait({ location: 'library' })
    ]);

    // Verify world state
    expect(world.getAllEntities()).toHaveLength(5);
    expect(world.getLocation('player')).toBe('entrance-hall');
    expect(world.getContents('entrance-hall').map(e => e.id)).toContain('torch');
  });

  test('should handle complex player actions', () => {
    // Setup world
    setupTestWorld();

    // Player takes torch
    const torch = world.getEntity('torch');
    world.moveEntity('torch', 'player');

    // Verify torch is in inventory
    expect(world.getLocation('torch')).toBe('player');
    const playerContents = world.getContents('player');
    expect(playerContents.map(e => e.id)).toContain('torch');

    // Light torch
    const lightTrait = torch?.get<LightSourceTrait>(TraitType.LIGHT_SOURCE);
    if (lightTrait) lightTrait.providing = true;

    // Move to dark library
    world.moveEntity('player', 'library');

    // Check visibility with torch
    const scope = world.getScope().calculateScope('player');
    expect(scope.inDarkness).toBe(false); // Torch provides light
    expect(scope.visible.has('chest')).toBe(true);

    // Try to open locked chest
    const chest = world.getEntity('chest');
    const lockable = chest?.get<LockableTrait>(TraitType.LOCKABLE);
    expect(lockable?.locked).toBe(true);

    // Find and use key
    world.createEntity('golden-key', [
      new IdentityTrait({ name: 'golden key' }),
      new PortableTrait({ weight: 0.1 }),
      new LocationTrait({ location: 'library' })
    ]);

    world.moveEntity('golden-key', 'player');

    // Unlock chest
    if (lockable && world.query().hasTrait(TraitType.PORTABLE).execute()
        .find(e => e.id === 'golden-key')) {
      lockable.locked = false;
      const openable = chest?.get<OpenableTrait>(TraitType.OPENABLE);
      if (openable) openable.open = true;
    }

    // Put treasure in chest
    world.createEntity('treasure', [
      new IdentityTrait({ name: 'ancient treasure' }),
      new PortableTrait({ weight: 5 }),
      new LocationTrait({ location: 'chest' })
    ]);

    // Verify treasure is in chest
    const chestContents = world.getContents('chest');
    expect(chestContents.map(e => e.id)).toContain('treasure');
  });

  test('should handle NPC interactions', () => {
    setupTestWorld();

    // Create merchant NPC
    const merchant = world.createEntity('merchant', [
      new IdentityTrait({ 
        name: 'Friendly Merchant',
        description: 'A jovial merchant with a large pack.'
      }),
      new NPCTrait({
        mood: 'friendly',
        relationship: 0,
        goals: ['sell-items', 'make-profit']
      }),
      new LocationTrait({ location: 'entrance-hall' })
    ]);

    // Player interacts with merchant
    const npcTrait = merchant.get<NPCTrait>(TraitType.NPC);

    // First meeting
    npcTrait?.remember('metPlayer', true);
    npcTrait?.setConversationState('greeted');

    // Buy something (improves relationship)
    npcTrait?.adjustRelationship(10);
    expect(npcTrait?.relationship).toBe(10);

    // Complete quest
    npcTrait?.remember('questCompleted', true);
    npcTrait?.adjustRelationship(50);
    npcTrait?.setMood('happy');

    expect(npcTrait?.mood).toBe('happy');
    expect(npcTrait?.relationship).toBe(60);
    expect(npcTrait?.recalls('questCompleted')).toBe(true);
  });

  test('should handle clothing and equipment', () => {
    setupTestWorld();

    // Create wearable items
    const cloak = world.createEntity('cloak', [
      new IdentityTrait({ name: 'traveler\'s cloak' }),
      new WearableTrait({ bodyPart: 'back', layer: 2 }),
      new PortableTrait({ weight: 2 }),
      new LocationTrait({ location: 'entrance-hall' })
    ]);

    const boots = world.createEntity('boots', [
      new IdentityTrait({ name: 'leather boots' }),
      new WearableTrait({ bodyPart: 'feet', layer: 1 }),
      new PortableTrait({ weight: 1 }),
      new LocationTrait({ location: 'entrance-hall' })
    ]);

    // Player takes and wears items
    world.moveEntity('cloak', 'player');
    world.moveEntity('boots', 'player');

    const player = world.getEntity('player');
    const playerTrait = player?.get<PlayerTrait>(TraitType.PLAYER);

    // Wear items
    const cloakWearable = cloak.get<WearableTrait>(TraitType.WEARABLE);
    const bootsWearable = boots.get<WearableTrait>(TraitType.WEARABLE);

    if (cloakWearable && playerTrait) {
      cloakWearable.wornBy = 'player';
      playerTrait.wear('cloak');
    }

    if (bootsWearable && playerTrait) {
      bootsWearable.wornBy = 'player';
      playerTrait.wear('boots');
    }

    // Verify worn items
    expect(playerTrait?.isWearing('cloak')).toBe(true);
    expect(playerTrait?.isWearing('boots')).toBe(true);
    expect(cloakWearable?.isWorn()).toBe(true);
    expect(bootsWearable?.isWorn()).toBe(true);
  });

  test('should handle doors and room connections', () => {
    // Create connected rooms
    const hall = world.createEntity('hall', [
      new IdentityTrait({ name: 'Hall' }),
      new RoomTrait({ exits: {} })
    ]);

    const chamber = world.createEntity('chamber', [
      new IdentityTrait({ name: 'Chamber' }),
      new RoomTrait({ exits: {} })
    ]);

    // Create door between rooms
    const door = world.createEntity('oak-door', [
      new IdentityTrait({ name: 'oak door' }),
      new DoorTrait({
        connects: ['hall', 'chamber'],
        direction: { hall: 'north', chamber: 'south' }
      }),
      new OpenableTrait({ open: false }),
      new LocationTrait({ location: 'hall' })
    ]);

    // Update room exits
    const hallRoom = hall.get<RoomTrait>(TraitType.ROOM);
    const chamberRoom = chamber.get<RoomTrait>(TraitType.ROOM);
    if (hallRoom) hallRoom.exits.north = 'oak-door';
    if (chamberRoom) chamberRoom.exits.south = 'oak-door';

    // Test door functionality
    const doorTrait = door.get<DoorTrait>(TraitType.DOOR);
    expect(doorTrait?.getOtherRoom('hall')).toBe('chamber');
    expect(doorTrait?.getOtherRoom('chamber')).toBe('hall');
    expect(doorTrait?.canTraverse('hall', 'chamber')).toBe(true);

    // Player uses door
    world.moveEntity('player', 'hall');

    const openable = door.get<OpenableTrait>(TraitType.OPENABLE);
    if (openable) openable.open = true;

    // Move through door
    world.moveEntity('player', 'chamber');
    expect(world.getLocation('player')).toBe('chamber');
  });

  test('should save and restore game state', () => {
    // Create complex world state
    setupTestWorld();

    // Add player progress
    const player = world.getEntity('player');
    const playerTrait = player?.get<PlayerTrait>(TraitType.PLAYER);
    if (playerTrait) {
      playerTrait.score = 150;
      playerTrait.moves = 42;
      playerTrait.addToInventory('torch');
      playerTrait.addToInventory('key');
    }

    // Move some entities
    world.moveEntity('torch', 'player');
    world.moveEntity('player', 'library');

    // Serialize state
    const savedState = world.serialize();

    // Create new world
    const restoredWorld = new WorldModelService({
      playerId: 'player',
      startingRoom: 'void'
    });

    // Restore state
    restoredWorld.deserialize(savedState);

    // Verify everything is restored
    const restoredPlayer = restoredWorld.getEntity('player');
    const restoredPlayerTrait = restoredPlayer?.get<PlayerTrait>(TraitType.PLAYER);

    expect(restoredPlayerTrait?.score).toBe(150);
    expect(restoredPlayerTrait?.moves).toBe(42);
    expect(restoredWorld.getLocation('player')).toBe('library');
    expect(restoredWorld.getLocation('torch')).toBe('player');
    expect(restoredWorld.getAllEntities()).toHaveLength(world.getAllEntities().length);
  });

  // Helper function to set up test world
  function setupTestWorld() {
    // Rooms
    world.createEntity('entrance-hall', [
      new IdentityTrait({ name: 'Entrance Hall' }),
      new RoomTrait({ exits: { north: 'library' } })
    ]);

    world.createEntity('library', [
      new IdentityTrait({ name: 'Library' }),
      new RoomTrait({ exits: { south: 'entrance-hall' }, dark: true })
    ]);

    // Player
    world.createEntity('player', [
      new IdentityTrait({ name: 'You' }),
      new PlayerTrait(),
      new ContainerTrait({ capacity: 10 }),
      new LocationTrait({ location: 'entrance-hall' })
    ]);

    // Objects
    world.createEntity('torch', [
      new IdentityTrait({ name: 'torch' }),
      new PortableTrait({ weight: 1 }),
      new LightSourceTrait({ providing: false }),
      new LocationTrait({ location: 'entrance-hall' })
    ]);

    world.createEntity('chest', [
      new IdentityTrait({ name: 'chest' }),
      new ContainerTrait({ capacity: 20, open: false }),
      new OpenableTrait({ open: false }),
      new LockableTrait({ locked: true, keyId: 'golden-key' }),
      new LocationTrait({ location: 'library' })
    ]);
  }
});
