/**
 * IF-specific entity types and interfaces
 */

import { Entity, EntityId } from '../types';

/**
 * Standard IF entity types
 */
export enum CoreIFEntityType {
  ROOM = 'room',
  THING = 'thing',
  CONTAINER = 'container',
  SUPPORTER = 'supporter',
  DOOR = 'door',
  PERSON = 'person',
  DEVICE = 'device',
  PLAYER = 'player'
}

/**
 * Base interface for all IF entities with common properties
 */
export interface IFEntity extends Entity {
  type: CoreIFEntityType;
  attributes: IFAttributes;
}

/**
 * Common attributes for IF entities
 */
export interface IFAttributes extends Record<string, unknown> {
  // Basic properties
  name: string;
  description?: string;
  initialDescription?: string;
  adjectives?: string[];
  synonyms?: string[];
  article?: 'a' | 'an' | 'the' | 'some';
  plural?: boolean;
  proper?: boolean; // Proper nouns don't use articles

  // Visibility
  visible?: boolean;
  concealed?: boolean;
  scenery?: boolean; // Background objects, not listed in room descriptions
  mentioned?: boolean; // Has been mentioned in room description

  // Interaction
  takeable?: boolean;
  fixed?: boolean; // Cannot be taken
  edible?: boolean;
  wearable?: boolean;
  worn?: boolean;

  // Container/Supporter
  container?: boolean;
  supporter?: boolean;
  transparent?: boolean; // Can see contents when closed
  capacity?: number; // Max items, -1 for unlimited
  bulk?: number; // How much space this takes up

  // Openable
  openable?: boolean;
  open?: boolean;
  lockable?: boolean;
  locked?: boolean;
  key?: EntityId;

  // Light
  lit?: boolean; // Currently providing light
  providesLight?: boolean; // Can provide light when lit
  requiresLight?: boolean; // Needs light to be visible

  // Text/Readable
  readable?: boolean;
  text?: string;

  // Device properties  
  switchable?: boolean;
  on?: boolean;

  // State tracking
  visited?: boolean; // For rooms
  touched?: boolean; // Has player interacted with this
  moved?: boolean; // Has been moved from initial position

  // Character properties
  animate?: boolean; // Is this a living being
  refuses?: EntityId[]; // Items this entity won't accept
}

/**
 * Room-specific entity
 */
export interface Room extends IFEntity {
  type: CoreIFEntityType.ROOM;
  attributes: RoomAttributes;
}

export interface RoomAttributes extends IFAttributes {
  // Room-specific
  dark?: boolean;
  visited?: boolean;
  indoors?: boolean;

  // Cannot have certain properties
  takeable?: false;
  fixed?: true;
}

/**
 * Thing - basic object in the world
 */
export interface Thing extends IFEntity {
  type: CoreIFEntityType.THING;
  attributes: ThingAttributes;
}

export interface ThingAttributes extends IFAttributes {
  // Things have all standard attributes
}

/**
 * Container - can hold other objects inside
 */
export interface Container extends IFEntity {
  type: CoreIFEntityType.CONTAINER;
  attributes: ContainerAttributes;
}

export interface ContainerAttributes extends IFAttributes {
  container: true;
  capacity?: number;
  transparent?: boolean;
}

/**
 * Supporter - can hold objects on top
 */
export interface Supporter extends IFEntity {
  type: CoreIFEntityType.SUPPORTER;
  attributes: SupporterAttributes;
}

export interface SupporterAttributes extends IFAttributes {
  supporter: true;
  capacity?: number;
}

/**
 * Door - connects two rooms
 */
export interface Door extends IFEntity {
  type: CoreIFEntityType.DOOR;
  attributes: DoorAttributes;
}

export interface DoorAttributes extends IFAttributes {
  openable: true;
  open?: boolean;
  lockable?: boolean;
  locked?: boolean;
  key?: EntityId;

  // Doors are always fixed
  takeable?: false;
  fixed?: true;
}

/**
 * Person - NPCs and the player
 */
export interface Person extends IFEntity {
  type: CoreIFEntityType.PERSON;
  attributes: PersonAttributes;
}

export interface PersonAttributes extends IFAttributes {
  proper?: boolean; // Most people have proper names
  gender?: 'male' | 'female' | 'neutral' | 'unknown';
  animate?: true; // People are always animate

  // People are not usually takeable
  takeable?: false;
  fixed?: true;
}

/**
 * Device - switchable objects
 */
export interface Device extends IFEntity {
  type: CoreIFEntityType.DEVICE;
  attributes: DeviceAttributes;
}

export interface DeviceAttributes extends IFAttributes {
  switchable: true;
  on?: boolean;
  providesLight?: boolean; // Many devices are light sources
}

/**
 * Type guards for IF entities
 */
export function isRoom(entity: Entity): entity is Room {
  return entity.type === CoreIFEntityType.ROOM;
}

export function isThing(entity: Entity): entity is Thing {
  return entity.type === CoreIFEntityType.THING;
}

export function isContainer(entity: Entity): entity is Container {
  return entity.type === CoreIFEntityType.CONTAINER || 
    (entity.attributes.container === true);
}

export function isSupporter(entity: Entity): entity is Supporter {
  return entity.type === CoreIFEntityType.SUPPORTER ||
    (entity.attributes.supporter === true);
}

export function isDoor(entity: Entity): entity is Door {
  return entity.type === CoreIFEntityType.DOOR;
}

export function isPerson(entity: Entity): entity is Person {
  return entity.type === CoreIFEntityType.PERSON || 
    entity.type === CoreIFEntityType.PLAYER;
}

export function isDevice(entity: Entity): entity is Device {
  return entity.type === CoreIFEntityType.DEVICE ||
    (entity.attributes.switchable === true);
}

/**
 * Check if an entity can contain other entities
 */
export function canContain(entity: Entity): boolean {
  return isContainer(entity) || isSupporter(entity) || isRoom(entity) || isPerson(entity);
}

/**
 * Check if an entity is portable
 */
export function isPortable(entity: Entity): boolean {
  const attrs = entity.attributes as IFAttributes;
  return attrs.takeable === true && attrs.fixed !== true;
}

/**
 * Check if an entity provides light
 */
export function providesLight(entity: Entity): boolean {
  const attrs = entity.attributes as IFAttributes;
  return attrs.lit === true && attrs.providesLight === true;
}

/**
 * IF World Model
 * 
 * A simplified world model specifically designed for interactive fiction.
 * Maintains the graph-based structure but adds IF-specific concepts.
 */

import { Entity, EntityId } from '../types';
import { 
  IFEntity, 
  IFEntityType, 
  Room, 
  Thing, 
  Container, 
  Door,
  Person,
  isRoom,
  isContainer,
  isSupporter,
  isPerson,
  canContain
} from '../if-entities/types';
import { IFRelationship, Direction, directionToRelationship } from '../if-entities/relationships';
import { EventEmitter } from './if-event-emitter';
import { LocationTracker } from './location-tracker';
import { ScopeCalculator } from './scope-calculator';

/**
 * Events emitted by the world model
 */
export interface IFWorldEvents {
  'entity:created': { entity: IFEntity };
  'entity:updated': { entity: IFEntity; previous: IFEntity };
  'entity:deleted': { id: EntityId };
  'entity:moved': { entity: IFEntity; from: EntityId | null; to: EntityId | null };
  'relationship:added': { from: EntityId; to: EntityId; type: IFRelationship };
  'relationship:removed': { from: EntityId; to: EntityId; type: IFRelationship };
}

/**
 * Configuration for the IF world
 */
export interface IFWorldConfig {
  /** ID of the player entity */
  playerId: EntityId;
  /** Starting room ID */
  startingRoom?: EntityId;
  /** Whether to validate capacity constraints */
  enforceCapacity?: boolean;
  /** Whether to track entity history */
  trackHistory?: boolean;
}

/**
 * The main IF world model
 */
export class IFWorld extends EventEmitter<IFWorldEvents> {
  private entities: Map<EntityId, IFEntity> = new Map();
  private locationTracker: LocationTracker;
  private scopeCalculator: ScopeCalculator;
  private config: Required<IFWorldConfig>;

  constructor(config: IFWorldConfig) {
    super();
    this.config = {
      startingRoom: config.startingRoom || 'void',
      enforceCapacity: config.enforceCapacity ?? true,
      trackHistory: config.trackHistory ?? false,
      ...config
    };

    this.locationTracker = new LocationTracker();
    this.scopeCalculator = new ScopeCalculator(this);
  }

  /**
   * Add an entity to the world
   */
  addEntity(entity: IFEntity, location?: EntityId): void {
    // Validate entity
    this.validateEntity(entity);

    // Store entity
    this.entities.set(entity.id, entity);

    // Set initial location
    if (location) {
      this.locationTracker.setLocation(entity.id, location);
    }

    // Emit event
    this.emit('entity:created', { entity });
  }

  /**
   * Update an entity
   */
  updateEntity(id: EntityId, updates: Partial<IFEntity>): void {
    const entity = this.getEntity(id);
    if (!entity) {
      throw new Error(`Entity ${id} not found`);
    }

    const previous = { ...entity };
    const updated = {
      ...entity,
      ...updates,
      id: entity.id, // Prevent ID changes
      type: entity.type // Prevent type changes
    } as IFEntity;

    this.entities.set(id, updated);
    this.emit('entity:updated', { entity: updated, previous });
  }

  /**
   * Delete an entity
   */
  deleteEntity(id: EntityId): void {
    const entity = this.entities.get(id);
    if (!entity) return;

    // Remove from location tracking
    this.locationTracker.removeEntity(id);

    // Remove all relationships
    for (const [otherId, other] of this.entities) {
      if (otherId === id) continue;

      for (const relType in other.relationships) {
        const rels = other.relationships[relType];
        const index = rels.indexOf(id);
        if (index >= 0) {
          rels.splice(index, 1);
        }
      }
    }

    // Delete entity
    this.entities.delete(id);
    this.emit('entity:deleted', { id });
  }

  /**
   * Get an entity by ID
   */
  getEntity(id: EntityId): IFEntity | undefined {
    return this.entities.get(id);
  }

  /**
   * Get all entities
   */
  getAllEntities(): IFEntity[] {
    return Array.from(this.entities.values());
  }

  /**
   * Move an entity to a new location
   */
  moveEntity(entityId: EntityId, toLocation: EntityId | null): void {
    const entity = this.getEntity(entityId);
    if (!entity) {
      throw new Error(`Entity ${entityId} not found`);
    }

    const toEntity = toLocation ? this.getEntity(toLocation) : null;

    // Validate destination
    if (toLocation && toEntity) {
      if (!canContain(toEntity)) {
        throw new Error(`${toLocation} cannot contain other entities`);
      }

      // Check capacity
      if (this.config.enforceCapacity) {
        const capacity = (toEntity.attributes.capacity as number) ?? -1;
        if (capacity >= 0) {
          const contents = this.getContents(toLocation);
          if (contents.length >= capacity) {
            throw new Error(`${toLocation} is full`);
          }
        }
      }
    }

    // Track movement
    const fromLocation = this.locationTracker.getLocation(entityId);
    this.locationTracker.setLocation(entityId, toLocation);

    // Update relationships
    if (fromLocation) {
      this.removeRelationship(fromLocation, entityId, IFRelationship.CONTAINS);
    }
    if (toLocation) {
      this.addRelationship(toLocation, entityId, IFRelationship.CONTAINS);
    }

    // Emit event
    this.emit('entity:moved', { 
      entity, 
      from: fromLocation, 
      to: toLocation 
    });
  }

  /**
   * Get the current location of an entity
   */
  getLocation(entityId: EntityId): EntityId | null {
    return this.locationTracker.getLocation(entityId);
  }

  /**
   * Get all entities at a location
   */
  getContents(locationId: EntityId): IFEntity[] {
    const contents = this.locationTracker.getContents(locationId);
    return contents
      .map(id => this.getEntity(id))
      .filter((e): e is IFEntity => e !== undefined);
  }

  /**
   * Add a relationship between entities
   */
  addRelationship(fromId: EntityId, toId: EntityId, type: IFRelationship): void {
    const from = this.getEntity(fromId);
    const to = this.getEntity(toId);

    if (!from || !to) {
      throw new Error('Both entities must exist');
    }

    // Initialize relationship array if needed
    if (!from.relationships[type]) {
      from.relationships[type] = [];
    }

    // Add if not already present
    if (!from.relationships[type].includes(toId)) {
      from.relationships[type].push(toId);
      this.emit('relationship:added', { from: fromId, to: toId, type });
    }
  }

  /**
   * Remove a relationship between entities
   */
  removeRelationship(fromId: EntityId, toId: EntityId, type: IFRelationship): void {
    const from = this.getEntity(fromId);
    if (!from || !from.relationships[type]) return;

    const index = from.relationships[type].indexOf(toId);
    if (index >= 0) {
      from.relationships[type].splice(index, 1);
      this.emit('relationship:removed', { from: fromId, to: toId, type });
    }
  }

  /**
   * Add an exit from one room to another
   */
  addExit(fromRoom: EntityId, direction: Direction, toRoom: EntityId): void {
    const from = this.getEntity(fromRoom);
    const to = this.getEntity(toRoom);

    if (!from || !to) {
      throw new Error('Both rooms must exist');
    }

    if (!isRoom(from) || !isRoom(to)) {
      throw new Error('Both entities must be rooms');
    }

    const relationship = directionToRelationship(direction);
    this.addRelationship(fromRoom, toRoom, relationship);
  }

  /**
   * Get room in a direction
   */
  getRoomInDirection(fromRoom: EntityId, direction: Direction): EntityId | undefined {
    const room = this.getEntity(fromRoom);
    if (!room || !isRoom(room)) return undefined;

    const relationship = directionToRelationship(direction);
    const targets = room.relationships[relationship] || [];
    return targets[0]; // Assume one room per direction
  }

  /**
   * Get the player entity
   */
  getPlayer(): IFEntity | undefined {
    return this.getEntity(this.config.playerId);
  }

  /**
   * Get the player's current location
   */
  getPlayerLocation(): EntityId | null {
    return this.locationTracker.getLocation(this.config.playerId);
  }

  /**
   * Calculate scope for the current player location
   */
  calculateScope(): ScopeCalculator.ScopeResult {
    return this.scopeCalculator.calculateScope(this.config.playerId);
  }

  /**
   * Check if an entity is visible to the player
   */
  isVisible(entityId: EntityId): boolean {
    const scope = this.calculateScope();
    return scope.visible.has(entityId);
  }

  /**
   * Check if an entity is reachable by the player
   */
  isReachable(entityId: EntityId): boolean {
    const scope = this.calculateScope();
    return scope.reachable.has(entityId);
  }

  /**
   * Validate an entity
   */
  private validateEntity(entity: IFEntity): void {
    if (!entity.id) {
      throw new Error('Entity must have an ID');
    }

    if (!entity.type) {
      throw new Error('Entity must have a type');
    }

    if (!entity.attributes) {
      throw new Error('Entity must have attributes');
    }

    if (!entity.attributes.name) {
      throw new Error('Entity must have a name');
    }

    // Type-specific validation
    switch (entity.type) {
      case IFEntityType.ROOM:
        if (entity.attributes.takeable === true) {
          throw new Error('Rooms cannot be takeable');
        }
        break;

      case IFEntityType.DOOR:
        if (entity.attributes.takeable === true) {
          throw new Error('Doors cannot be takeable');
        }
        if (entity.attributes.openable !== true) {
          throw new Error('Doors must be openable');
        }
        break;

      case IFEntityType.CONTAINER:
        if (entity.attributes.container !== true) {
          throw new Error('Containers must have container=true');
        }
        break;

      case IFEntityType.DEVICE:
        if (entity.attributes.switchable !== true) {
          throw new Error('Devices must be switchable');
        }
        break;
    }
  }
}

/**
 * Scope calculator for IF visibility and reachability
 * 
 * Determines what entities are visible, reachable, and known to the player
 * based on IF rules for darkness, containers, and distance.
 */

import { EntityId } from '../../core-imports';
import { IFWorld } from './if-world';
import { 
  IFEntity,
  IFAttributes,
  isRoom,
  isContainer,
  isPerson,
  providesLight
} from '../if-entities/types';

export namespace ScopeCalculator {
  /**
   * Result of scope calculation
   */
  export interface ScopeResult {
    /** Entities that can be seen */
    visible: Set<EntityId>;
    /** Entities that can be touched/interacted with */
    reachable: Set<EntityId>;
    /** Entities the player knows about */
    known: Set<EntityId>;
    /** Whether the player is in darkness */
    inDarkness: boolean;
    /** Current light level (0 = pitch black, 1 = normal) */
    lightLevel: number;
  }
}

/**
 * Calculates scope (visibility and reachability) in the IF world
 */
export class ScopeCalculator {
  constructor(private world: IFWorld) {}

  /**
   * Calculate scope for an entity (usually the player)
   */
  calculateScope(viewerId: EntityId): ScopeCalculator.ScopeResult {
    const viewer = this.world.getEntity(viewerId);
    if (!viewer) {
      return {
        visible: new Set(),
        reachable: new Set(),
        known: new Set(),
        inDarkness: true,
        lightLevel: 0
      };
    }

    const viewerLocation = this.world.getLocation(viewerId);
    if (!viewerLocation) {
      return {
        visible: new Set([viewerId]),
        reachable: new Set([viewerId]),
        known: new Set([viewerId]),
        inDarkness: false,
        lightLevel: 1
      };
    }

    // Calculate light level
    const { inDarkness, lightLevel } = this.calculateLighting(viewerLocation, viewerId);

    // Calculate visibility
    const visible = this.calculateVisibility(viewerId, viewerLocation, inDarkness);

    // Calculate reachability (subset of visible)
    const reachable = this.calculateReachability(viewerId, viewerLocation, visible);

    // Calculate known entities (everything the player has seen)
    const known = this.calculateKnown(viewerId);

    return {
      visible,
      reachable,
      known,
      inDarkness,
      lightLevel
    };
  }

  /**
   * Calculate lighting conditions
   */
  private calculateLighting(locationId: EntityId, viewerId: EntityId): {
    inDarkness: boolean;
    lightLevel: number;
  } {
    const location = this.world.getEntity(locationId);
    if (!location || !isRoom(location)) {
      return { inDarkness: false, lightLevel: 1 };
    }

    // Check if room is dark
    const isDark = (location.attributes.dark as boolean) ?? false;
    if (!isDark) {
      return { inDarkness: false, lightLevel: 1 };
    }

    // Look for light sources
    let lightLevel = 0;

    // Check viewer
    if (providesLight(this.world.getEntity(viewerId)!)) {
      lightLevel = 1;
    }

    // Check carried items
    const carried = this.world.getContents(viewerId);
    for (const item of carried) {
      if (providesLight(item)) {
        lightLevel = Math.max(lightLevel, 1);
      }
    }

    // Check room contents
    const roomContents = this.world.getContents(locationId);
    for (const item of roomContents) {
      if (providesLight(item)) {
        lightLevel = Math.max(lightLevel, 0.8);
      }

      // Check inside open containers
      if (isContainer(item) && item.attributes.open) {
        const containerContents = this.world.getContents(item.id);
        for (const inner of containerContents) {
          if (providesLight(inner)) {
            lightLevel = Math.max(lightLevel, 0.6);
          }
        }
      }
    }

    return {
      inDarkness: lightLevel === 0,
      lightLevel
    };
  }

  /**
   * Calculate what's visible
   */
  private calculateVisibility(
    viewerId: EntityId,
    viewerLocation: EntityId,
    inDarkness: boolean
  ): Set<EntityId> {
    const visible = new Set<EntityId>();

    // Viewer can always see themselves
    visible.add(viewerId);

    // In total darkness, can only see self and carried light sources
    if (inDarkness) {
      const carried = this.world.getContents(viewerId);
      for (const item of carried) {
        if (providesLight(item)) {
          visible.add(item.id);
        }
      }
      return visible;
    }

    // Add the current location
    visible.add(viewerLocation);

    // Add everything in the room
    this.addVisibleInLocation(viewerLocation, visible);

    // Add carried items
    const carried = this.world.getContents(viewerId);
    for (const item of carried) {
      visible.add(item.id);
      // And contents of open/transparent containers
      if (isContainer(item) && (item.attributes.open || item.attributes.transparent)) {
        this.addVisibleInContainer(item.id, visible);
      }
    }

    // Add worn items
    const viewer = this.world.getEntity(viewerId)!;
    for (const item of carried) {
      if (item.attributes.worn) {
        visible.add(item.id);
      }
    }

    return visible;
  }

  /**
   * Add visible items in a location
   */
  private addVisibleInLocation(locationId: EntityId, visible: Set<EntityId>): void {
    const contents = this.world.getContents(locationId);

    for (const entity of contents) {
      const attrs = entity.attributes as IFAttributes;

      // Skip concealed items
      if (attrs.concealed) continue;

      // Add the item
      visible.add(entity.id);

      // Look inside open/transparent containers
      if (isContainer(entity) && (attrs.open || attrs.transparent)) {
        this.addVisibleInContainer(entity.id, visible);
      }

      // Look on supporters
      if (attrs.supporter) {
        this.addVisibleInLocation(entity.id, visible);
      }
    }
  }

  /**
   * Add visible items in a container
   */
  private addVisibleInContainer(containerId: EntityId, visible: Set<EntityId>): void {
    const contents = this.world.getContents(containerId);

    for (const entity of contents) {
      visible.add(entity.id);

      // Recursively check nested containers
      if (isContainer(entity) && (entity.attributes.open || entity.attributes.transparent)) {
        this.addVisibleInContainer(entity.id, visible);
      }
    }
  }

  /**
   * Calculate what's reachable
   */
  private calculateReachability(
    viewerId: EntityId,
    viewerLocation: EntityId,
    visible: Set<EntityId>
  ): Set<EntityId> {
    const reachable = new Set<EntityId>();

    // Viewer can reach themselves
    reachable.add(viewerId);

    // Can reach current location
    reachable.add(viewerLocation);

    // Can reach carried/worn items
    const carried = this.world.getContents(viewerId);
    for (const item of carried) {
      reachable.add(item.id);

      // Can reach inside open containers we're carrying
      if (isContainer(item) && item.attributes.open) {
        const contents = this.world.getContents(item.id);
        for (const inner of contents) {
          reachable.add(inner.id);
        }
      }
    }

    // Can reach items in the room (if visible)
    const roomContents = this.world.getContents(viewerLocation);
    for (const entity of roomContents) {
      if (!visible.has(entity.id)) continue;

      const attrs = entity.attributes as IFAttributes;

      // Can't reach things inside closed containers
      if (isContainer(entity) && !attrs.open) {
        reachable.add(entity.id); // Can reach the container itself
        continue;
      }

      // Can reach the item
      reachable.add(entity.id);

      // Can reach things in/on open containers and supporters
      if ((isContainer(entity) && attrs.open) || attrs.supporter) {
        const contents = this.world.getContents(entity.id);
        for (const inner of contents) {
          if (visible.has(inner.id)) {
            reachable.add(inner.id);
          }
        }
      }
    }

    return reachable;
  }

  /**
   * Calculate known entities (simplified for now)
   */
  private calculateKnown(viewerId: EntityId): Set<EntityId> {
    // For now, known = everything currently visible
    // In a full implementation, this would track what the player has seen before
    // and persist across turns

    const viewerLocation = this.world.getLocation(viewerId);
    if (!viewerLocation) {
      return new Set([viewerId]);
    }

    // Calculate lighting without recursion
    const { inDarkness } = this.calculateLighting(viewerLocation, viewerId);

    // Calculate visibility without recursion
    const visible = this.calculateVisibility(viewerId, viewerLocation, inDarkness);

    return new Set(visible);
  }
}

/**
 * World Model Service
 * 
 * High-level service layer for IF world model operations.
 * Provides entity management, trait handling, spatial relationships,
 * and state serialization.
 */

import { Entity, EntityId, EventPayload } from '../../../core-imports';
import { IFEntity } from '../traits/if-entity';
import { IFWorld, IFWorldConfig, IFWorldEvents } from '../if-world/if-world';
import { TraitType } from '../traits/trait-types';
import { Trait } from '../traits/trait';
import { QueryBuilder, query } from './query-builder';
import { ScopeService } from './scope-service';
import { TRAIT_IMPLEMENTATIONS, getTraitImplementation } from '../traits/implementations';

/**
 * World model state for serialization
 */
export interface WorldModelState {
  config: IFWorldConfig;
  entities: Array<{
    id: EntityId;
    traits: Array<{
      type: TraitType;
      data: any;
    }>;
  }>;
  locations: Record<EntityId, EntityId | null>;
  version: string;
}

/**
 * Options for world model service
 */
export interface WorldModelServiceOptions extends IFWorldConfig {
  /** Whether to validate trait constraints */
  validateTraits?: boolean;
  /** Whether to emit detailed events */
  detailedEvents?: boolean;
}

/**
 * High-level world model service
 */
export class WorldModelService {
  private world: IFWorld;
  private scopeService: ScopeService;
  private options: Required<WorldModelServiceOptions>;

  constructor(options: WorldModelServiceOptions) {
    this.options = {
      validateTraits: true,
      detailedEvents: true,
      ...options
    };

    this.world = new IFWorld(options);
    this.scopeService = new ScopeService(this);
  }

  // ========== Entity CRUD Operations ==========

  /**
   * Create a new entity with traits
   */
  createEntity(id: EntityId, traits: Trait[]): IFEntity {
    const entity = new IFEntity(id);

    // Add traits
    for (const trait of traits) {
      entity.add(trait);
    }

    // Validate if enabled
    if (this.options.validateTraits) {
      this.validateEntity(entity);
    }

    // Add to world
    this.world.addEntity(entity);

    return entity;
  }

  /**
   * Get an entity by ID
   */
  getEntity(id: EntityId): IFEntity | undefined {
    return this.world.getEntity(id);
  }

  /**
   * Get all entities
   */
  getAllEntities(): IFEntity[] {
    return this.world.getAllEntities();
  }

  /**
   * Update an entity
   */
  updateEntity(id: EntityId, updates: (entity: IFEntity) => void): void {
    const entity = this.getEntity(id);
    if (!entity) {
      throw new Error(`Entity ${id} not found`);
    }

    // Apply updates
    updates(entity);

    // Validate if enabled
    if (this.options.validateTraits) {
      this.validateEntity(entity);
    }

    // Trigger update event
    this.world.updateEntity(id, entity);
  }

  /**
   * Delete an entity
   */
  deleteEntity(id: EntityId): void {
    this.world.deleteEntity(id);
  }

  // ========== Trait Management ==========

  /**
   * Add a trait to an entity
   */
  addTrait(entityId: EntityId, trait: Trait): void {
    this.updateEntity(entityId, entity => {
      entity.add(trait);
    });
  }

  /**
   * Remove a trait from an entity
   */
  removeTrait(entityId: EntityId, traitType: TraitType): void {
    this.updateEntity(entityId, entity => {
      entity.remove(traitType);
    });
  }

  /**
   * Update trait data
   */
  updateTrait(entityId: EntityId, traitType: TraitType, data: Partial<any>): void {
    this.updateEntity(entityId, entity => {
      const trait = entity.get(traitType);
      if (!trait) {
        throw new Error(`Entity ${entityId} does not have trait ${traitType}`);
      }
      Object.assign(trait.data, data);
    });
  }

  /**
   * Create a trait instance from type and data
   */
  createTrait(type: TraitType, data?: any): Trait {
    const TraitClass = getTraitImplementation(type);
    if (!TraitClass) {
      throw new Error(`Unknown trait type: ${type}`);
    }
    return new TraitClass(data);
  }

  // ========== Spatial Relationships ==========

  /**
   * Move an entity to a location
   */
  moveEntity(entityId: EntityId, toLocation: EntityId | null): void {
    this.world.moveEntity(entityId, toLocation);
  }

  /**
   * Get entity location
   */
  getLocation(entityId: EntityId): EntityId | null {
    return this.world.getLocation(entityId);
  }

  /**
   * Get contents of a location
   */
  getContents(locationId: EntityId): IFEntity[] {
    return this.world.getContents(locationId);
  }

  /**
   * Get all entities in a location hierarchy
   */
  getAllContents(locationId: EntityId): IFEntity[] {
    const results: IFEntity[] = [];
    const visited = new Set<EntityId>();

    const traverse = (locId: EntityId) => {
      if (visited.has(locId)) return;
      visited.add(locId);

      const contents = this.getContents(locId);
      for (const entity of contents) {
        results.push(entity);

        // Recurse into containers and supporters
        if (entity.has(TraitType.CONTAINER) || entity.has(TraitType.SUPPORTER)) {
          traverse(entity.id);
        }
      }
    };

    traverse(locationId);
    return results;
  }

  /**
   * Check if an entity is inside another (recursively)
   */
  isInside(entityId: EntityId, containerId: EntityId): boolean {
    let current = this.getLocation(entityId);
    while (current) {
      if (current === containerId) return true;
      current = this.getLocation(current);
    }
    return false;
  }

  /**
   * Get the room containing an entity
   */
  getContainingRoom(entityId: EntityId): IFEntity | undefined {
    let current = this.getLocation(entityId);
    while (current) {
      const entity = this.getEntity(current);
      if (entity && entity.has(TraitType.ROOM)) {
        return entity;
      }
      current = this.getLocation(current);
    }
    return undefined;
  }

  // ========== Query Support ==========

  /**
   * Create a new query builder
   */
  query(): QueryBuilder {
    return query(this);
  }

  /**
   * Find entities by trait
   */
  findByTrait(trait: TraitType): IFEntity[] {
    return this.query().hasTrait(trait).execute();
  }

  /**
   * Find entities by multiple traits
   */
  findByTraits(...traits: TraitType[]): IFEntity[] {
    return this.query().hasTraits(...traits).execute();
  }

  /**
   * Find entities in location
   */
  findInLocation(locationId: EntityId): IFEntity[] {
    return this.query().in(locationId).execute();
  }

  // ========== Scope and Visibility ==========

  /**
   * Check if entity is visible from location
   */
  isVisibleFrom(entityId: EntityId, viewpoint: EntityId): boolean {
    return this.scopeService.isVisibleFrom(entityId, viewpoint);
  }

  /**
   * Check if entity is reachable from location
   */
  isReachableFrom(entityId: EntityId, viewpoint: EntityId): boolean {
    return this.scopeService.isReachableFrom(entityId, viewpoint);
  }

  /**
   * Get scope service
   */
  getScope(): ScopeService {
    return this.scopeService;
  }

  // ========== Player Support ==========

  /**
   * Get the player entity
   */
  getPlayer(): IFEntity | undefined {
    return this.world.getPlayer();
  }

  /**
   * Get player location
   */
  getPlayerLocation(): EntityId | null {
    return this.world.getPlayerLocation();
  }

  /**
   * Get player scope
   */
  getPlayerScope() {
    return this.world.calculateScope();
  }

  // ========== State Serialization ==========

  /**
   * Serialize world state
   */
  serialize(): WorldModelState {
    const entities = this.getAllEntities();
    const locations: Record<EntityId, EntityId | null> = {};

    // Collect location data
    for (const entity of entities) {
      locations[entity.id] = this.getLocation(entity.id);
    }

    return {
      config: {
        playerId: this.options.playerId,
        startingRoom: this.options.startingRoom,
        enforceCapacity: this.options.enforceCapacity,
        trackHistory: this.options.trackHistory
      },
      entities: entities.map(entity => ({
        id: entity.id,
        traits: Array.from(entity.traits.entries()).map(([type, trait]) => ({
          type,
          data: trait.data
        }))
      })),
      locations,
      version: '1.0.0'
    };
  }

  /**
   * Deserialize world state
   */
  deserialize(state: WorldModelState): void {
    // Clear current world
    const allEntities = this.getAllEntities();
    for (const entity of allEntities) {
      this.deleteEntity(entity.id);
    }

    // Recreate entities
    for (const entityData of state.entities) {
      const traits = entityData.traits.map(t => 
        this.createTrait(t.type, t.data)
      );
      this.createEntity(entityData.id, traits);
    }

    // Restore locations
    for (const [entityId, locationId] of Object.entries(state.locations)) {
      if (locationId) {
        this.moveEntity(entityId, locationId);
      }
    }
  }

  // ========== Event Support ==========

  /**
   * Subscribe to world events
   */
  on<K extends keyof IFWorldEvents>(
    event: K,
    handler: (payload: IFWorldEvents[K]) => void
  ): void {
    this.world.on(event, handler);
  }

  /**
   * Unsubscribe from world events
   */
  off<K extends keyof IFWorldEvents>(
    event: K,
    handler: (payload: IFWorldEvents[K]) => void
  ): void {
    this.world.off(event, handler);
  }

  // ========== Validation ==========

  /**
   * Validate entity constraints
   */
  private validateEntity(entity: IFEntity): void {
    // Validate trait combinations
    this.validateTraitCombinations(entity);

    // Validate required traits
    this.validateRequiredTraits(entity);

    // Validate trait data
    this.validateTraitData(entity);
  }

  /**
   * Validate trait combinations
   */
  private validateTraitCombinations(entity: IFEntity): void {
    // Example: Can't be both fixed and portable
    if (entity.has(TraitType.FIXED) && entity.has(TraitType.PORTABLE)) {
      throw new Error('Entity cannot be both fixed and portable');
    }

    // Example: Lockable requires openable
    if (entity.has(TraitType.LOCKABLE) && !entity.has(TraitType.OPENABLE)) {
      throw new Error('Lockable entities must be openable');
    }

    // Example: Wearable must be portable
    if (entity.has(TraitType.WEARABLE) && !entity.has(TraitType.PORTABLE)) {
      throw new Error('Wearable items must be portable');
    }
  }

  /**
   * Validate required traits
   */
  private validateRequiredTraits(entity: IFEntity): void {
    // All entities must have identity
    if (!entity.has(TraitType.IDENTITY)) {
      throw new Error('All entities must have an identity trait');
    }

    // Rooms must not be portable
    if (entity.has(TraitType.ROOM) && entity.has(TraitType.PORTABLE)) {
      throw new Error('Rooms cannot be portable');
    }

    // Doors must be openable
    if (entity.has(TraitType.DOOR) && !entity.has(TraitType.OPENABLE)) {
      throw new Error('Doors must be openable');
    }
  }

  /**
   * Validate trait data
   */
  private validateTraitData(entity: IFEntity): void {
    // Validate identity has name
    const identity = entity.get(TraitType.IDENTITY);
    if (identity && !identity.data.name) {
      throw new Error('Identity trait must have a name');
    }

    // Validate container capacity
    const container = entity.get(TraitType.CONTAINER);
    if (container && container.data.capacity !== undefined) {
      if (container.data.capacity < -1) {
        throw new Error('Container capacity must be -1 (unlimited) or >= 0');
      }
    }

    // Validate valuable amount
    const valuable = entity.get(TraitType.VALUABLE);
    if (valuable && valuable.data.value < 0) {
      throw new Error('Valuable items cannot have negative value');
    }
  }
}

