/**
 * Entity Templates
 * 
 * Pre-defined trait combinations for common IF entity types.
 * These templates provide convenient starting points for creating
 * standard IF objects while maintaining the flexibility of the
 * trait-based system.
 */

import { TraitType } from '../traits/trait-types';
import { Trait } from '../traits/trait';
import { 
  IdentityTrait,
  LocationTrait,
  ContainerTrait,
  SupporterTrait,
  RoomTrait,
  PortableTrait,
  FixedTrait,
  OpenableTrait,
  LockableTrait,
  DoorTrait,
  NPCTrait,
  PlayerTrait,
  SwitchableTrait,
  LightSourceTrait
} from '../traits';

/**
 * Common entity templates
 */
export class EntityTemplates {
  /**
   * Create a basic room
   */
  static room(name: string, description: string): Trait[] {
    return [
      new IdentityTrait({ name, description }),
      new RoomTrait(),
      new ContainerTrait({ capacity: -1 }), // Unlimited capacity
      new FixedTrait()
    ];
  }

  /**
   * Create a basic portable object
   */
  static thing(name: string, description: string): Trait[] {
    return [
      new IdentityTrait({ name, description }),
      new PortableTrait()
    ];
  }

  /**
   * Create a container
   */
  static container(
    name: string, 
    description: string,
    options: {
      capacity?: number;
      openable?: boolean;
      lockable?: boolean;
      transparent?: boolean;
      portable?: boolean;
    } = {}
  ): Trait[] {
    const traits: Trait[] = [
      new IdentityTrait({ name, description }),
      new ContainerTrait({ 
        capacity: options.capacity ?? 10,
        transparent: options.transparent ?? false
      })
    ];

    if (options.portable ?? true) {
      traits.push(new PortableTrait());
    } else {
      traits.push(new FixedTrait());
    }

    if (options.openable ?? false) {
      traits.push(new OpenableTrait({ isOpen: true }));

      if (options.lockable ?? false) {
        traits.push(new LockableTrait({ isLocked: false }));
      }
    }

    return traits;
  }

  /**
   * Create a supporter (table, shelf, etc.)
   */
  static supporter(
    name: string,
    description: string,
    options: {
      capacity?: number;
      portable?: boolean;
    } = {}
  ): Trait[] {
    const traits: Trait[] = [
      new IdentityTrait({ name, description }),
      new SupporterTrait({ capacity: options.capacity ?? 10 })
    ];

    if (options.portable ?? false) {
      traits.push(new PortableTrait());
    } else {
      traits.push(new FixedTrait());
    }

    return traits;
  }

  /**
   * Create a door between rooms
   */
  static door(
    name: string,
    description: string,
    options: {
      lockable?: boolean;
      initiallyOpen?: boolean;
      initiallyLocked?: boolean;
      keyId?: string;
    } = {}
  ): Trait[] {
    const traits: Trait[] = [
      new IdentityTrait({ name, description }),
      new DoorTrait(),
      new OpenableTrait({ isOpen: options.initiallyOpen ?? false }),
      new FixedTrait()
    ];

    if (options.lockable ?? false) {
      traits.push(new LockableTrait({ 
        isLocked: options.initiallyLocked ?? false,
        keyId: options.keyId
      }));
    }

    return traits;
  }

  /**
   * Create an NPC (non-player character)
   */
  static npc(
    name: string,
    description: string,
    options: {
      capacity?: number;
      dialogue?: any;
    } = {}
  ): Trait[] {
    return [
      new IdentityTrait({ name, description, properName: true }),
      new NPCTrait(),
      new ContainerTrait({ capacity: options.capacity ?? 10 }),
      new FixedTrait() // NPCs typically don't move
    ];
  }

  /**
   * Create the player entity
   */
  static player(name: string = 'you'): Trait[] {
    return [
      new IdentityTrait({ 
        name, 
        description: 'As good-looking as ever.',
        properName: true 
      }),
      new PlayerTrait(),
      new ContainerTrait({ capacity: 10 }) // Inventory limit
    ];
  }

  /**
   * Create a light source
   */
  static lightSource(
    name: string,
    description: string,
    options: {
      portable?: boolean;
      switchable?: boolean;
      initiallyOn?: boolean;
    } = {}
  ): Trait[] {
    const traits: Trait[] = [
      new IdentityTrait({ name, description }),
      new LightSourceTrait({ isLit: options.initiallyOn ?? false })
    ];

    if (options.portable ?? true) {
      traits.push(new PortableTrait());
    } else {
      traits.push(new FixedTrait());
    }

    if (options.switchable ?? true) {
      traits.push(new SwitchableTrait({ isOn: options.initiallyOn ?? false }));
    }

    return traits;
  }

  /**
   * Create a device (switchable object)
   */
  static device(
    name: string,
    description: string,
    options: {
      portable?: boolean;
      initiallyOn?: boolean;
      providesLight?: boolean;
    } = {}
  ): Trait[] {
    const traits: Trait[] = [
      new IdentityTrait({ name, description }),
      new SwitchableTrait({ isOn: options.initiallyOn ?? false })
    ];

    if (options.portable ?? true) {
      traits.push(new PortableTrait());
    } else {
      traits.push(new FixedTrait());
    }

    if (options.providesLight ?? false) {
      traits.push(new LightSourceTrait({ isLit: options.initiallyOn ?? false }));
    }

    return traits;
  }
}

/**
 * Common trait bundles for quick entity creation
 */
export const TraitBundles = {
  /** Basic room traits */
  ROOM: [TraitType.IDENTITY, TraitType.ROOM, TraitType.CONTAINER, TraitType.FIXED],

  /** Basic portable object */
  THING: [TraitType.IDENTITY, TraitType.PORTABLE],

  /** Fixed scenery object */
  SCENERY: [TraitType.IDENTITY, TraitType.FIXED],

  /** Container that can be opened */
  OPENABLE_CONTAINER: [TraitType.IDENTITY, TraitType.CONTAINER, TraitType.OPENABLE],

  /** Lockable container */
  LOCKABLE_CONTAINER: [TraitType.IDENTITY, TraitType.CONTAINER, TraitType.OPENABLE, TraitType.LOCKABLE],

  /** Surface that can hold things */
  SURFACE: [TraitType.IDENTITY, TraitType.SUPPORTER, TraitType.FIXED],

  /** Standard door */
  DOOR: [TraitType.IDENTITY, TraitType.DOOR, TraitType.OPENABLE, TraitType.FIXED],

  /** Standard NPC */
  NPC: [TraitType.IDENTITY, TraitType.NPC, TraitType.CONTAINER, TraitType.FIXED],

  /** Light source device */
  LAMP: [TraitType.IDENTITY, TraitType.PORTABLE, TraitType.SWITCHABLE, TraitType.LIGHT_SOURCE],

  /** Wearable item */
  CLOTHING: [TraitType.IDENTITY, TraitType.PORTABLE, TraitType.WEARABLE],

  /** Edible item */
  FOOD: [TraitType.IDENTITY, TraitType.PORTABLE, TraitType.EDIBLE],

  /** Readable item */
  BOOK: [TraitType.IDENTITY, TraitType.PORTABLE, TraitType.READABLE],

  /** Valuable treasure */
  TREASURE: [TraitType.IDENTITY, TraitType.PORTABLE, TraitType.VALUABLE]
};

/**
 * Entity Builder
 * 
 * Fluent builder pattern for creating IF entities with traits.
 * Provides a convenient way to construct entities with type-safe
 * trait combinations.
 */

import { EntityId } from '../../../core-imports';
import { IFEntity } from '../traits/if-entity';
import { Trait } from '../traits/trait';
import { TraitType } from '../traits/trait-types';
import {
  IdentityTrait,
  LocationTrait,
  ContainerTrait,
  SupporterTrait,
  RoomTrait,
  PortableTrait,
  FixedTrait,
  WearableTrait,
  EdibleTrait,
  OpenableTrait,
  LockableTrait,
  SwitchableTrait,
  ReadableTrait,
  LightSourceTrait,
  LightSensitiveTrait,
  ValuableTrait,
  ScriptableTrait,
  NPCTrait,
  DialogueTrait,
  MerchantTrait,
  PlayerTrait,
  DoorTrait
} from '../traits';

/**
 * Builder for creating IF entities
 */
export class EntityBuilder {
  private traits: Map<TraitType, Trait> = new Map();
  private id: EntityId;

  constructor(id: EntityId) {
    this.id = id;
  }

  /**
   * Create a new builder
   */
  static create(id: EntityId): EntityBuilder {
    return new EntityBuilder(id);
  }

  // ========== Identity ==========

  /**
   * Set entity identity
   */
  withIdentity(name: string, description?: string): this {
    this.traits.set(TraitType.IDENTITY, new IdentityTrait({ name, description }));
    return this;
  }

  /**
   * Set as proper name (no article)
   */
  asProperName(): this {
    const identity = this.traits.get(TraitType.IDENTITY) as IdentityTrait;
    if (identity) {
      identity.data.properName = true;
    }
    return this;
  }

  /**
   * Add aliases
   */
  withAliases(...aliases: string[]): this {
    const identity = this.traits.get(TraitType.IDENTITY) as IdentityTrait;
    if (identity) {
      identity.data.aliases = [...(identity.data.aliases || []), ...aliases];
    }
    return this;
  }

  // ========== Spatial Traits ==========

  /**
   * Make this a room
   */
  asRoom(options?: { darkByDefault?: boolean }): this {
    this.traits.set(TraitType.ROOM, new RoomTrait(options));
    this.traits.set(TraitType.CONTAINER, new ContainerTrait({ capacity: -1 }));
    this.traits.set(TraitType.FIXED, new FixedTrait());
    return this;
  }

  /**
   * Make this a container
   */
  asContainer(options?: { capacity?: number; transparent?: boolean }): this {
    this.traits.set(TraitType.CONTAINER, new ContainerTrait(options));
    return this;
  }

  /**
   * Make this a supporter
   */
  asSupporter(options?: { capacity?: number }): this {
    this.traits.set(TraitType.SUPPORTER, new SupporterTrait(options));
    return this;
  }

  /**
   * Make this a door
   */
  asDoor(): this {
    this.traits.set(TraitType.DOOR, new DoorTrait());
    this.traits.set(TraitType.OPENABLE, new OpenableTrait({ isOpen: false }));
    this.traits.set(TraitType.FIXED, new FixedTrait());
    return this;
  }

  // ========== Physical Traits ==========

  /**
   * Make portable
   */
  makePortable(weight?: number): this {
    this.traits.set(TraitType.PORTABLE, new PortableTrait({ weight }));
    this.traits.delete(TraitType.FIXED); // Remove conflicting trait
    return this;
  }

  /**
   * Make fixed
   */
  makeFixed(): this {
    this.traits.set(TraitType.FIXED, new FixedTrait());
    this.traits.delete(TraitType.PORTABLE); // Remove conflicting trait
    return this;
  }

  /**
   * Make wearable
   */
  makeWearable(slot?: string): this {
    this.traits.set(TraitType.WEARABLE, new WearableTrait({ slot }));
    this.makePortable(); // Wearable items must be portable
    return this;
  }

  /**
   * Make edible
   */
  makeEdible(options?: { nutrition?: number; message?: string }): this {
    this.traits.set(TraitType.EDIBLE, new EdibleTrait(options));
    this.makePortable(); // Edible items are typically portable
    return this;
  }

  // ========== Interactive Traits ==========

  /**
   * Make openable
   */
  makeOpenable(initiallyOpen: boolean = false): this {
    this.traits.set(TraitType.OPENABLE, new OpenableTrait({ isOpen: initiallyOpen }));
    return this;
  }

  /**
   * Make lockable
   */
  makeLockable(options?: { isLocked?: boolean; keyId?: string }): this {
    this.traits.set(TraitType.LOCKABLE, new LockableTrait(options));
    this.makeOpenable(); // Lockable requires openable
    return this;
  }

  /**
   * Make switchable
   */
  makeSwitchable(initiallyOn: boolean = false): this {
    this.traits.set(TraitType.SWITCHABLE, new SwitchableTrait({ isOn: initiallyOn }));
    return this;
  }

  /**
   * Make readable
   */
  makeReadable(text: string): this {
    this.traits.set(TraitType.READABLE, new ReadableTrait({ text }));
    return this;
  }

  /**
   * Make light source
   */
  makeLightSource(initiallyLit: boolean = false): this {
    this.traits.set(TraitType.LIGHT_SOURCE, new LightSourceTrait({ isLit: initiallyLit }));
    return this;
  }

  /**
   * Make light sensitive
   */
  makeLightSensitive(): this {
    this.traits.set(TraitType.LIGHT_SENSITIVE, new LightSensitiveTrait());
    return this;
  }

  /**
   * Make valuable
   */
  makeValuable(value: number): this {
    this.traits.set(TraitType.VALUABLE, new ValuableTrait({ value }));
    return this;
  }

  // ========== Advanced Traits ==========

  /**
   * Make scriptable
   */
  makeScriptable(scripts: any): this {
    this.traits.set(TraitType.SCRIPTABLE, new ScriptableTrait({ scripts }));
    return this;
  }

  /**
   * Make NPC
   */
  asNPC(options?: any): this {
    this.traits.set(TraitType.NPC, new NPCTrait(options));
    this.traits.set(TraitType.CONTAINER, new ContainerTrait({ capacity: 10 }));
    this.makeFixed(); // NPCs are typically fixed
    return this;
  }

  /**
   * Add dialogue
   */
  withDialogue(tree: any): this {
    this.traits.set(TraitType.DIALOGUE, new DialogueTrait({ dialogueTree: tree }));
    return this;
  }

  /**
   * Make merchant
   */
  asMerchant(options?: any): this {
    this.traits.set(TraitType.MERCHANT, new MerchantTrait(options));
    this.asNPC(); // Merchants are NPCs
    return this;
  }

  /**
   * Make player
   */
  asPlayer(options?: any): this {
    this.traits.set(TraitType.PLAYER, new PlayerTrait(options));
    this.traits.set(TraitType.CONTAINER, new ContainerTrait({ capacity: 10 }));
    return this;
  }

  // ========== Generic Trait Support ==========

  /**
   * Add a custom trait
   */
  withTrait(trait: Trait): this {
    this.traits.set(trait.type, trait);
    return this;
  }

  /**
   * Add multiple traits
   */
  withTraits(...traits: Trait[]): this {
    for (const trait of traits) {
      this.traits.set(trait.type, trait);
    }
    return this;
  }

  // ========== Building ==========

  /**
   * Build the entity
   */
  build(): IFEntity {
    // Ensure identity trait exists
    if (!this.traits.has(TraitType.IDENTITY)) {
      throw new Error('Entity must have an identity trait. Use withIdentity() first.');
    }

    // Create entity
    const entity = new IFEntity(this.id);

    // Add all traits
    for (const trait of this.traits.values()) {
      entity.add(trait);
    }

    return entity;
  }

  /**
   * Build and return trait array (for use with templates)
   */
  buildTraits(): Trait[] {
    return Array.from(this.traits.values());
  }
}

/**
 * Convenience function to start building
 */
export function buildEntity(id: EntityId): EntityBuilder {
  return EntityBuilder.create(id);
}

