/**
 * IF World Model - Trait-based version
 * 
 * A simplified world model specifically designed for interactive fiction.
 * Uses trait-based entities instead of type-based entities.
 */

import { Entity, EntityId } from '../types';
import { IFEntity } from '../traits/if-entity';
import { TraitType } from '../traits/trait-types';
import { IFRelationship, Direction, directionToRelationship } from '../if-entities/relationships';
import { EventEmitter } from './if-event-emitter';
import { LocationTracker } from './location-tracker';
import { ScopeCalculator } from './scope-calculator';

/**
 * Events emitted by the world model
 */
export interface IFWorldEvents {
  'entity:created': { entity: IFEntity };
  'entity:updated': { entity: IFEntity; previous: IFEntity };
  'entity:deleted': { id: EntityId };
  'entity:moved': { entity: IFEntity; from: EntityId | null; to: EntityId | null };
  'relationship:added': { from: EntityId; to: EntityId; type: IFRelationship };
  'relationship:removed': { from: EntityId; to: EntityId; type: IFRelationship };
}

/**
 * Configuration for the IF world
 */
export interface IFWorldConfig {
  /** ID of the player entity */
  playerId: EntityId;
  /** Starting room ID */
  startingRoom?: EntityId;
  /** Whether to validate capacity constraints */
  enforceCapacity?: boolean;
  /** Whether to track entity history */
  trackHistory?: boolean;
}

/**
 * The main IF world model - trait-based version
 */
export class IFWorld extends EventEmitter<IFWorldEvents> {
  private entities: Map<EntityId, IFEntity> = new Map();
  private locationTracker: LocationTracker;
  private scopeCalculator: ScopeCalculator;
  private config: Required<IFWorldConfig>;

  constructor(config: IFWorldConfig) {
    super();
    this.config = {
      startingRoom: config.startingRoom || 'void',
      enforceCapacity: config.enforceCapacity ?? true,
      trackHistory: config.trackHistory ?? false,
      ...config
    };

    this.locationTracker = new LocationTracker();
    this.scopeCalculator = new ScopeCalculator(this);
  }

  /**
   * Add an entity to the world
   */
  addEntity(entity: IFEntity, location?: EntityId): void {
    // Validate entity
    this.validateEntity(entity);

    // Store entity
    this.entities.set(entity.id, entity);

    // Set initial location
    if (location) {
      this.locationTracker.setLocation(entity.id, location);
    }

    // Emit event
    this.emit('entity:created', { entity });
  }

  /**
   * Update an entity
   */
  updateEntity(id: EntityId, updates: Partial<IFEntity>): void {
    const entity = this.getEntity(id);
    if (!entity) {
      throw new Error(`Entity ${id} not found`);
    }

    const previous = { ...entity };
    const updated = Object.assign(entity, updates, {
      id: entity.id // Prevent ID changes
    });

    this.emit('entity:updated', { entity: updated, previous });
  }

  /**
   * Delete an entity
   */
  deleteEntity(id: EntityId): void {
    const entity = this.entities.get(id);
    if (!entity) return;

    // Remove from location tracking
    this.locationTracker.removeEntity(id);

    // Remove all relationships
    for (const [otherId, other] of this.entities) {
      if (otherId === id) continue;

      for (const relType in other.relationships) {
        const rels = other.relationships[relType];
        const index = rels.indexOf(id);
        if (index >= 0) {
          rels.splice(index, 1);
        }
      }
    }

    // Delete entity
    this.entities.delete(id);
    this.emit('entity:deleted', { id });
  }

  /**
   * Get an entity by ID
   */
  getEntity(id: EntityId): IFEntity | undefined {
    return this.entities.get(id);
  }

  /**
   * Get all entities
   */
  getAllEntities(): IFEntity[] {
    return Array.from(this.entities.values());
  }

  /**
   * Move an entity to a new location
   */
  moveEntity(entityId: EntityId, toLocation: EntityId | null): void {
    const entity = this.getEntity(entityId);
    if (!entity) {
      throw new Error(`Entity ${entityId} not found`);
    }

    const toEntity = toLocation ? this.getEntity(toLocation) : null;

    // Validate destination
    if (toLocation && toEntity) {
      if (!toEntity.canContain) {
        throw new Error(`${toLocation} cannot contain other entities`);
      }

      // Check capacity
      if (this.config.enforceCapacity) {
        const container = toEntity.get(TraitType.CONTAINER);
        const supporter = toEntity.get(TraitType.SUPPORTER);
        const capacity = container?.data.capacity ?? supporter?.data.capacity ?? -1;

        if (capacity >= 0) {
          const contents = this.getContents(toLocation);
          if (contents.length >= capacity) {
            throw new Error(`${toLocation} is full`);
          }
        }
      }
    }

    // Track movement
    const fromLocation = this.locationTracker.getLocation(entityId);
    this.locationTracker.setLocation(entityId, toLocation);

    // Update relationships
    if (fromLocation) {
      this.removeRelationship(fromLocation, entityId, IFRelationship.CONTAINS);
    }
    if (toLocation) {
      this.addRelationship(toLocation, entityId, IFRelationship.CONTAINS);
    }

    // Emit event
    this.emit('entity:moved', { 
      entity, 
      from: fromLocation, 
      to: toLocation 
    });
  }

  /**
   * Get the current location of an entity
   */
  getLocation(entityId: EntityId): EntityId | null {
    return this.locationTracker.getLocation(entityId);
  }

  /**
   * Get all entities at a location
   */
  getContents(locationId: EntityId): IFEntity[] {
    const contents = this.locationTracker.getContents(locationId);
    return contents
      .map(id => this.getEntity(id))
      .filter((e): e is IFEntity => e !== undefined);
  }

  /**
   * Add a relationship between entities
   */
  addRelationship(fromId: EntityId, toId: EntityId, type: IFRelationship): void {
    const from = this.getEntity(fromId);
    const to = this.getEntity(toId);

    if (!from || !to) {
      throw new Error('Both entities must exist');
    }

    // Initialize relationship array if needed
    if (!from.relationships[type]) {
      from.relationships[type] = [];
    }

    // Add if not already present
    if (!from.relationships[type].includes(toId)) {
      from.relationships[type].push(toId);
      this.emit('relationship:added', { from: fromId, to: toId, type });
    }
  }

  /**
   * Remove a relationship between entities
   */
  removeRelationship(fromId: EntityId, toId: EntityId, type: IFRelationship): void {
    const from = this.getEntity(fromId);
    if (!from || !from.relationships[type]) return;

    const index = from.relationships[type].indexOf(toId);
    if (index >= 0) {
      from.relationships[type].splice(index, 1);
      this.emit('relationship:removed', { from: fromId, to: toId, type });
    }
  }

  /**
   * Add an exit from one room to another
   */
  addExit(fromRoom: EntityId, direction: Direction, toRoom: EntityId): void {
    const from = this.getEntity(fromRoom);
    const to = this.getEntity(toRoom);

    if (!from || !to) {
      throw new Error('Both rooms must exist');
    }

    if (!from.isRoom || !to.isRoom) {
      throw new Error('Both entities must be rooms');
    }

    const relationship = directionToRelationship(direction);
    this.addRelationship(fromRoom, toRoom, relationship);
  }

  /**
   * Get room in a direction
   */
  getRoomInDirection(fromRoom: EntityId, direction: Direction): EntityId | undefined {
    const room = this.getEntity(fromRoom);
    if (!room || !room.isRoom) return undefined;

    const relationship = directionToRelationship(direction);
    const targets = room.relationships[relationship] || [];
    return targets[0]; // Assume one room per direction
  }

  /**
   * Get the player entity
   */
  getPlayer(): IFEntity | undefined {
    return this.getEntity(this.config.playerId);
  }

  /**
   * Get the player's current location
   */
  getPlayerLocation(): EntityId | null {
    return this.locationTracker.getLocation(this.config.playerId);
  }

  /**
   * Calculate scope for the current player location
   */
  calculateScope(): ScopeCalculator.ScopeResult {
    return this.scopeCalculator.calculateScope(this.config.playerId);
  }

  /**
   * Check if an entity is visible to the player
   */
  isVisible(entityId: EntityId): boolean {
    const scope = this.calculateScope();
    return scope.visible.has(entityId);
  }

  /**
   * Check if an entity is reachable by the player
   */
  isReachable(entityId: EntityId): boolean {
    const scope = this.calculateScope();
    return scope.reachable.has(entityId);
  }

  /**
   * Validate an entity
   */
  private validateEntity(entity: IFEntity): void {
    if (!entity.id) {
      throw new Error('Entity must have an ID');
    }

    // Must have identity trait
    if (!entity.has(TraitType.IDENTITY)) {
      throw new Error('Entity must have an identity trait');
    }

    // Trait-based validation

    // Rooms must not be portable
    if (entity.has(TraitType.ROOM) && entity.has(TraitType.PORTABLE)) {
      throw new Error('Rooms cannot be portable');
    }

    // Doors must be openable and fixed
    if (entity.has(TraitType.DOOR)) {
      if (!entity.has(TraitType.OPENABLE)) {
        throw new Error('Doors must be openable');
      }
      if (entity.has(TraitType.PORTABLE)) {
        throw new Error('Doors cannot be portable');
      }
    }

    // Lockable requires openable
    if (entity.has(TraitType.LOCKABLE) && !entity.has(TraitType.OPENABLE)) {
      throw new Error('Lockable entities must be openable');
    }

    // Wearable must be portable
    if (entity.has(TraitType.WEARABLE) && !entity.has(TraitType.PORTABLE)) {
      throw new Error('Wearable items must be portable');
    }

    // Fixed and portable are mutually exclusive
    if (entity.has(TraitType.FIXED) && entity.has(TraitType.PORTABLE)) {
      throw new Error('Entity cannot be both fixed and portable');
    }
  }
}

/**
 * Scope calculator for IF visibility and reachability - Trait-based version
 * 
 * Determines what entities are visible, reachable, and known to the player
 * based on IF rules for darkness, containers, and distance.
 */

import { EntityId } from '../../core-imports';
import { IFWorld } from './if-world-trait-based';
import { IFEntity } from '../traits/if-entity';
import { TraitType } from '../traits/trait-types';

export namespace ScopeCalculator {
  /**
   * Result of scope calculation
   */
  export interface ScopeResult {
    /** Entities that can be seen */
    visible: Set<EntityId>;
    /** Entities that can be touched/interacted with */
    reachable: Set<EntityId>;
    /** Entities the player knows about */
    known: Set<EntityId>;
    /** Whether the player is in darkness */
    inDarkness: boolean;
    /** Current light level (0 = pitch black, 1 = normal) */
    lightLevel: number;
  }
}

/**
 * Calculates scope (visibility and reachability) in the IF world - Trait-based version
 */
export class ScopeCalculator {
  constructor(private world: IFWorld) {}

  /**
   * Calculate scope for an entity (usually the player)
   */
  calculateScope(viewerId: EntityId): ScopeCalculator.ScopeResult {
    const viewer = this.world.getEntity(viewerId);
    if (!viewer) {
      return {
        visible: new Set(),
        reachable: new Set(),
        known: new Set(),
        inDarkness: true,
        lightLevel: 0
      };
    }

    const viewerLocation = this.world.getLocation(viewerId);
    if (!viewerLocation) {
      return {
        visible: new Set([viewerId]),
        reachable: new Set([viewerId]),
        known: new Set([viewerId]),
        inDarkness: false,
        lightLevel: 1
      };
    }

    // Calculate light level
    const { inDarkness, lightLevel } = this.calculateLighting(viewerLocation, viewerId);

    // Calculate visibility
    const visible = this.calculateVisibility(viewerId, viewerLocation, inDarkness);

    // Calculate reachability (subset of visible)
    const reachable = this.calculateReachability(viewerId, viewerLocation, visible);

    // Calculate known entities (everything the player has seen)
    const known = this.calculateKnown(viewerId);

    return {
      visible,
      reachable,
      known,
      inDarkness,
      lightLevel
    };
  }

  /**
   * Calculate lighting conditions
   */
  private calculateLighting(locationId: EntityId, viewerId: EntityId): {
    inDarkness: boolean;
    lightLevel: number;
  } {
    const location = this.world.getEntity(locationId);
    if (!location || !location.isRoom) {
      return { inDarkness: false, lightLevel: 1 };
    }

    // Check if room is dark
    const roomTrait = location.get(TraitType.ROOM);
    const isDark = roomTrait?.data.darkByDefault ?? false;
    if (!isDark) {
      return { inDarkness: false, lightLevel: 1 };
    }

    // Look for light sources
    let lightLevel = 0;

    // Check viewer
    const viewer = this.world.getEntity(viewerId);
    if (viewer && viewer.providesLight) {
      lightLevel = 1;
    }

    // Check carried items
    const carried = this.world.getContents(viewerId);
    for (const item of carried) {
      if (item.providesLight) {
        lightLevel = Math.max(lightLevel, 1);
      }
    }

    // Check room contents
    const roomContents = this.world.getContents(locationId);
    for (const item of roomContents) {
      if (item.providesLight) {
        lightLevel = Math.max(lightLevel, 0.8);
      }

      // Check inside open containers
      if (item.isContainer && item.isOpen) {
        const containerContents = this.world.getContents(item.id);
        for (const inner of containerContents) {
          if (inner.providesLight) {
            lightLevel = Math.max(lightLevel, 0.6);
          }
        }
      }
    }

    return {
      inDarkness: lightLevel === 0,
      lightLevel
    };
  }

  /**
   * Calculate what's visible
   */
  private calculateVisibility(
    viewerId: EntityId,
    viewerLocation: EntityId,
    inDarkness: boolean
  ): Set<EntityId> {
    const visible = new Set<EntityId>();

    // Viewer can always see themselves
    visible.add(viewerId);

    // In total darkness, can only see self and carried light sources
    if (inDarkness) {
      const carried = this.world.getContents(viewerId);
      for (const item of carried) {
        if (item.providesLight) {
          visible.add(item.id);
        }
      }
      return visible;
    }

    // Add the current location
    visible.add(viewerLocation);

    // Add everything in the room
    this.addVisibleInLocation(viewerLocation, visible);

    // Add carried items
    const carried = this.world.getContents(viewerId);
    for (const item of carried) {
      visible.add(item.id);
      // And contents of open/transparent containers
      if (item.isContainer) {
        const container = item.get(TraitType.CONTAINER);
        if (item.isOpen || container?.data.transparent) {
          this.addVisibleInContainer(item.id, visible);
        }
      }
    }

    // Add worn items
    for (const item of carried) {
      const wearable = item.get(TraitType.WEARABLE);
      if (wearable?.data.isWorn) {
        visible.add(item.id);
      }
    }

    return visible;
  }

  /**
   * Add visible items in a location
   */
  private addVisibleInLocation(locationId: EntityId, visible: Set<EntityId>): void {
    const contents = this.world.getContents(locationId);

    for (const entity of contents) {
      const identity = entity.get(TraitType.IDENTITY);

      // Skip concealed items
      if (identity?.data.concealed) continue;

      // Add the item
      visible.add(entity.id);

      // Look inside open/transparent containers
      if (entity.isContainer) {
        const container = entity.get(TraitType.CONTAINER);
        if (entity.isOpen || container?.data.transparent) {
          this.addVisibleInContainer(entity.id, visible);
        }
      }

      // Look on supporters
      if (entity.isSupporter) {
        this.addVisibleInLocation(entity.id, visible);
      }
    }
  }

  /**
   * Add visible items in a container
   */
  private addVisibleInContainer(containerId: EntityId, visible: Set<EntityId>): void {
    const contents = this.world.getContents(containerId);

    for (const entity of contents) {
      visible.add(entity.id);

      // Recursively check nested containers
      if (entity.isContainer) {
        const container = entity.get(TraitType.CONTAINER);
        if (entity.isOpen || container?.data.transparent) {
          this.addVisibleInContainer(entity.id, visible);
        }
      }
    }
  }

  /**
   * Calculate what's reachable
   */
  private calculateReachability(
    viewerId: EntityId,
    viewerLocation: EntityId,
    visible: Set<EntityId>
  ): Set<EntityId> {
    const reachable = new Set<EntityId>();

    // Viewer can reach themselves
    reachable.add(viewerId);

    // Can reach current location
    reachable.add(viewerLocation);

    // Can reach carried/worn items
    const carried = this.world.getContents(viewerId);
    for (const item of carried) {
      reachable.add(item.id);

      // Can reach inside open containers we're carrying
      if (item.isContainer && item.isOpen) {
        const contents = this.world.getContents(item.id);
        for (const inner of contents) {
          reachable.add(inner.id);
        }
      }
    }

    // Can reach items in the room (if visible)
    const roomContents = this.world.getContents(viewerLocation);
    for (const entity of roomContents) {
      if (!visible.has(entity.id)) continue;

      // Can't reach things inside closed containers
      if (entity.isContainer && !entity.isOpen) {
        reachable.add(entity.id); // Can reach the container itself
        continue;
      }

      // Can reach the item
      reachable.add(entity.id);

      // Can reach things in/on open containers and supporters
      if ((entity.isContainer && entity.isOpen) || entity.isSupporter) {
        const contents = this.world.getContents(entity.id);
        for (const inner of contents) {
          if (visible.has(inner.id)) {
            reachable.add(inner.id);
          }
        }
      }
    }

    return reachable;
  }

  /**
   * Calculate known entities (simplified for now)
   */
  private calculateKnown(viewerId: EntityId): Set<EntityId> {
    // For now, known = everything currently visible
    // In a full implementation, this would track what the player has seen before
    // and persist across turns

    const viewerLocation = this.world.getLocation(viewerId);
    if (!viewerLocation) {
      return new Set([viewerId]);
    }

    // Calculate lighting without recursion
    const { inDarkness } = this.calculateLighting(viewerLocation, viewerId);

    // Calculate visibility without recursion
    const visible = this.calculateVisibility(viewerId, viewerLocation, inDarkness);

    return new Set(visible);
  }
}

# Type-based to Trait-based Migration Guide

## Overview

Sharpee has migrated from a type-based entity system to a more flexible trait-based system. This guide helps you understand the changes and migrate existing code.

## Key Concepts

### Old: Type-based Entities
```typescript
// Old approach - fixed types
interface Room extends IFEntity {
  type: CoreIFEntityType.ROOM;
  attributes: RoomAttributes;
}

const room: Room = {
  id: 'kitchen',
  type: CoreIFEntityType.ROOM,
  attributes: {
    name: 'Kitchen',
    description: 'A well-equipped kitchen.',
    dark: false
  }
};
```

### New: Trait-based Entities
```typescript
// New approach - composable traits
const room = new IFEntity('kitchen')
  .add(new IdentityTrait({ 
    name: 'Kitchen', 
    description: 'A well-equipped kitchen.' 
  }))
  .add(new RoomTrait({ darkByDefault: false }))
  .add(new ContainerTrait({ capacity: -1 }));
```

## Migration Patterns

### 1. Creating Entities

#### Old Way
```typescript
const thing: Thing = {
  id: 'lamp',
  type: CoreIFEntityType.DEVICE,
  attributes: {
    name: 'brass lamp',
    description: 'A shiny brass lamp.',
    takeable: true,
    switchable: true,
    on: false,
    providesLight: true
  }
};
```

#### New Way - Using Builder
```typescript
const lamp = buildEntity('lamp')
  .withIdentity('brass lamp', 'A shiny brass lamp.')
  .makePortable()
  .makeSwitchable(false)
  .makeLightSource(false)
  .build();
```

#### New Way - Using Templates
```typescript
const lamp = world.createEntity('lamp', 
  EntityTemplates.lightSource(
    'brass lamp',
    'A shiny brass lamp.',
    { portable: true, switchable: true, initiallyOn: false }
  )
);
```

### 2. Type Checking

#### Old Way
```typescript
if (isRoom(entity)) {
  // Handle room
}

if (entity.type === CoreIFEntityType.CONTAINER) {
  // Handle container
}
```

#### New Way - Using Traits
```typescript
if (entity.has(TraitType.ROOM)) {
  // Handle room
}

if (entity.isRoom) {  // Convenience property
  // Handle room
}
```

### 3. Accessing Properties

#### Old Way
```typescript
const name = entity.attributes.name;
const isOpen = entity.attributes.open;
const capacity = entity.attributes.capacity;
```

#### New Way
```typescript
const name = entity.name;  // Convenience property
const isOpen = entity.isOpen;  // Convenience property

// Or access trait data directly
const identity = entity.get(TraitType.IDENTITY);
const name = identity?.data.name;

const container = entity.get(TraitType.CONTAINER);
const capacity = container?.data.capacity;
```

### 4. Common Entity Patterns

#### Creating a Room
```typescript
// Old
const room: Room = {
  id: 'library',
  type: CoreIFEntityType.ROOM,
  attributes: {
    name: 'Library',
    description: 'Dusty shelves line the walls.',
    dark: true,
    visited: false
  }
};

// New
const room = buildEntity('library')
  .asRoom({ darkByDefault: true })
  .withIdentity('Library', 'Dusty shelves line the walls.')
  .build();
```

#### Creating a Container
```typescript
// Old
const box: Container = {
  id: 'wooden-box',
  type: CoreIFEntityType.CONTAINER,
  attributes: {
    name: 'wooden box',
    description: 'A sturdy wooden box.',
    container: true,
    openable: true,
    open: false,
    capacity: 5
  }
};

// New
const box = buildEntity('wooden-box')
  .withIdentity('wooden box', 'A sturdy wooden box.')
  .asContainer({ capacity: 5 })
  .makeOpenable(false)
  .makePortable()
  .build();
```

#### Creating a Door
```typescript
// Old
const door: Door = {
  id: 'oak-door',
  type: CoreIFEntityType.DOOR,
  attributes: {
    name: 'oak door',
    description: 'A solid oak door.',
    openable: true,
    open: false,
    lockable: true,
    locked: true,
    key: 'brass-key'
  }
};

// New
const door = EntityTemplates.door(
  'oak door',
  'A solid oak door.',
  {
    lockable: true,
    initiallyOpen: false,
    initiallyLocked: true,
    keyId: 'brass-key'
  }
);
```

## Type Guard Migration

### Old Type Guards
```typescript
function canContain(entity: Entity): boolean {
  return isContainer(entity) || isSupporter(entity) || 
         isRoom(entity) || isPerson(entity);
}

function isPortable(entity: Entity): boolean {
  const attrs = entity.attributes as IFAttributes;
  return attrs.takeable === true && attrs.fixed !== true;
}
```

### New Convenience Properties
```typescript
// Built into IFEntity class
entity.canContain    // true if has container/supporter/room/person traits
entity.isPortable    // true if has portable trait and not fixed
entity.isFixed       // true if has fixed trait or is room/door
entity.providesLight // true if has light source trait and is lit
```

## Action Migration Example

### Old Action
```typescript
export const takeAction: Action = {
  name: 'take',
  patterns: ['take {object}', 'get {object}', 'pick up {object}'],

  execute: async (executor: ActionExecutor, command: ParsedCommand) => {
    const object = command.entities.object;

    if (!object) {
      return executor.error(msg('take.what'));
    }

    if (object.attributes.takeable !== true) {
      return executor.error(msg('take.notTakeable', { object: object.attributes.name }));
    }

    // Move object to player
    executor.world.moveEntity(object.id, executor.world.playerId);

    return executor.success(msg('take.success', { object: object.attributes.name }));
  }
};
```

### New Action
```typescript
export const takeAction: Action = {
  name: 'take',
  patterns: ['take {object}', 'get {object}', 'pick up {object}'],

  execute: async (context: ActionContext) => {
    const { command, world, output } = context;
    const object = command.entities.object;

    if (!object) {
      return output.error(msg('take.what'));
    }

    if (!object.isPortable) {
      return output.error(msg('take.notTakeable', { object: object.name }));
    }

    // Move object to player
    world.moveEntity(object.id, world.getPlayer()!.id);

    return output.success(msg('take.success', { object: object.name }));
  }
};
```

## Best Practices

### 1. Use Templates for Common Patterns
```typescript
// Instead of manually adding traits
const room = new IFEntity('room1');
room.add(new IdentityTrait({...}));
room.add(new RoomTrait());
room.add(new ContainerTrait({...}));

// Use templates
const room = world.createEntity('room1',
  EntityTemplates.room('Living Room', 'A comfortable living room.')
);
```

### 2. Use Builder for Complex Entities
```typescript
const complexNPC = buildEntity('wizard')
  .withIdentity('Gandalf', 'A wise old wizard.')
  .asProperName()
  .asNPC()
  .withDialogue(wizardDialogue)
  .makeScriptable({
    onTalk: (world, npc, player) => {
      // Custom behavior
    }
  })
  .build();
```

### 3. Leverage Convenience Properties
