const backpack = buildEntity('backpack')
  .withIdentity('backpack', 'A sturdy hiking backpack.')
  .asContainer({ capacity: 15 })
  .makePortable(2)
  .makeWearable('back')
  .build();

const pocket = buildEntity('backpack-pocket')
  .withIdentity('side pocket', 'A small zippered pocket.')
  .asContainer({ capacity: 3 })
  .makeOpenable(true)
  .build();

// Place pocket in backpack
world.moveEntity(pocket.id, backpack.id);
