/**
 * Taking action - Pick up an object
 */

// Import from local action types
import { ActionDefinition } from './types';

// Import from parser types  
import { ResolvedIFCommand } from '../parser/if-parser-types';

// Import from world model types
import { GameContext } from '../world-model/types';

// Import from Core through controlled boundary
import { SemanticEvent, createEvent } from '../core-imports';

// Import IF-specific constants from local constants
import { IFActions } from '../constants/if-actions';
import { IFEvents } from '../constants/if-events';
import { IFEntityType } from '../constants/if-entity-types';

/**
 * Taking action definition
 * 
 * Handles "take [object]" commands
 */
export const takingAction: ActionDefinition = {
  id: IFActions.TAKING,
  name: IFActions.TAKING,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: true,
    undoable: true,
    category: 'manipulation'
  },
  phases: {
    // VALIDATE phase - Check if taking is possible
    validate: (command: ResolvedIFCommand, context: GameContext): boolean | string => {
      // Must have a target
      if (!command.noun) {
        return context.languageProvider.formatMessage('action.taking.no_target');
      }

      const target = command.noun;
      const targetId = target.id;

      // Can't take yourself
      if (targetId === command.actor.id) {
        return context.languageProvider.formatMessage('action.taking.cannot_take_self');
      }

      // Check if already held
      const playerLocation = context.world.getLocation(targetId);
      if (playerLocation === command.actor.id) {
        return context.languageProvider.formatMessage('action.taking.already_held', {
          item: target.attributes.name || 'that'
        });
      }

      // Check if takeable (default to true if not specified)
      const takeable = target.attributes.takeable !== false;
      if (!takeable) {
        return context.languageProvider.formatMessage('action.taking.not_takeable', {
          item: target.attributes.name || 'that'
        });
      }

      // Check if it's a location
      if (target.type === IFEntityType.ROOM || target.type === IFEntityType.REGION) {
        return context.languageProvider.formatMessage('action.taking.cannot_take_location');
      }

      // Check if accessible
      if (!context.isAccessible(targetId)) {
        return context.languageProvider.formatMessage('action.taking.not_accessible', {
          item: target.attributes.name || 'that'
        });
      }

      // All checks passed
      return true;
    },

    // EXECUTE phase - Actually take the object
    execute: (command: ResolvedIFCommand, context: GameContext): SemanticEvent[] => {
      if (!command.noun) return [];

      const target = command.noun;
      const targetId = target.id;
      const events: SemanticEvent[] = [];

      // Find where the item currently is
      const currentLocation = context.world.getLocation(targetId);

      // Move the item to the player
      try {
        context.world.moveEntity(targetId, command.actor.id);

        // Create success event
        events.push(createEvent(
          IFEvents.ITEM_TAKEN,
          { 
            itemId: targetId,
            itemName: target.attributes.name,
            from: currentLocation,
            actor: command.actor.id
          },
          { 
            narrate: true,
            location: context.currentLocation.id
          }
        ));
      } catch (error) {
        // If the move fails, report the error
        events.push(createEvent(
          IFEvents.ACTION_FAILED,
          { 
            action: IFActions.TAKING,
            reason: 'move_failed',
            message: error instanceof Error ? error.message : 'Failed to take item'
          },
          { narrate: true }
        ));
      }

      return events;
    }
  }
};

