import { ValidatedTrait, validators } from '../validation';

/**
 * Portable trait indicates an entity can be picked up and carried.
 * Most objects in IF have this trait.
 */
export class PortableTrait extends ValidatedTrait {
  static readonly type = 'portable';
  readonly type = 'portable';

  /** Weight of the object (arbitrary units) */
  weight: number = 1;

  /** Size/bulk of the object (arbitrary units) */
  bulk: number = 1;

  /** Whether the object is currently being carried by someone */
  carried: boolean = false;

  /** Custom message when trying to take this object */
  takeMessage?: string;

  /** Whether taking this object requires confirmation */
  requiresConfirmation: boolean = false;

  constructor(data?: Partial<PortableTrait>) {
    super();
    if (data) {
      Object.assign(this, data);
    }
  }

  validate(): void {
    validators.number(this.weight, this.type, 'weight');
    validators.range(this.weight, 0, Number.MAX_SAFE_INTEGER, this.type, 'weight');

    validators.number(this.bulk, this.type, 'bulk');
    validators.range(this.bulk, 0, Number.MAX_SAFE_INTEGER, this.type, 'bulk');

    validators.boolean(this.carried, this.type, 'carried');
    validators.boolean(this.requiresConfirmation, this.type, 'requiresConfirmation');

    if (this.takeMessage !== undefined) {
      validators.string(this.takeMessage, this.type, 'takeMessage');
    }
  }
}

