import { Entity, EntityId, EntityCreationParams } from '../../core-imports';
import { Trait, TraitConstructor, isTrait } from './types';
import { TraitType } from './trait-types';

/**
 * Interactive Fiction Entity with trait-based composition.
 * Implements the core Entity interface and adds trait management capabilities.
 */
export class IFEntity implements Entity {
  readonly id: EntityId;
  readonly type: string;
  attributes: Record<string, unknown>;
  relationships: Record<string, EntityId[]>;
  traits: Map<TraitType, Trait>;

  constructor(id: string, type: string, params?: Partial<EntityCreationParams>) {
    this.id = id;
    this.type = type;
    this.attributes = params?.attributes || {};
    this.relationships = params?.relationships || {};
    this.traits = new Map();
  }

  /**
   * Check if entity has a specific trait
   */
  has(type: TraitType): boolean {
    return this.traits.has(type);
  }

  /**
   * Get a typed trait from the entity
   */
  get<T extends Trait>(type: TraitType | TraitConstructor<T>): T | undefined {
    const traitType = typeof type === 'string' ? type as TraitType : type.type;
    return this.traits.get(traitType) as T | undefined;
  }

  /**
   * Add a trait to the entity
   */
  add(trait: Trait): this {
    if (!isTrait(trait)) {
      throw new Error('Invalid trait: must have a type property');
    }

    // Remove existing trait of same type if present
    if (this.traits.has(trait.type)) {
      this.remove(trait.type);
    }

    // Validate trait if it has validation
    if (trait.validate) {
      trait.validate();
    }

    this.traits.set(trait.type as TraitType, trait);
    return this;
  }

  /**
   * Remove a trait from the entity
   */
  remove(type: TraitType): boolean {
    const trait = this.traits.get(type);
    if (!trait) {
      return false;
    }

    // Cleanup trait if it has cleanup
    if (trait.cleanup) {
      trait.cleanup();
    }

    return this.traits.delete(type);
  }

  /**
   * Check if entity has all specified traits
   */
  hasAll(...types: TraitType[]): boolean {
    return types.every(type => this.traits.has(type));
  }

  /**
   * Check if entity has any of the specified traits
   */
  hasAny(...types: TraitType[]): boolean {
    return types.some(type => this.traits.has(type));
  }

  /**
   * Get all traits on this entity
   */
  getTraits(): Trait[] {
    return Array.from(this.traits.values());
  }

  /**
   * Get all trait types on this entity
   */
  getTraitTypes(): TraitType[] {
    return Array.from(this.traits.keys());
  }

  /**
   * Clear all traits from the entity
   */
  clearTraits(): void {
    // Cleanup all traits
    for (const trait of this.traits.values()) {
      if (trait.cleanup) {
        trait.cleanup();
      }
    }
    this.traits.clear();
  }

  /**
   * Clone this entity with all its traits
   */
  clone(newId: string): IFEntity {
    const cloned = new IFEntity(newId, this.type, {
      attributes: { ...this.attributes },
      relationships: JSON.parse(JSON.stringify(this.relationships)) // Deep copy
    });

    // Clone traits (shallow copy for now)
    for (const [type, trait] of this.traits) {
      cloned.traits.set(type, { ...trait });
    }

    return cloned;
  }

  /**
   * Serialize entity and traits to JSON
   */
  toJSON(): any {
    return {
      id: this.id,
      type: this.type,
      attributes: this.attributes,
      relationships: this.relationships,
      traits: Array.from(this.traits.entries()).map(([type, trait]) => ({
        type,
        ...trait
      }))
    };
  }

  /**
   * Create entity from JSON data
   */
  static fromJSON(json: any): IFEntity {
    const entity = new IFEntity(json.id, json.type, {
      attributes: json.attributes,
      relationships: json.relationships
    });

    if (json.traits && Array.isArray(json.traits)) {
      for (const traitData of json.traits) {
        entity.traits.set(traitData.type, traitData);
      }
    }

    return entity;
  }

  // ========== Convenience Properties ==========

  /**
   * Check if this is a room
   */
  get isRoom(): boolean {
    return this.has(TraitType.ROOM);
  }

  /**
   * Check if this can contain other entities
   */
  get canContain(): boolean {
    return this.has(TraitType.CONTAINER) || this.has(TraitType.SUPPORTER) || 
           this.has(TraitType.ROOM) || this.has(TraitType.NPC) || this.has(TraitType.PLAYER);
  }

  /**
   * Check if this is portable
   */
  get isPortable(): boolean {
    return this.has(TraitType.PORTABLE) && !this.has(TraitType.FIXED);
  }

  /**
   * Check if this is fixed in place
   */
  get isFixed(): boolean {
    return this.has(TraitType.FIXED) || this.has(TraitType.ROOM) || this.has(TraitType.DOOR);
  }

  /**
   * Check if this can be opened
   */
  get isOpenable(): boolean {
    return this.has(TraitType.OPENABLE);
  }

  /**
   * Check if this is currently open
   */
  get isOpen(): boolean {
    const openable = this.get(TraitType.OPENABLE);
    return openable ? openable.data.isOpen : false;
  }

  /**
   * Check if this can be locked
   */
  get isLockable(): boolean {
    return this.has(TraitType.LOCKABLE);
  }

  /**
   * Check if this is currently locked
   */
  get isLocked(): boolean {
    const lockable = this.get(TraitType.LOCKABLE);
    return lockable ? lockable.data.isLocked : false;
  }

  /**
   * Check if this is a container
   */
  get isContainer(): boolean {
    return this.has(TraitType.CONTAINER);
  }

  /**
   * Check if this is a supporter
   */
  get isSupporter(): boolean {
    return this.has(TraitType.SUPPORTER);
  }

  /**
   * Check if this is a door
   */
  get isDoor(): boolean {
    return this.has(TraitType.DOOR);
  }

  /**
   * Check if this is a person (NPC or player)
   */
  get isPerson(): boolean {
    return this.has(TraitType.NPC) || this.has(TraitType.PLAYER);
  }

  /**
   * Check if this is the player
   */
  get isPlayer(): boolean {
    return this.has(TraitType.PLAYER);
  }

  /**
   * Check if this provides light
   */
  get providesLight(): boolean {
    const lightSource = this.get(TraitType.LIGHT_SOURCE);
    return lightSource ? lightSource.data.isLit : false;
  }

  /**
   * Check if this is switchable
   */
  get isSwitchable(): boolean {
    return this.has(TraitType.SWITCHABLE);
  }

  /**
   * Check if this is switched on
   */
  get isOn(): boolean {
    const switchable = this.get(TraitType.SWITCHABLE);
    return switchable ? switchable.data.isOn : false;
  }

  /**
   * Get the name of this entity
   */
  get name(): string {
    const identity = this.get(TraitType.IDENTITY);
    return identity ? identity.data.name : this.id;
  }

  /**
   * Get the description of this entity
   */
  get description(): string | undefined {
    const identity = this.get(TraitType.IDENTITY);
    return identity ? identity.data.description : undefined;
  }
}

/**
 * Taking Action - Trait-based Implementation
 * 
 * Updated version of the taking action that uses the trait-based world model.
 */

import { ActionDefinition } from '../../../actions/types';
import { ResolvedIFCommand } from '../../../parser/if-parser-types';
import { WorldModelContext } from '../world-model-context';
import { SemanticEvent, createEvent } from '../../../core-imports';
import { IFActions } from '../../../constants/if-actions';
import { IFEvents } from '../../../constants/if-events';
import { 
  canBeTaken, 
  getEntityName, 
  ValidationMessages,
  createValidator 
} from '../trait-validation';
import { TraitType } from '../../traits/trait-types';
import { IFEntity } from '../../traits/if-entity';

/**
 * Taking action - pick up an object
 * 
 * This version uses the trait-based world model for all checks
 */
export const takingAction: ActionDefinition = {
  id: IFActions.TAKING,
  name: 'take',
  verbs: ['take', 'get', 'pick up', 'grab'],
  metadata: {
    changesWorld: true,
    undoable: true,
    category: 'manipulation'
  },

  phases: {
    /**
     * Validate that taking is possible
     */
    validate: (command: ResolvedIFCommand, context: WorldModelContext): boolean | string => {
      const validator = createValidator(context);

      // Must have a target
      if (!command.noun) {
        return 'What do you want to take?';
      }

      // Convert to trait-based entity
      const target = context.world.getEntity(command.noun.id);
      if (!target) {
        return 'That doesn\'t exist.';
      }

      // Can't take yourself
      if (target.id === context.player.id) {
        return 'You can\'t take yourself.';
      }

      // Must be visible and reachable
      const reachCheck = validator.validateReachable(target);
      if (reachCheck !== true) return reachCheck;

      // Check if already held
      const currentLocation = context.world.getLocation(target.id);
      if (currentLocation === context.player.id) {
        return ValidationMessages.alreadyHeld(target);
      }

      // Check if takeable
      if (!canBeTaken(target)) {
        return ValidationMessages.notTakeable(target);
      }

      // Check if it's worn by someone else
      if (target.has(TraitType.WEARABLE)) {
        const wearable = target.get(TraitType.WEARABLE);
        if (wearable?.data.worn && currentLocation !== context.player.id) {
          const wearer = context.world.getEntity(currentLocation!);
          if (wearer) {
            return `${getEntityName(wearer)} is wearing ${getEntityName(target)}.`;
          }
        }
      }

      // Check player capacity
      const playerCapacity = context.player.get(TraitType.CONTAINER)?.data.capacity;
      if (playerCapacity !== undefined && playerCapacity !== -1) {
        const carried = context.world.getContents(context.player.id);
        if (carried.length >= playerCapacity) {
          return 'You are carrying too much.';
        }
      }

      // Custom validation from scriptable trait
      if (target.has(TraitType.SCRIPTABLE)) {
        const scriptable = target.get(TraitType.SCRIPTABLE);
        if (scriptable?.data.beforeTake) {
          const result = scriptable.data.beforeTake(target, context);
          if (result !== true) return result;
        }
      }

      return true;
    },

    /**
     * Execute the taking action
     */
    execute: (command: ResolvedIFCommand, context: WorldModelContext): SemanticEvent[] => {
      if (!command.noun) return [];

      const target = context.world.getEntity(command.noun.id)!;
      const events: SemanticEvent[] = [];

      // Get current location for event data
      const fromLocation = context.world.getLocation(target.id);
      const fromEntity = fromLocation ? context.world.getEntity(fromLocation) : null;

      try {
        // Move the entity to the player
        context.world.moveEntity(target.id, context.player.id);

        // Update portable trait if it tracks carrier
        if (target.has(TraitType.PORTABLE)) {
          context.world.updateTrait(target.id, TraitType.PORTABLE, {
            carriedBy: context.player.id
          });
        }

        // Create success event
        events.push(createEvent(
          IFEvents.ITEM_TAKEN,
          {
            itemId: target.id,
            itemName: getEntityName(target),
            from: fromLocation,
            fromName: fromEntity ? getEntityName(fromEntity) : undefined,
            actor: context.player.id
          },
          {
            narrate: true,
            location: context.currentLocation.id
          }
        ));

        // Trigger after-take script if present
        if (target.has(TraitType.SCRIPTABLE)) {
          const scriptable = target.get(TraitType.SCRIPTABLE);
          if (scriptable?.data.afterTake) {
            const additionalEvents = scriptable.data.afterTake(target, context);
            if (additionalEvents) {
              events.push(...additionalEvents);
            }
          }
        }

        // Mark as touched/moved
        if (target.has(TraitType.IDENTITY)) {
          context.world.updateTrait(target.id, TraitType.IDENTITY, {
            touched: true,
            moved: true
          });
        }

      } catch (error) {
        events.push(createEvent(
          IFEvents.ACTION_FAILED,
          {
            action: IFActions.TAKING,
            reason: 'move_failed',
            message: error instanceof Error ? error.message : 'Failed to take item'
          },
          { narrate: true }
        ));
      }

      return events;
    },

    /**
     * Undo a take action
     */
    undo: (command: ResolvedIFCommand, context: WorldModelContext, previousState: any): SemanticEvent[] => {
      if (!command.noun || !previousState?.fromLocation) return [];

      const target = context.world.getEntity(command.noun.id);
      if (!target) return [];

      // Move back to original location
      context.world.moveEntity(target.id, previousState.fromLocation);

      // Update portable trait
      if (target.has(TraitType.PORTABLE)) {
        context.world.updateTrait(target.id, TraitType.PORTABLE, {
          carriedBy: null
        });
      }

      return [createEvent(
        IFEvents.ACTION_UNDONE,
        {
          action: IFActions.TAKING,
          itemId: target.id,
          itemName: getEntityName(target)
        },
        { narrate: true }
      )];
    }
  }
};

/**
 * Dropping action - Put down a held object
 */

// Import from local action types
import { ActionDefinition } from './types';

// Import from parser types  
import { ResolvedIFCommand } from '../parser/if-parser-types';

// Import from world model types
import { GameContext } from '../world-model/types';

// Import from Core through controlled boundary
import { SemanticEvent, createEvent } from '../core-imports';

// Import IF-specific constants from local constants
import { IFActions } from '../constants/if-actions';
import { IFEvents } from '../constants/if-events';
import { IFAttributes } from '../constants/if-attributes';

/**
 * Dropping action definition
 * 
 * Handles "drop [object]" commands
 */
export const droppingAction: ActionDefinition = {
  id: IFActions.DROPPING,
  name: IFActions.DROPPING,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: true,
    undoable: true,
    category: 'manipulation'
  },
  phases: {
    // VALIDATE phase - Check if dropping is possible
    validate: (command: ResolvedIFCommand, context: GameContext): boolean | string => {
      // Must have a target
      if (!command.noun) {
        return context.languageProvider.formatMessage('action.dropping.no_target');
      }

      const target = command.noun;
      const targetId = target.id;

      // Can't drop yourself
      if (targetId === command.actor.id) {
        return context.languageProvider.formatMessage('action.dropping.cannot_drop_self');
      }

      // Check if held by the actor
      const heldLocation = context.world.getLocation(targetId);
      if (heldLocation !== command.actor.id) {
        return context.languageProvider.formatMessage('action.dropping.not_held', {
          item: target.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Check if droppable (default to true if not specified)
      const droppable = target.attributes.droppable !== false;
      if (!droppable) {
        return context.languageProvider.formatMessage('action.dropping.not_droppable', {
          item: target.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // All checks passed
      return true;
    },

    // EXECUTE phase - Actually drop the object
    execute: (command: ResolvedIFCommand, context: GameContext): SemanticEvent[] => {
      if (!command.noun) return [];

      const target = command.noun;
      const targetId = target.id;
      const events: SemanticEvent[] = [];

      // Move the item to the current location
      try {
        context.world.moveEntity(targetId, context.currentLocation.id);

        // Create success event
        events.push(createEvent(
          IFEvents.ITEM_DROPPED,
          { 
            itemId: targetId,
            itemName: target.attributes[IFAttributes.NAME],
            location: context.currentLocation.id,
            locationName: context.currentLocation.attributes[IFAttributes.NAME]
          },
          { 
            narrate: true,
            location: context.currentLocation.id
          }
        ));
      } catch (error) {
        // If the move fails, report the error
        events.push(createEvent(
          IFEvents.ACTION_FAILED,
          { 
            action: IFActions.DROPPING,
            reason: 'move_failed',
            message: error instanceof Error ? error.message : 'Failed to drop item'
          },
          { narrate: true }
        ));
      }

      return events;
    }
  }
};

