 */

// Import from local action types
import { ActionDefinition } from './types';

// Import from parser types  
import { ResolvedIFCommand } from '../parser/if-parser-types';

// Import from world model types
import { WorldModelContext } from '../world-model/integration/world-model-context';
import { TraitType } from '../world-model/traits/trait-types';
import { IFEntity } from '../world-model/traits/if-entity';
import { getEntityName, createValidator } from '../world-model/integration/trait-validation';

// Import from Core through controlled boundary
import { SemanticEvent, createEvent } from '../core-imports';

// Import IF-specific constants from local constants
import { IFActions } from '../constants/if-actions';
import { IFEvents } from '../constants/if-events';

/**
 * Examining action definition
 * 
 * Handles "examine [object]", "look at [object]", "x [object]" commands
 */
export const examiningAction: ActionDefinition = {
  id: IFActions.EXAMINING,
  name: IFActions.EXAMINING,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: false,
    undoable: false,
    category: 'observation'
  },
  phases: {
    // VALIDATE phase - Check if examining is possible
    validate: (command: ResolvedIFCommand, context: WorldModelContext): boolean | string => {
      const validator = createValidator(context);

      // Must have a target
      if (!command.noun) {
        return 'What do you want to examine?';
      }

      // Get entity from world
      const target = context.world.getEntity(command.noun.id);
      if (!target) {
        return 'That doesn\'t exist.';
      }

      // Check if visible
      const visibleCheck = validator.validateVisible(target);
      if (visibleCheck !== true) return visibleCheck;

      // Check for scriptable hook
      if (target.has(TraitType.SCRIPTABLE)) {
        const scriptable = target.get(TraitType.SCRIPTABLE);
        if (scriptable?.data.beforeExamine) {
          const result = scriptable.data.beforeExamine(target, context);
          if (result !== true) return result;
        }
      }

      // All checks passed
      return true;
    },

    // EXECUTE phase - Examine the object
    execute: (command: ResolvedIFCommand, context: WorldModelContext): SemanticEvent[] => {
      if (!command.noun) return [];

      const target = context.world.getEntity(command.noun.id)!;
      const events: SemanticEvent[] = [];

      // Mark as touched (examined) if it has identity trait
      if (target.has(TraitType.IDENTITY)) {
        context.world.updateTrait(target.id, TraitType.IDENTITY, {
          touched: true
        });
      }

      // Create examination event with all details
      const examineData: Record<string, unknown> = {
        itemId: target.id,
        itemName: getEntityName(target),
        itemType: target.type
      };

      // Add description from identity trait
      if (target.has(TraitType.IDENTITY)) {
        const identity = target.get(TraitType.IDENTITY)!;
        examineData.description = identity.data.description || null;
        examineData.details = identity.data.details || null;
      }

      // Add readable text if present
      if (target.has(TraitType.READABLE)) {
        const readable = target.get(TraitType.READABLE)!;
        examineData.isReadable = true;
        examineData.text = readable.data.text;
        examineData.shortText = readable.data.shortText;
      }

      // Add container-specific information
      if (target.has(TraitType.CONTAINER)) {
        const container = target.get(TraitType.CONTAINER)!;
        examineData.isContainer = true;
        examineData.isOpen = !target.has(TraitType.OPENABLE) || target.isOpen;
        examineData.capacity = container.data.capacity;

        if (examineData.isOpen) {
          // Get contents
          const contents = context.world.getContents(target.id);
          examineData.contents = contents.map(item => {
            const entity = context.world.getEntity(item);
            return entity ? {
              id: item,
              name: getEntityName(entity)
            } : null;
          }).filter(Boolean);
          examineData.isEmpty = contents.length === 0;
        }
      }

      // Add supporter-specific information
      if (target.has(TraitType.SUPPORTER)) {
        const supporter = target.get(TraitType.SUPPORTER)!;
        examineData.isSupporter = true;
        examineData.supportCapacity = supporter.data.capacity;

        const supported = context.world.getContents(target.id);
        examineData.supportedItems = supported.map(item => {
          const entity = context.world.getEntity(item);
          return entity ? {
            id: item,
            name: getEntityName(entity)
          } : null;
        }).filter(Boolean);
        examineData.hasItems = supported.length > 0;
      }

      // Add openable information
      if (target.has(TraitType.OPENABLE)) {
        const openable = target.get(TraitType.OPENABLE)!;
        examineData.isOpenable = true;
        examineData.isOpen = openable.data.isOpen;
      }

      // Add lockable information
      if (target.has(TraitType.LOCKABLE)) {
        const lockable = target.get(TraitType.LOCKABLE)!;
        examineData.isLockable = true;
        examineData.isLocked = lockable.data.isLocked;
        examineData.keyRequired = lockable.data.keyId !== null;
      }

      // Add switchable information
      if (target.has(TraitType.SWITCHABLE)) {
        const switchable = target.get(TraitType.SWITCHABLE)!;
        examineData.isSwitchable = true;
        examineData.isOn = switchable.data.isOn;
      }

      // Add light source information
      if (target.has(TraitType.LIGHT_SOURCE)) {
        const lightSource = target.get(TraitType.LIGHT_SOURCE)!;
        examineData.isLightSource = true;
        examineData.isLit = lightSource.data.isLit;
        examineData.lightRadius = lightSource.data.radius;
      }

      // Add door-specific information
      if (target.has(TraitType.DOOR)) {
        const door = target.get(TraitType.DOOR)!;
        examineData.isDoor = true;
        examineData.connects = door.data.connects;
        examineData.isOpen = target.isOpen;
        examineData.isLocked = target.isLocked;
      }

      // Add wearable information
      if (target.has(TraitType.WEARABLE)) {
        const wearable = target.get(TraitType.WEARABLE)!;
        examineData.isWearable = true;
        examineData.isWorn = wearable.data.worn;
        examineData.wornBy = wearable.data.wornBy;
      }

      // Add valuable information
      if (target.has(TraitType.VALUABLE)) {
        const valuable = target.get(TraitType.VALUABLE)!;
        examineData.isValuable = true;
        examineData.value = valuable.data.value;
      }

      // Create the examination event
      events.push(createEvent(
        IFEvents.ITEM_EXAMINED,
        examineData,
        { 
          narrate: true,
          location: context.currentLocation.id
        }
      ));

      // Trigger after-examine script if present
      if (target.has(TraitType.SCRIPTABLE)) {
        const scriptable = target.get(TraitType.SCRIPTABLE);
        if (scriptable?.data.afterExamine) {
          const additionalEvents = scriptable.data.afterExamine(target, context);
          if (additionalEvents) {
            events.push(...additionalEvents);
          }
        }
      }

      return events;
    }
  }
};

/**
 * Dropping action - Put down a held object
 */

// Import from local action types
import { ActionDefinition } from './types';

// Import from parser types  
import { ResolvedIFCommand } from '../parser/if-parser-types';

// Import from world model types
import { WorldModelContext } from '../world-model/integration/world-model-context';
import { TraitType } from '../world-model/traits/trait-types';
import { IFEntity } from '../world-model/traits/if-entity';
import { getEntityName, ValidationMessages } from '../world-model/integration/trait-validation';

// Import from Core through controlled boundary
import { SemanticEvent, createEvent } from '../core-imports';

// Import IF-specific constants from local constants
import { IFActions } from '../constants/if-actions';
import { IFEvents } from '../constants/if-events';

/**
 * Dropping action definition
 * 
 * Handles "drop [object]" commands
 */
export const droppingAction: ActionDefinition = {
  id: IFActions.DROPPING,
  name: IFActions.DROPPING,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: true,
    undoable: true,
    category: 'manipulation'
  },
  phases: {
    // VALIDATE phase - Check if dropping is possible
    validate: (command: ResolvedIFCommand, context: WorldModelContext): boolean | string => {
      // Must have a target
      if (!command.noun) {
        return 'What do you want to drop?';
      }

      // Get entity from world
      const target = context.world.getEntity(command.noun.id);
      if (!target) {
        return 'That doesn\'t exist.';
      }

      // Can't drop yourself
      if (target.id === context.player.id) {
        return 'You can\'t drop yourself.';
      }

      // Check if held by the actor
      const heldLocation = context.world.getLocation(target.id);
      if (heldLocation !== context.player.id) {
        return ValidationMessages.notHeld(target);
      }

      // Check if it's fixed (shouldn't be if held, but check anyway)
      if (target.has(TraitType.FIXED)) {
        return 'That seems to be fixed in place.';
      }

      // Check if it's worn
      if (target.has(TraitType.WEARABLE)) {
        const wearable = target.get(TraitType.WEARABLE)!;
        if (wearable.data.worn) {
          return `You're wearing ${getEntityName(target)}. Take it off first.`;
        }
      }

      // Check for scriptable hook
      if (target.has(TraitType.SCRIPTABLE)) {
        const scriptable = target.get(TraitType.SCRIPTABLE);
        if (scriptable?.data.beforeDrop) {
          const result = scriptable.data.beforeDrop(target, context);
          if (result !== true) return result;
        }
      }

      // All checks passed
      return true;
    },

    // EXECUTE phase - Actually drop the object
    execute: (command: ResolvedIFCommand, context: WorldModelContext): SemanticEvent[] => {
      if (!command.noun) return [];

      const target = context.world.getEntity(command.noun.id)!;
      const events: SemanticEvent[] = [];

      // Move the item to the current location
      try {
        context.world.moveEntity(target.id, context.currentLocation.id);

        // Update portable trait if it tracks carrier
        if (target.has(TraitType.PORTABLE)) {
          context.world.updateTrait(target.id, TraitType.PORTABLE, {
            carriedBy: null
          });
        }

        // Create success event
        events.push(createEvent(
          IFEvents.ITEM_DROPPED,
          { 
            itemId: target.id,
            itemName: getEntityName(target),
            location: context.currentLocation.id,
            locationName: getEntityName(context.currentLocation),
            actor: context.player.id
          },
          { 
            narrate: true,
            location: context.currentLocation.id
          }
        ));

        // Trigger after-drop script if present
        if (target.has(TraitType.SCRIPTABLE)) {
          const scriptable = target.get(TraitType.SCRIPTABLE);
          if (scriptable?.data.afterDrop) {
            const additionalEvents = scriptable.data.afterDrop(target, context);
            if (additionalEvents) {
              events.push(...additionalEvents);
            }
          }
        }
      } catch (error) {
        // If the move fails, report the error
        events.push(createEvent(
          IFEvents.ACTION_FAILED,
          { 
            action: IFActions.DROPPING,
            reason: 'move_failed',
            message: error instanceof Error ? error.message : 'Failed to drop item'
          },
          { narrate: true }
        ));
      }

      return events;
    },

    // UNDO phase - Pick the item back up
    undo: (command: ResolvedIFCommand, context: WorldModelContext): SemanticEvent[] => {
      if (!command.noun) return [];

      const target = context.world.getEntity(command.noun.id);
      if (!target) return [];

      // Move back to player
      context.world.moveEntity(target.id, context.player.id);

      // Update portable trait
      if (target.has(TraitType.PORTABLE)) {
        context.world.updateTrait(target.id, TraitType.PORTABLE, {
          carriedBy: context.player.id
        });
      }

      return [createEvent(
        IFEvents.ACTION_UNDONE,
        {
          action: IFActions.DROPPING,
          itemId: target.id,
          itemName: getEntityName(target)
        },
        { narrate: true }
      )];
    }
  }
};

