/**
 * Opening action - Open a container or door
 */

// Import from local action types
import { ActionDefinition } from './types';

// Import from parser types  
import { ResolvedIFCommand } from '../parser/if-parser-types';

// Import from world model types
import { GameContext } from '../world-model/types';
import { IFEntity } from '../world-model/if-entities/types';

// Import from Core through controlled boundary
import { SemanticEvent, createEvent } from '../core-imports';

// Import IF-specific constants from local constants
import { IFActions } from '../constants/if-actions';
import { IFEvents } from '../constants/if-events';
import { IFEntityType } from '../constants/if-entity-types';
import { IFAttributes } from '../constants/if-attributes';

/**
 * Opening action definition
 * 
 * Handles "open [object]" commands
 */
export const openingAction: ActionDefinition = {
  id: IFActions.OPENING,
  name: IFActions.OPENING,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: true,
    undoable: true,
    category: 'manipulation'
  },
  phases: {
    // VALIDATE phase - Check if opening is possible
    validate: (command: ResolvedIFCommand, context: GameContext): boolean | string => {
      // Must have a target
      if (!command.noun) {
        return context.languageProvider.formatMessage('action.opening.no_target');
      }

      const target = command.noun;
      const targetId = target.id;

      // Check if it's openable
      const isOpenable = target.attributes.openable === true || 
                        target.type === IFEntityType.DOOR || 
                        target.type === IFEntityType.CONTAINER ||
                        target.attributes[IFAttributes.CONTAINER] === true;

      if (!isOpenable) {
        return context.languageProvider.formatMessage('action.opening.not_openable', {
          item: target.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Check if already open
      if (target.attributes[IFAttributes.OPEN] === true) {
        return context.languageProvider.formatMessage('action.opening.already_open', {
          item: target.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Check if locked
      if (target.attributes[IFAttributes.LOCKED] === true) {
        return context.languageProvider.formatMessage('action.opening.locked', {
          item: target.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Check if accessible
      if (!context.isAccessible(targetId)) {
        return context.languageProvider.formatMessage('action.opening.not_accessible', {
          item: target.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // All checks passed
      return true;
    },

    // EXECUTE phase - Actually open the object
    execute: (command: ResolvedIFCommand, context: GameContext): SemanticEvent[] => {
      if (!command.noun) return [];

      const target = command.noun;
      const targetId = target.id;
      const events: SemanticEvent[] = [];

      // Update the object's open state
      // Get fresh entity state to ensure we have complete attributes
      const currentEntity = context.world.getEntity(targetId) as IFEntity;
      if (currentEntity) {
        context.world.updateEntity(targetId, {
          attributes: {
            ...currentEntity.attributes,
            [IFAttributes.OPEN]: true
          }
        });
      }

      // Determine what type of thing was opened
      const eventType = target.type === IFEntityType.DOOR ? 
        IFEvents.DOOR_OPENED : IFEvents.CONTAINER_OPENED;

      // Create success event
      events.push(createEvent(
        eventType,
        { 
          itemId: targetId,
          itemName: target.attributes[IFAttributes.NAME],
          itemType: target.type
        },
        { 
          narrate: true,
          location: context.currentLocation.id
        }
      ));

      // If it's a container with contents, check what's inside
      if ((target.type === IFEntityType.CONTAINER || target.attributes[IFAttributes.CONTAINER])) {
        const contents = context.world.getContents(targetId);

        if (contents.length > 0) {
          // Create an event about revealing contents
          events.push(createEvent(
            IFEvents.HIDDEN_ITEM_REVEALED,
            { 
              containerId: targetId,
              containerName: target.attributes[IFAttributes.NAME],
              items: contents.map(item => ({
                id: item.id,
                name: item.attributes[IFAttributes.NAME]
              }))
            },
            { 
              narrate: true,
              location: context.currentLocation.id
            }
          ));
        }
      }

      return events;
    }
  }
};

