// Import from local action types
import { ActionDefinition } from './types';

// Import from parser types  
import { ResolvedIFCommand } from '../parser/if-parser-types';

// Import from world model types
import { WorldModelContext } from '../world-model/integration/world-model-context';
import { TraitType } from '../world-model/traits/trait-types';
import { IFEntity } from '../world-model/traits/if-entity';
import { getEntityName, createValidator } from '../world-model/integration/trait-validation';

// Import from Core through controlled boundary
import { SemanticEvent, createEvent } from '../core-imports';

// Import IF-specific constants from local constants
import { IFActions } from '../constants/if-actions';
import { IFEvents } from '../constants/if-events';

/**
 * Opening action definition
 * 
 * Handles "open [object]" commands
 */
export const openingAction: ActionDefinition = {
  id: IFActions.OPENING,
  name: IFActions.OPENING,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: true,
    undoable: true,
    category: 'manipulation'
  },
  phases: {
    // VALIDATE phase - Check if opening is possible
    validate: (command: ResolvedIFCommand, context: WorldModelContext): boolean | string => {
      const validator = createValidator(context);

      // Must have a target
      if (!command.noun) {
        return 'What do you want to open?';
      }

      // Get entity from world
      const target = context.world.getEntity(command.noun.id);
      if (!target) {
        return 'That doesn\'t exist.';
      }

      // Check if it's openable
      if (!target.has(TraitType.OPENABLE)) {
        return `${getEntityName(target)} can't be opened.`;
      }

      const openable = target.get(TraitType.OPENABLE)!;

      // Check if already open
      if (openable.data.isOpen) {
        return `${getEntityName(target)} is already open.`;
      }

      // Check if locked
      if (target.has(TraitType.LOCKABLE)) {
        const lockable = target.get(TraitType.LOCKABLE)!;
        if (lockable.data.isLocked) {
          return `${getEntityName(target)} is locked.`;
        }
      }

      // Check if accessible
      const reachCheck = validator.validateReachable(target);
      if (reachCheck !== true) return reachCheck;

      // Check for scriptable hook
      if (target.has(TraitType.SCRIPTABLE)) {
        const scriptable = target.get(TraitType.SCRIPTABLE);
        if (scriptable?.data.beforeOpen) {
          const result = scriptable.data.beforeOpen(target, context);
          if (result !== true) return result;
        }
      }

      // All checks passed
      return true;
    },

    // EXECUTE phase - Actually open the object
    execute: (command: ResolvedIFCommand, context: WorldModelContext): SemanticEvent[] => {
      if (!command.noun) return [];

      const target = context.world.getEntity(command.noun.id)!;
      const events: SemanticEvent[] = [];

      // Update the object's open state
      context.world.updateTrait(target.id, TraitType.OPENABLE, {
        isOpen: true
      });

      // Determine what type of thing was opened
      const eventType = target.has(TraitType.DOOR) ? 
        IFEvents.DOOR_OPENED : IFEvents.CONTAINER_OPENED;

      // Create success event
      events.push(createEvent(
        eventType,
        { 
          itemId: target.id,
          itemName: getEntityName(target),
          itemType: target.type,
          actor: context.player.id
        },
        { 
          narrate: true,
          location: context.currentLocation.id
        }
      ));

      // If it's a container with contents, check what's inside
      if (target.has(TraitType.CONTAINER)) {
        const contents = context.world.getContents(target.id);

        if (contents.length > 0) {
          // Get entity details for contents
          const items = contents
            .map(id => context.world.getEntity(id))
            .filter((entity): entity is IFEntity => entity !== null)
            .map(item => ({
              id: item.id,
              name: getEntityName(item)
            }));

          // Create an event about revealing contents
          events.push(createEvent(
            IFEvents.HIDDEN_ITEM_REVEALED,
            { 
              containerId: target.id,
              containerName: getEntityName(target),
              items: items
            },
            { 
              narrate: true,
              location: context.currentLocation.id
            }
          ));
        }
      }

      // Mark as touched
      if (target.has(TraitType.IDENTITY)) {
        context.world.updateTrait(target.id, TraitType.IDENTITY, {
          touched: true
        });
      }

      // Trigger after-open script if present
      if (target.has(TraitType.SCRIPTABLE)) {
        const scriptable = target.get(TraitType.SCRIPTABLE);
        if (scriptable?.data.afterOpen) {
          const additionalEvents = scriptable.data.afterOpen(target, context);
          if (additionalEvents) {
            events.push(...additionalEvents);
          }
        }
      }

      return events;
    },

    // UNDO phase - Close the object again
    undo: (command: ResolvedIFCommand, context: WorldModelContext): SemanticEvent[] => {
      if (!command.noun) return [];

      const target = context.world.getEntity(command.noun.id);
      if (!target || !target.has(TraitType.OPENABLE)) return [];

      // Close it again
      context.world.updateTrait(target.id, TraitType.OPENABLE, {
        isOpen: false
      });

      return [createEvent(
        IFEvents.ACTION_UNDONE,
        {
          action: IFActions.OPENING,
          itemId: target.id,
          itemName: getEntityName(target)
        },
        { narrate: true }
      )];
    }
  }
};

