      // Open it again
      context.world.updateTrait(target.id, TraitType.OPENABLE, {
        isOpen: true
      });

      return [createEvent(
        IFEvents.ACTION_UNDONE,
        {
          action: IFActions.CLOSING,
          itemId: target.id,
          itemName: getEntityName(target)
        },
        { narrate: true }
      )];
    }
  }
};

