/**
 * Putting action - Put an object in or on another object
 */

// Import from local action types
import { ActionDefinition } from './types';

// Import from parser types  
import { ResolvedIFCommand } from '../parser/if-parser-types';

// Import from world model types
import { GameContext } from '../world-model/types';

// Import from Core through controlled boundary
import { SemanticEvent, createEvent } from '../core-imports';

// Import IF-specific constants from local constants
import { IFActions } from '../constants/if-actions';
import { IFEvents } from '../constants/if-events';
import { IFEntityType } from '../constants/if-entity-types';

/**
 * Putting action definition
 * 
 * Handles "put [object] in [container]", "put [object] on [supporter]", "place [object] in/on [target]" commands
 */
export const puttingAction: ActionDefinition = {
  id: IFActions.PUTTING,
  name: IFActions.PUTTING,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: true,
    undoable: true,
    category: 'manipulation'
  },
  phases: {
    // VALIDATE phase - Check if putting is possible
    validate: (command: ResolvedIFCommand, context: GameContext): boolean | string => {
      // Must have a direct object
      if (!command.noun) {
        return context.languageProvider.formatMessage('action.putting.no_target');
      }

      // Must have a second object (what to put it in/on)
      if (!command.preposition || !command.second) {
        return context.languageProvider.formatMessage('action.putting.no_container');
      }

      const item = command.noun;
      const container = command.second;
      const preposition = command.preposition;

      // Can't put something in/on itself
      if (item.id === container.id) {
        return context.languageProvider.formatMessage('action.putting.put_in_self', {
          item: item.attributes.name || 'it'
        });
      }

      // Check if player is holding the item
      if (!context.isCarriedBy(item.id, command.actor.id)) {
        return context.languageProvider.formatMessage('action.putting.not_held', {
          item: item.attributes.name || 'that'
        });
      }

      // Check if container is a valid container or supporter
      const isContainer = container.type === IFEntityType.CONTAINER || 
                         container.attributes.container === true;
      const isSupporter = container.attributes.supporter === true;

      if (!isContainer && !isSupporter) {
        return context.languageProvider.formatMessage('action.putting.not_container', {
          container: container.attributes.name || 'that'
        });
      }

      // If putting IN something, it must be a container
      if (preposition === 'in' && !isContainer) {
        return context.languageProvider.formatMessage('action.putting.not_container', {
          container: container.attributes.name || 'that'
        });
      }

      // If putting ON something, it must be a supporter
      if (preposition === 'on' && !isSupporter) {
        return context.languageProvider.formatMessage('action.putting.not_supporter', {
          supporter: container.attributes.name || 'that'
        });
      }

      // If putting in a container, check if it's open
      if (preposition === 'in' && isContainer && 
          container.attributes.openable && 
          !container.attributes.open) {
        return context.languageProvider.formatMessage('action.putting.container_closed', {
          container: container.attributes.name || 'that'
        });
      }

      // Check if container is full
      if (container.attributes.capacity) {
        const currentContents = context.world.getContents(container.id);
        const capacity = container.attributes.capacity as number;
        if (currentContents.length >= capacity) {
          return context.languageProvider.formatMessage('action.putting.wont_fit', {
            item: item.attributes.name || 'that',
            container: container.attributes.name || 'it'
          });
        }
      }

      // Check if container is accessible
      if (!context.isAccessible(container.id)) {
        return context.languageProvider.formatMessage('action.putting.not_accessible', {
          container: container.attributes.name || 'that'
        });
      }

      // All checks passed
      return true;
    },

    // EXECUTE phase - Actually put the object
    execute: (command: ResolvedIFCommand, context: GameContext): SemanticEvent[] => {
      if (!command.noun || !command.second || !command.preposition) return [];

      const item = command.noun;
      const container = command.second;
      const preposition = command.preposition;
      const events: SemanticEvent[] = [];

      // Move the item from player to container
      context.world.moveEntity(item.id, container.id);

      // Create the appropriate event based on whether it's a container or supporter
      const eventType = preposition === 'in' ? IFEvents.ITEM_PUT_IN : IFEvents.ITEM_PUT_ON;

      events.push(createEvent(
        eventType,
        {
          itemId: item.id,
          itemName: item.attributes.name,
          containerId: container.id,
          containerName: container.attributes.name,
          preposition: preposition,
          isContainer: container.attributes.container === true,
          isSupporter: container.attributes.supporter === true
        },
        {
          narrate: true,
          location: context.currentLocation.id
        }
      ));

      return events;
    }
  }
};

// packages/stdlib/src/actions/types/index.ts

import { ResolvedIFCommand } from '../../parser/if-parser-types';
import { GameContext } from '../../world-model/types';
import { SemanticEvent } from '../../core-imports';

/**
 * Base action definition for the action system
 * This is an IF concept that belongs in StdLib, not Core
 */
export interface ActionDefinition {
  /**
   * Unique identifier for the action
   */
  id: string;

  /**
   * Human-readable name for the action
   */
  name: string;

  /**
   * List of verbs that trigger this action
   * These are populated by the language provider
   */
  verbs?: string[];

  /**
   * Action metadata
   */
  metadata?: {
    /**
     * Whether this action is reversible
     */
    reversible?: boolean;

    /**
     * Category of the action
     */
    category?: string;

    /**
     * Additional metadata
     */
    [key: string]: any;
  };

  /**
   * Action execution phases
   */
  phases: {
    /**
     * Validate if the action can be performed
     * Return true to continue, or a string message to block
     */
    validate?: (command: ResolvedIFCommand, context: GameContext) => boolean | string;

    /**
     * Execute the action
     * Returns events describing what happened
     */
    execute: (command: ResolvedIFCommand, context: GameContext) => SemanticEvent[];
  };
}

/**
 * World Model Context
 * 
 * Integrates the trait-based world model with the execution system.
 * Provides a unified context for actions to interact with entities.
 */

import { ExecutionContext } from '../../core-imports';
import { WorldModelService } from '../services/world-model-service';
import { IFEntity } from '../traits/if-entity';
import { TraitType } from '../traits/trait-types';
import { ResolvedIFCommand } from '../../parser/if-parser-types';

/**
 * World model integrated game context
 */
export interface WorldModelContext extends ExecutionContext {
  /**
   * The world model service
   */
  world: WorldModelService;

  /**
   * The player entity
   */
  player: IFEntity;

  /**
   * The current location entity
   */
  currentLocation: IFEntity;

  /**
   * Current command being executed
   */
  currentCommand?: ResolvedIFCommand;

  /**
   * Check if an entity is visible from player's perspective
   */
  isVisible(entityId: string): boolean;

  /**
   * Check if an entity is reachable by the player
   */
  isReachable(entityId: string): boolean;

  /**
   * Find the location of an entity
   */
  findLocationOf(entityId: string): string | null;

  /**
   * Get entities in current scope
   */
  getScope(): IFEntity[];

  /**
   * Get visible entities
   */
  getVisibleEntities(): IFEntity[];

  /**
   * Get reachable entities
   */
  getReachableEntities(): IFEntity[];

  /**
   * Get the current turn number
   */
  getTurnNumber(): number;

  /**
   * Additional game state
   */
  gameState: Record<string, any>;
}

/**
 * Implementation of world model context
 */
export class WorldModelContextImpl implements WorldModelContext {
  world: WorldModelService;
  player: IFEntity;
  currentCommand?: ResolvedIFCommand;
  gameState: Record<string, any> = {};

  constructor(
    public worldService: WorldModelService,
    public textService: ExecutionContext['textService'],
    public languageProvider: ExecutionContext['languageProvider']
  ) {
    this.world = worldService;

    // Get player entity
    const player = worldService.getPlayer();
    if (!player) {
      throw new Error('No player entity found in world');
    }
    this.player = player;
  }

  get currentLocation(): IFEntity {
    const locationId = this.world.getPlayerLocation();
    if (!locationId) {
      throw new Error('Player has no location');
    }

    const location = this.world.getEntity(locationId);
    if (!location) {
      throw new Error(`Location ${locationId} not found`);
    }

    return location;
  }

  isVisible(entityId: string): boolean {
    return this.world.isVisibleFrom(entityId, this.player.id);
  }

  isReachable(entityId: string): boolean {
    return this.world.isReachableFrom(entityId, this.player.id);
  }

  findLocationOf(entityId: string): string | null {
    return this.world.getLocation(entityId);
  }

  getScope(): IFEntity[] {
    const scope = this.world.getPlayerScope();
    return Array.from(scope.visible)
      .map(id => this.world.getEntity(id))
      .filter((e): e is IFEntity => e !== undefined);
  }

  getVisibleEntities(): IFEntity[] {
    const scope = this.world.getPlayerScope();
    return Array.from(scope.visible)
      .map(id => this.world.getEntity(id))
      .filter((e): e is IFEntity => e !== undefined);
  }

  getReachableEntities(): IFEntity[] {
    const scope = this.world.getPlayerScope();
    return Array.from(scope.reachable)
      .map(id => this.world.getEntity(id))
      .filter((e): e is IFEntity => e !== undefined);
  }

  getTurnNumber(): number {
    return this.gameState.turnNumber || 0;
  }
}

/**
 * Factory function to create a world model context
 */
export function createWorldModelContext(params: {
  worldService: WorldModelService;
  textService: ExecutionContext['textService'];
  languageProvider: ExecutionContext['languageProvider'];
}): WorldModelContext {
  return new WorldModelContextImpl(
    params.worldService,
    params.textService,
    params.languageProvider
  );
}

// packages/stdlib/src/world-model/traits/types.ts

/**
 * Base interface for all entity traits in the world model.
 * Traits represent composable behaviors and properties that entities can have.
 */
export interface Trait {
  /** Unique type identifier for this trait */
  readonly type: string;

  /** Version for migration support */
  readonly version?: number;

  /** Optional validation method */
  validate?(): void;

  /** Optional cleanup when trait is removed */
  cleanup?(): void;
}

/**
 * Constructor type for creating traits
 */
export interface TraitConstructor<T extends Trait = Trait> {
  new (data?: any): T;
  readonly type: string;
}

/**
 * Type guard for checking if an object is a trait
 */
export function isTrait(obj: any): obj is Trait {
  return obj && typeof obj === 'object' && 'type' in obj && typeof obj.type === 'string';
}

/**
 * Helper type to extract trait data type from a trait class
 */
export type TraitData<T extends Trait> = Omit<T, keyof Trait>;

/**
 * Registry for trait constructors
 */
export class TraitRegistry {
  private static traits = new Map<string, TraitConstructor>();

  static register(trait: TraitConstructor): void {
    if (this.traits.has(trait.type)) {
      throw new Error(`Trait type '${trait.type}' is already registered`);
    }
    this.traits.set(trait.type, trait);
  }

  static get(type: string): TraitConstructor | undefined {
    return this.traits.get(type);
  }

  static has(type: string): boolean {
    return this.traits.has(type);
  }

  static clear(): void {
    this.traits.clear();
  }
}

// packages/stdlib/src/world-model/traits/standard/identity.ts

import { ValidatedTrait, validators } from '../validation';

/**
 * Identity trait provides basic naming and description for entities.
 * This is one of the most fundamental traits in IF.
 */
export class IdentityTrait extends ValidatedTrait {
  static readonly type = 'identity';
  readonly type = 'identity';

  /** Primary name of the entity */
  name: string = '';

  /** Full description shown when examining */
  description: string = '';

  /** Alternative names/aliases the entity can be referred to by */
  aliases: string[] = [];

  /** Brief description shown in room listings */
  brief?: string;

  /** Whether the entity has a proper name (like "John" vs "a man") */
  properName: boolean = false;

  /** Article to use with the name ("a", "an", "the", "some", or empty for proper names) */
  article: string = 'a';

  constructor(data?: Partial<IdentityTrait>) {
    super();
    if (data) {
      Object.assign(this, data);
    }
  }

  validate(): void {
    validators.required(this.name, this.type, 'name');
    validators.notEmpty(this.name, this.type, 'name');
    validators.string(this.description, this.type, 'description');
    validators.unique(this.aliases, this.type, 'aliases');

    if (this.brief !== undefined) {
      validators.string(this.brief, this.type, 'brief');
    }

    validators.boolean(this.properName, this.type, 'properName');
    validators.string(this.article, this.type, 'article');

    // Validate article is one of the allowed values
    if (!this.properName && this.article) {
      validators.oneOf(this.article, ['a', 'an', 'the', 'some', ''], this.type, 'article');
    }
  }

  /**
   * Get the full name with article
   */
  getFullName(): string {
    if (this.properName || !this.article) {
      return this.name;
    }
    return `${this.article} ${this.name}`;
  }

  /**
   * Get all names this entity can be referred to by
   */
  getAllNames(): string[] {
    return [this.name, ...this.aliases];
  }

  /**
   * Check if a given name matches this entity
   */
  matchesName(name: string): boolean {
    const lowerName = name.toLowerCase();
    return this.getAllNames().some(n => n.toLowerCase() === lowerName);
  }
}

/**
 * Trait-based Validation Helpers
 * 
 * Common validation functions for actions using the trait system.
 * These replace the old type-based checks with trait-based ones.
 */

import { IFEntity } from '../traits/if-entity';
import { TraitType } from '../traits/trait-types';
import { WorldModelContext } from './world-model-context';
import { IdentityTrait } from '../traits/standard/identity';
import { PortableTrait } from '../traits/standard/portable';
import { ContainerTrait } from '../traits/standard/container';
import { OpenableTrait } from '../traits/interactive/openable';
import { LockableTrait } from '../traits/interactive/lockable';

/**
 * Get the display name of an entity
 */
export function getEntityName(entity: IFEntity): string {
  const identity = entity.get(TraitType.IDENTITY);
  return identity?.data.name || entity.id;
}

/**
 * Check if an entity can be taken
 */
export function canBeTaken(entity: IFEntity): boolean {
  // Must have portable trait
  if (!entity.has(TraitType.PORTABLE)) {
    return false;
  }

  // Must not have fixed trait
  if (entity.has(TraitType.FIXED)) {
    return false;
  }

  // Rooms cannot be taken
  if (entity.has(TraitType.ROOM)) {
    return false;
  }

  // NPCs cannot be taken (usually)
  if (entity.has(TraitType.NPC)) {
    return false;
  }

  return true;
}

/**
 * Check if an entity can contain other entities
 */
export function canContain(entity: IFEntity): boolean {
  return entity.has(TraitType.CONTAINER) || 
         entity.has(TraitType.SUPPORTER) || 
         entity.has(TraitType.ROOM) ||
         entity.has(TraitType.PLAYER);
}

/**
 * Check if a container is open
 */
export function isOpen(entity: IFEntity): boolean {
  if (!entity.has(TraitType.OPENABLE)) {
    // Not openable means always "open" for access
    return true;
  }

  const openable = entity.get(TraitType.OPENABLE);
  return openable?.data.open === true;
}

/**
 * Check if an entity is locked
 */
export function isLocked(entity: IFEntity): boolean {
  if (!entity.has(TraitType.LOCKABLE)) {
    return false;
  }

  const lockable = entity.get(TraitType.LOCKABLE);
  return lockable?.data.locked === true;
}

/**
 * Check if an entity is worn
 */
export function isWorn(entity: IFEntity, wearer: IFEntity): boolean {
  if (!entity.has(TraitType.WEARABLE)) {
    return false;
  }

  const wearable = entity.get(TraitType.WEARABLE);
  const location = entity.get(TraitType.LOCATION);

  return wearable?.data.worn === true && 
         location?.data.parent === wearer.id;
}

/**
 * Check if an entity is lit (providing light)
 */
export function isLit(entity: IFEntity): boolean {
  if (!entity.has(TraitType.LIGHT_SOURCE)) {
    return false;
  }

  const lightSource = entity.get(TraitType.LIGHT_SOURCE);
  return lightSource?.data.lit === true;
}

/**
 * Check if an entity is switched on
 */
export function isSwitchedOn(entity: IFEntity): boolean {
  if (!entity.has(TraitType.SWITCHABLE)) {
    return false;
  }

  const switchable = entity.get(TraitType.SWITCHABLE);
  return switchable?.data.on === true;
}

/**
 * Check if player can see inside a container
 */
export function canSeeInside(container: IFEntity): boolean {
  if (!canContain(container)) {
    return false;
  }

  // Rooms and supporters are always visible inside
  if (container.has(TraitType.ROOM) || container.has(TraitType.SUPPORTER)) {
    return true;
  }

  // For containers, check if open or transparent
  if (container.has(TraitType.CONTAINER)) {
    const containerTrait = container.get(TraitType.CONTAINER);
    if (containerTrait?.data.transparent) {
      return true;
    }

    return isOpen(container);
  }

  // Players can see their inventory
  if (container.has(TraitType.PLAYER)) {
    return true;
  }

  return false;
}

/**
 * Get capacity information for a container
 */
export function getCapacity(entity: IFEntity): { current: number; max: number } | null {
  if (!canContain(entity)) {
    return null;
  }

  let maxCapacity = -1; // Unlimited by default

  if (entity.has(TraitType.CONTAINER)) {
    const container = entity.get(TraitType.CONTAINER);
    maxCapacity = container?.data.capacity ?? -1;
  } else if (entity.has(TraitType.SUPPORTER)) {
    const supporter = entity.get(TraitType.SUPPORTER);
    maxCapacity = supporter?.data.capacity ?? -1;
  }

  // Count current contents
  const world = (entity as any).__world; // Need reference to world service
  const contents = world?.getContents(entity.id) || [];

  return {
    current: contents.length,
    max: maxCapacity
  };
}

/**
 * Check if an entity blocks movement (for doors)
 */
export function blocksMovement(entity: IFEntity): boolean {
  if (!entity.has(TraitType.DOOR)) {
    return false;
  }

  // Closed doors block movement
  return !isOpen(entity);
}

/**
 * Get the key for a lockable entity
 */
export function getRequiredKey(entity: IFEntity): string | null {
  if (!entity.has(TraitType.LOCKABLE)) {
    return null;
  }

  const lockable = entity.get(TraitType.LOCKABLE);
  return lockable?.data.keyId || null;
}

/**
 * Check if an entity is a valid key for a lock
 */
export function isKeyFor(key: IFEntity, lock: IFEntity): boolean {
  const requiredKey = getRequiredKey(lock);
  return requiredKey === key.id;
}

/**
 * Check if an entity is edible
 */
export function isEdible(entity: IFEntity): boolean {
  return entity.has(TraitType.EDIBLE);
}

/**
 * Check if an entity is readable
 */
export function isReadable(entity: IFEntity): boolean {
  return entity.has(TraitType.READABLE);
}

/**
 * Get the text content of a readable entity
 */
export function getReadableText(entity: IFEntity): string | null {
  if (!entity.has(TraitType.READABLE)) {
    return null;
  }

  const readable = entity.get(TraitType.READABLE);
  return readable?.data.text || null;
}

/**
 * Common validation messages
 */
export const ValidationMessages = {
  notTakeable: (entity: IFEntity) => 
    `${getEntityName(entity)} cannot be taken.`,

  alreadyHeld: (entity: IFEntity) => 
    `You are already holding ${getEntityName(entity)}.`,

  notAccessible: (entity: IFEntity) => 
    `You can't reach ${getEntityName(entity)}.`,

  containerClosed: (entity: IFEntity) => 
    `${getEntityName(entity)} is closed.`,

  containerLocked: (entity: IFEntity) => 
    `${getEntityName(entity)} is locked.`,

  containerFull: (entity: IFEntity) => 
    `${getEntityName(entity)} is full.`,

  notOpenable: (entity: IFEntity) => 
    `${getEntityName(entity)} cannot be opened.`,

  alreadyOpen: (entity: IFEntity) => 
    `${getEntityName(entity)} is already open.`,

  alreadyClosed: (entity: IFEntity) => 
    `${getEntityName(entity)} is already closed.`,

  notSwitchable: (entity: IFEntity) => 
    `${getEntityName(entity)} cannot be switched.`,

  alreadyOn: (entity: IFEntity) => 
    `${getEntityName(entity)} is already on.`,

  alreadyOff: (entity: IFEntity) => 
    `${getEntityName(entity)} is already off.`,

  notWearable: (entity: IFEntity) => 
    `${getEntityName(entity)} cannot be worn.`,

  alreadyWorn: (entity: IFEntity) => 
    `You are already wearing ${getEntityName(entity)}.`,

  notWorn: (entity: IFEntity) => 
    `You are not wearing ${getEntityName(entity)}.`,

  wrongKey: (key: IFEntity, lock: IFEntity) => 
    `${getEntityName(key)} doesn't fit ${getEntityName(lock)}.`,

  noKey: () => 
    `You need a key to do that.`
};

/**
 * Validation helper for common checks
 */
export class ActionValidator {
  constructor(private context: WorldModelContext) {}

  /**
   * Validate that an entity exists and is in scope
   */
  validateInScope(entity: IFEntity | undefined, errorMessage?: string): string | true {
    if (!entity) {
      return errorMessage || 'You need to specify what to act on.';
    }

    if (!this.context.isVisible(entity.id)) {
      return `You can't see ${getEntityName(entity)} here.`;
    }

    return true;
  }

  /**
   * Validate that an entity is reachable
   */
  validateReachable(entity: IFEntity, errorMessage?: string): string | true {
    const scopeCheck = this.validateInScope(entity);
    if (scopeCheck !== true) return scopeCheck;

    if (!this.context.isReachable(entity.id)) {
      return errorMessage || ValidationMessages.notAccessible(entity);
    }

    return true;
  }

  /**
   * Validate that a container is accessible (open if needed)
   */
  validateContainerAccessible(container: IFEntity): string | true {
    if (!canContain(container)) {
      return `${getEntityName(container)} cannot contain things.`;
    }

    if (!canSeeInside(container)) {
      return ValidationMessages.containerClosed(container);
    }

    return true;
  }

  /**
   * Validate capacity constraints
   */
  validateCapacity(container: IFEntity, itemsToAdd: number = 1): string | true {
    const capacity = getCapacity(container);
    if (!capacity) {
      return `${getEntityName(container)} cannot contain things.`;
    }

    if (capacity.max !== -1 && capacity.current + itemsToAdd > capacity.max) {
      return ValidationMessages.containerFull(container);
    }

    return true;
  }
}

/**
 * Create an action validator for a context
 */
export function createValidator(context: WorldModelContext): ActionValidator {
  return new ActionValidator(context);
}

