/**
 * Examining action - Look at an object in detail
 */

// Import from local action types
import { ActionDefinition } from './types';

// Import from parser types  
import { ResolvedIFCommand } from '../parser/if-parser-types';

// Import from world model types
import { GameContext } from '../world-model/types';

// Import from Core through controlled boundary
import { SemanticEvent, createEvent } from '../core-imports';

// Import IF-specific constants from local constants
import { IFActions } from '../constants/if-actions';
import { IFEvents } from '../constants/if-events';

/**
 * Examining action definition
 * 
 * Handles "examine [object]", "look at [object]", "x [object]" commands
 */
export const examiningAction: ActionDefinition = {
  id: IFActions.EXAMINING,
  name: IFActions.EXAMINING,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: false,
    undoable: false,
    category: 'observation'
  },
  phases: {
    // VALIDATE phase - Check if examining is possible
    validate: (command: ResolvedIFCommand, context: GameContext): boolean | string => {
      // Must have a target
      if (!command.noun) {
        return context.languageProvider.formatMessage('action.examining.no_target');
      }

      const target = command.noun;
      const targetId = target.id;

      // Check if visible
      if (!context.isVisible(targetId)) {
        return context.languageProvider.formatMessage('action.examining.not_visible', {
          item: target.attributes.name || 'that'
        });
      }

      // All checks passed
      return true;
    },

    // EXECUTE phase - Examine the object
    execute: (command: ResolvedIFCommand, context: GameContext): SemanticEvent[] => {
      if (!command.noun) return [];

      const target = command.noun;
      const events: SemanticEvent[] = [];

      // Mark as touched (examined)
      if (!target.attributes.touched) {
        // Get fresh entity state to ensure we have complete attributes
        const currentEntity = context.world.getEntity(target.id);
        if (currentEntity) {
          context.world.updateEntity(target.id, {
            attributes: {
              ...currentEntity.attributes,
              touched: true
            }
          });
        }
      }

      // Create examination event with all details
      const examineData: Record<string, unknown> = {
        itemId: target.id,
        itemName: target.attributes.name,
        itemType: target.type,
        description: target.attributes.description || null,
        details: target.attributes.details || null
      };

      // Add container-specific information
      if (target.attributes.container) {
        examineData.isContainer = true;
        examineData.isOpen = target.attributes.open !== false;

        if (examineData.isOpen) {
          // Get contents
          const contents = context.world.getContents(target.id);
          examineData.contents = contents.map(item => ({
            id: item.id,
            name: item.attributes.name
          }));
          examineData.isEmpty = contents.length === 0;
        }
      }

      // Add supporter-specific information
      if (target.attributes.supporter) {
        examineData.isSupporter = true;
        const supported = context.world.getContents(target.id);
        examineData.supportedItems = supported.map(item => ({
          id: item.id,
          name: item.attributes.name
        }));
        examineData.hasItems = supported.length > 0;
      }

      // Add device-specific information
      if (target.attributes.switchable) {
        examineData.isSwitchable = true;
        examineData.isOn = target.attributes.on === true;
      }

      // Add door-specific information
      if (target.attributes.door) {
        examineData.isDoor = true;
        examineData.isOpen = target.attributes.open === true;
        examineData.isLocked = target.attributes.locked === true;
      }

      // Add wearable information
      if (target.attributes.wearable) {
        examineData.isWearable = true;
        examineData.isWorn = target.attributes.worn === true;
      }

      // Add readable information
      if (target.attributes.readable) {
        examineData.isReadable = true;
        examineData.text = target.attributes.text;
      }

      // Add valuable information
      if (target.attributes.valuable) {
        examineData.isValuable = true;
        examineData.value = target.attributes.value;
      }

      // Create the examination event
      events.push(createEvent(
        IFEvents.ITEM_EXAMINED,
        examineData,
        { 
          narrate: true,
          location: context.currentLocation.id
        }
      ));

      return events;
    }
  }
};

# Action Migration Strategy: Attribute to Trait-Based System

## Current Situation

1. Actions are defined to use `GameContext` interface
2. The trait-based world model uses `WorldModelContext` 
3. Entities in actions still use attributes (e.g., `target.attributes.name`)
4. The trait system stores data as direct properties, not under a `data` property

## Migration Options

### Option 1: Adapter Pattern (Recommended for Now)
Create an adapter that makes `WorldModelContext` compatible with `GameContext`:

```typescript
// world-model/integration/context-adapter.ts
export class GameContextAdapter implements GameContext {
  constructor(private worldContext: WorldModelContext) {}

  get world() {
    // Return a facade that provides the old API
    return {
      getEntity: (id: string) => {
        const entity = this.worldContext.world.getEntity(id);
        if (!entity) return null;

        // Convert trait-based entity to attribute-based
        return this.convertToAttributeEntity(entity);
      },
      // ... other methods
    };
  }

  private convertToAttributeEntity(entity: IFEntity) {
    const attributes: Record<string, any> = {};

    // Map common traits to attributes
    if (entity.has(TraitType.IDENTITY)) {
      const identity = entity.get(TraitType.IDENTITY) as IdentityTrait;
      attributes.name = identity.name;
      attributes.description = identity.description;
    }

    if (entity.has(TraitType.PORTABLE)) {
      attributes.takeable = true;
      attributes.portable = true;
    }

    // ... map other traits

    return {
      id: entity.id,
      type: entity.type,
      attributes,
      relationships: entity.relationships
    };
  }
}
```

### Option 2: Dual Support in Actions
Make actions work with both contexts:

```typescript
export const examiningAction: ActionDefinition = {
  // ...
  phases: {
    validate: (command: ResolvedIFCommand, context: GameContext): boolean | string => {
      // Check if it's a WorldModelContext
      if ('world' in context && context.world instanceof WorldModelService) {
        return validateWithTraits(command, context as WorldModelContext);
      }
      // Otherwise use attribute-based validation
      return validateWithAttributes(command, context);
    }
  }
};
```

### Option 3: Update Action Interface
Change the action interface to accept a union type:

```typescript
export interface ActionDefinition {
  phases: {
    validate?: (
      command: ResolvedIFCommand, 
      context: GameContext | WorldModelContext
    ) => boolean | string;
    // ...
  };
}
```

## Recommended Approach

1. **Phase 1: Keep Attribute-Based Actions** (Current)
   - Actions continue using `GameContext` and attributes
   - This maintains compatibility with existing code

2. **Phase 2: Create Adapter Layer**
   - Implement `GameContextAdapter` that wraps `WorldModelContext`
   - Map traits to attributes for backward compatibility
   - Test with existing actions

3. **Phase 3: Gradual Migration**
   - Create new trait-aware versions of actions in `/world-model/integration/actions/`
   - Keep old versions for compatibility
   - Allow Story class to use either version

4. **Phase 4: Full Migration**
   - Once all systems are trait-aware, remove attribute-based code
   - Update action interface to use `WorldModelContext`
   - Remove adapters

## Immediate Actions

For now, keep the actions using the attribute-based approach:

1. Revert examining.ts, dropping.ts, opening.ts, and closing.ts to use attributes
2. Focus on getting the existing system working
3. Create the adapter layer as a separate task
4. Migrate actions one at a time after the adapter is tested

## Benefits of This Approach

- **No Breaking Changes**: Existing code continues to work
- **Incremental Migration**: Can migrate one action at a time
- **Testing**: Can compare old vs new implementations
- **Flexibility**: Can support both systems during transition
- **Safety**: Can roll back if issues arise

## Example Adapter Implementation

```typescript
// Quick example of key methods
class GameContextAdapter implements GameContext {
  isVisible(entityId: string): boolean {
    return this.worldContext.isVisible(entityId);
  }

  isAccessible(entityId: string): boolean {
    return this.worldContext.isReachable(entityId);
  }

  isCarriedBy(entityId: string, carrierId: string): boolean {
    const location = this.worldContext.world.getLocation(entityId);
    return location === carrierId;
  }

  // ... etc
}
```

This approach allows us to maintain stability while gradually migrating to the better trait-based system.

/**
 * Dropping action - Put down a held object
 */

// Import from local action types
import { ActionDefinition } from './types';

// Import from parser types  
import { ResolvedIFCommand } from '../parser/if-parser-types';

// Import from world model types
import { GameContext } from '../world-model/types';

// Import from Core through controlled boundary
import { SemanticEvent, createEvent } from '../core-imports';

// Import IF-specific constants from local constants
import { IFActions } from '../constants/if-actions';
import { IFEvents } from '../constants/if-events';
import { IFAttributes } from '../constants/if-attributes';

/**
 * Dropping action definition
 * 
 * Handles "drop [object]" commands
 */
export const droppingAction: ActionDefinition = {
  id: IFActions.DROPPING,
  name: IFActions.DROPPING,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: true,
    undoable: true,
    category: 'manipulation'
  },
  phases: {
    // VALIDATE phase - Check if dropping is possible
    validate: (command: ResolvedIFCommand, context: GameContext): boolean | string => {
      // Must have a target
      if (!command.noun) {
        return context.languageProvider.formatMessage('action.dropping.no_target');
      }

      const target = command.noun;
      const targetId = target.id;

      // Can't drop yourself
      if (targetId === command.actor.id) {
        return context.languageProvider.formatMessage('action.dropping.cannot_drop_self');
      }

      // Check if held by the actor
      const heldLocation = context.world.getLocation(targetId);
      if (heldLocation !== command.actor.id) {
        return context.languageProvider.formatMessage('action.dropping.not_held', {
          item: target.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Check if droppable (default to true if not specified)
      const droppable = target.attributes.droppable !== false;
      if (!droppable) {
        return context.languageProvider.formatMessage('action.dropping.not_droppable', {
          item: target.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // All checks passed
      return true;
    },

    // EXECUTE phase - Actually drop the object
    execute: (command: ResolvedIFCommand, context: GameContext): SemanticEvent[] => {
      if (!command.noun) return [];

      const target = command.noun;
      const targetId = target.id;
      const events: SemanticEvent[] = [];

      // Move the item to the current location
      try {
        context.world.moveEntity(targetId, context.currentLocation.id);

        // Create success event
        events.push(createEvent(
          IFEvents.ITEM_DROPPED,
          { 
            itemId: targetId,
            itemName: target.attributes[IFAttributes.NAME],
            location: context.currentLocation.id,
            locationName: context.currentLocation.attributes[IFAttributes.NAME]
          },
          { 
            narrate: true,
            location: context.currentLocation.id
          }
        ));
      } catch (error) {
        // If the move fails, report the error
        events.push(createEvent(
          IFEvents.ACTION_FAILED,
          { 
            action: IFActions.DROPPING,
            reason: 'move_failed',
            message: error instanceof Error ? error.message : 'Failed to drop item'
          },
          { narrate: true }
        ));
      }

      return events;
    }
  }
};

/**
 * Opening action - Open a container or door
 */

// Import from local action types
import { ActionDefinition } from './types';

// Import from parser types  
import { ResolvedIFCommand } from '../parser/if-parser-types';

// Import from world model types
import { GameContext } from '../world-model/types';
import { IFEntity } from '../world-model/if-entities/types';

// Import from Core through controlled boundary
import { SemanticEvent, createEvent } from '../core-imports';

// Import IF-specific constants from local constants
import { IFActions } from '../constants/if-actions';
import { IFEvents } from '../constants/if-events';
import { IFEntityType } from '../constants/if-entity-types';
import { IFAttributes } from '../constants/if-attributes';

/**
 * Opening action definition
 * 
 * Handles "open [object]" commands
 */
export const openingAction: ActionDefinition = {
  id: IFActions.OPENING,
  name: IFActions.OPENING,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: true,
    undoable: true,
    category: 'manipulation'
  },
  phases: {
    // VALIDATE phase - Check if opening is possible
    validate: (command: ResolvedIFCommand, context: GameContext): boolean | string => {
      // Must have a target
      if (!command.noun) {
        return context.languageProvider.formatMessage('action.opening.no_target');
      }

      const target = command.noun;
      const targetId = target.id;

      // Check if it's openable
      const isOpenable = target.attributes.openable === true || 
                        target.type === IFEntityType.DOOR || 
                        target.type === IFEntityType.CONTAINER ||
                        target.attributes[IFAttributes.CONTAINER] === true;

      if (!isOpenable) {
        return context.languageProvider.formatMessage('action.opening.not_openable', {
          item: target.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Check if already open
      if (target.attributes[IFAttributes.OPEN] === true) {
        return context.languageProvider.formatMessage('action.opening.already_open', {
          item: target.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Check if locked
      if (target.attributes[IFAttributes.LOCKED] === true) {
        return context.languageProvider.formatMessage('action.opening.locked', {
          item: target.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Check if accessible
      if (!context.isAccessible(targetId)) {
        return context.languageProvider.formatMessage('action.opening.not_accessible', {
          item: target.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // All checks passed
      return true;
    },

    // EXECUTE phase - Actually open the object
    execute: (command: ResolvedIFCommand, context: GameContext): SemanticEvent[] => {
      if (!command.noun) return [];

      const target = command.noun;
      const targetId = target.id;
      const events: SemanticEvent[] = [];

      // Update the object's open state
      // Get fresh entity state to ensure we have complete attributes
      const currentEntity = context.world.getEntity(targetId) as IFEntity;
      if (currentEntity) {
        context.world.updateEntity(targetId, {
          attributes: {
            ...currentEntity.attributes,
            [IFAttributes.OPEN]: true
          }
        });
      }

      // Determine what type of thing was opened
      const eventType = target.type === IFEntityType.DOOR ? 
        IFEvents.DOOR_OPENED : IFEvents.CONTAINER_OPENED;

      // Create success event
      events.push(createEvent(
        eventType,
        { 
          itemId: targetId,
          itemName: target.attributes[IFAttributes.NAME],
          itemType: target.type
        },
        { 
          narrate: true,
          location: context.currentLocation.id
        }
      ));

      // If it's a container with contents, check what's inside
      if ((target.type === IFEntityType.CONTAINER || target.attributes[IFAttributes.CONTAINER])) {
        const contents = context.world.getContents(targetId);

        if (contents.length > 0) {
          // Create an event about revealing contents
          events.push(createEvent(
            IFEvents.HIDDEN_ITEM_REVEALED,
            { 
              containerId: targetId,
              containerName: target.attributes[IFAttributes.NAME],
              items: contents.map(item => ({
                id: item.id,
                name: item.attributes[IFAttributes.NAME]
              }))
            },
            { 
              narrate: true,
              location: context.currentLocation.id
            }
          ));
        }
      }

      return events;
    }
  }
};

/**
 * Closing action - Close a container or door
 */

// Import from local action types
import { ActionDefinition } from './types';

// Import from parser types  
import { ResolvedIFCommand } from '../parser/if-parser-types';

// Import from world model types
import { GameContext } from '../world-model/types';
import { IFEntity } from '../world-model/if-entities/types';

// Import from Core through controlled boundary
import { SemanticEvent, createEvent } from '../core-imports';

// Import IF-specific constants from local constants
import { IFActions } from '../constants/if-actions';
import { IFEvents } from '../constants/if-events';
import { IFEntityType } from '../constants/if-entity-types';
import { IFAttributes } from '../constants/if-attributes';

/**
 * Closing action definition
 * 
 * Handles "close [object]" commands
 */
export const closingAction: ActionDefinition = {
  id: IFActions.CLOSING,
  name: IFActions.CLOSING,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: true,
    undoable: true,
    category: 'manipulation'
  },
  phases: {
    // VALIDATE phase - Check if closing is possible
    validate: (command: ResolvedIFCommand, context: GameContext): boolean | string => {
      // Must have a target
      if (!command.noun) {
        return context.languageProvider.formatMessage('action.closing.no_target');
      }

      const target = command.noun;
      const targetId = target.id;

      // Check if it's openable (and thus closeable)
      const isOpenable = target.attributes.openable === true || 
                        target.type === IFEntityType.DOOR || 
                        target.type === IFEntityType.CONTAINER ||
                        target.attributes[IFAttributes.CONTAINER] === true;

      if (!isOpenable) {
        return context.languageProvider.formatMessage('action.closing.not_closeable', {
          item: target.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Check if already closed
      if (target.attributes[IFAttributes.OPEN] !== true) {
        return context.languageProvider.formatMessage('action.closing.already_closed', {
          item: target.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Check if accessible
      if (!context.isAccessible(targetId)) {
        return context.languageProvider.formatMessage('action.closing.not_accessible', {
          item: target.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // All checks passed
      return true;
    },

    // EXECUTE phase - Actually close the object
    execute: (command: ResolvedIFCommand, context: GameContext): SemanticEvent[] => {
      if (!command.noun) return [];

      const target = command.noun;
      const targetId = target.id;
      const events: SemanticEvent[] = [];

      // Update the object's open state
      // Get fresh entity state to ensure we have complete attributes
      const currentEntity = context.world.getEntity(targetId) as IFEntity;
      if (currentEntity) {
        context.world.updateEntity(targetId, {
          attributes: {
            ...currentEntity.attributes,
            [IFAttributes.OPEN]: false
          }
        });
      }

      // Determine what type of thing was closed
      const eventType = target.type === IFEntityType.DOOR ? 
        IFEvents.DOOR_CLOSED : IFEvents.CONTAINER_CLOSED;

      // Create success event
      events.push(createEvent(
        eventType,
        { 
          itemId: targetId,
          itemName: target.attributes[IFAttributes.NAME],
          itemType: target.type
        },
        { 
          narrate: true,
          location: context.currentLocation.id
        }
      ));

      return events;
    }
  }
};

