/**
 * Implementation of ActionContext for trait-based action system
 */

import { ActionContext } from './types/action-context';
import { IFEntity } from '../world-model/traits/if-entity';
import { WorldModelService } from '../world-model/services/world-model-service';
import { ScopeService } from '../world-model/services/scope-service';
import { IFLanguageProvider } from '../language/if-language-provider';
import { TraitType } from '../world-model/traits/trait-types';
import { IdentityTrait } from '../world-model/traits/standard/identity';

/**
 * Default implementation of ActionContext
 */
export class ActionContextImpl implements ActionContext {
  public readonly world: WorldModelService;
  public readonly player: IFEntity;
  public readonly currentLocation: IFEntity;
  public readonly scope: ScopeService;
  public readonly language: IFLanguageProvider;

  constructor(params: {
    world: WorldModelService;
    player: IFEntity;
    language: IFLanguageProvider;
  }) {
    this.world = params.world;
    this.player = params.player;
    this.language = params.language;

    // Create scope service
    this.scope = new ScopeService(this.world);

    // Get current location
    const locationId = this.world.getLocation(this.player.id);
    if (!locationId) {
      throw new Error('Player has no location');
    }

    const location = this.world.getEntity(locationId);
    if (!location) {
      throw new Error(`Location ${locationId} not found`);
    }

    this.currentLocation = location;
  }

  /**
   * Check if the player can see an entity
   */
  canSee(entity: IFEntity): boolean {
    return this.scope.isVisibleFrom(entity.id, this.player.id);
  }

  /**
   * Check if the player can physically reach an entity
   * 
   * An entity is reachable if:
   * - It's visible
   * - It's in the same location as the player, or
   * - It's inside an open container that the player can reach, or
   * - It's on a supporter that the player can reach
   */
  canReach(entity: IFEntity): boolean {
    // Must be visible first
    if (!this.canSee(entity)) {
      return false;
    }

    return this.scope.isReachableFrom(entity.id, this.player.id);
  }

  /**
   * Check if the player can take an entity
   * 
   * An entity can be taken if:
   * - It's reachable
   * - It has the PORTABLE trait
   * - It doesn't have the FIXED trait
   * - It's not already held by the player
   */
  canTake(entity: IFEntity): boolean {
    // Must be reachable
    if (!this.canReach(entity)) {
      return false;
    }

    // Must be portable
    if (!entity.has(TraitType.PORTABLE)) {
      return false;
    }

    // Must not be fixed
    if (entity.has(TraitType.FIXED)) {
      return false;
    }

    // Check if already held
    const entityLocation = this.world.getLocation(entity.id);
    if (entityLocation === this.player.id) {
      return false; // Already held
    }

    return true;
  }

  /**
   * Get the display name of an entity
   */
  getName(entity: IFEntity): string {
    const identity = entity.get<IdentityTrait>(TraitType.IDENTITY);
    return identity?.name || entity.id;
  }

  /**
   * Get the description of an entity
   */
  getDescription(entity: IFEntity): string | undefined {
    const identity = entity.get<IdentityTrait>(TraitType.IDENTITY);
    return identity?.description;
  }

  /**
   * Get the current turn number
   */
  getTurnNumber(): number {
    // TODO: Implement turn tracking in WorldModelService
    return 0;
  }
}

/**
 * Factory function to create an ActionContext
 */
export function createActionContext(params: {
  world: WorldModelService;
  player: IFEntity;
  language: IFLanguageProvider;
}): ActionContext {
  return new ActionContextImpl(params);
}

/**
 * Action context interface for trait-based action system
 * 
 * Provides a clean interface for actions to interact with the world model
 * using traits instead of attributes.
 */

import { IFEntity } from '../../world-model/traits/if-entity';
import { WorldModelService } from '../../world-model/services/world-model-service';
import { ScopeService } from '../../world-model/services/scope-service';
import { IFLanguageProvider } from '../../language/if-language-provider';


/**
 * Context provided to actions during execution
 * 
 * This replaces GameContext with a trait-aware interface
 */
export interface ActionContext {
  /**
   * The world model service for entity operations
   */
  world: WorldModelService;

  /**
   * The player entity
   */
  player: IFEntity;

  /**
   * The current location entity
   */
  currentLocation: IFEntity;

  /**
   * Service for scope calculations
   */
  scope: ScopeService;

  /**
   * Language provider for message formatting
   */
  language: IFLanguageProvider;

  // Helper methods for common operations

  /**
   * Check if the player can see an entity
   */
  canSee(entity: IFEntity): boolean;

  /**
   * Check if the player can physically reach an entity
   */
  canReach(entity: IFEntity): boolean;

  /**
   * Check if the player can take an entity
   * (visible, reachable, portable, not fixed, etc.)
   */
  canTake(entity: IFEntity): boolean;

  // Trait-aware helpers

  /**
   * Get the display name of an entity
   * Uses the IdentityTrait if available, falls back to entity ID
   */
  getName(entity: IFEntity): string;

  /**
   * Get the description of an entity
   * Uses the IdentityTrait if available
   */
  getDescription(entity: IFEntity): string | undefined;

  /**
   * Get the current turn number
   */
  getTurnNumber(): number;
}

/**
 * Closing action executor
 * 
 * Handles the logic for closing containers, doors, and other openable objects
 */

import { ActionExecutor, ParsedCommand } from '../../actions/types/command-types';
import { ActionContext } from '../action-context';
import { IFActions } from '../../constants/if-actions';
import { IFEvents } from '../../constants/if-events';
import { ActionFailureReason } from '../../constants/action-failure-reason';
import { TraitType } from '../../world-model/traits/trait-types';
import { createEvent, Event } from '../../core-imports';
import { OpenableBehavior } from '../../world-model/traits/openable/openableBehavior';

/**
 * Executor for the closing action
 */
export const closingAction: ActionExecutor = {
  id: IFActions.CLOSING,

  execute(command: ParsedCommand, context: ActionContext): Event[] {
    const { actor, noun } = command;

    // Validate we have a target
    if (!noun) {
      return [createEvent(IFEvents.ACTION_FAILED, {
        action: IFActions.CLOSING,
        reason: ActionFailureReason.INVALID_TARGET
      })];
    }

    // Check if openable
    if (!noun.has(TraitType.OPENABLE)) {
      return [createEvent(IFEvents.ACTION_FAILED, {
        action: IFActions.CLOSING,
        reason: ActionFailureReason.NOT_OPENABLE,
        target: noun.id
      })];
    }

    const openableBehavior = new OpenableBehavior();

    // Check if already closed
    if (!openableBehavior.isOpen(noun)) {
      return [createEvent(IFEvents.ACTION_FAILED, {
        action: IFActions.CLOSING,
        reason: ActionFailureReason.ALREADY_CLOSED,
        target: noun.id
      })];
    }

    // Check if reachable
    if (!context.world.canReach(actor, noun)) {
      return [createEvent(IFEvents.ACTION_FAILED, {
        action: IFActions.CLOSING,
        reason: ActionFailureReason.NOT_REACHABLE,
        target: noun.id
      })];
    }

    // Actually close it
    openableBehavior.close(noun);

    // Build event data
    const eventData: Record<string, unknown> = {
      target: noun.id,
      actor: actor.id,
    };

    // Add specific data based on what was closed
    if (noun.has(TraitType.DOOR)) {
      eventData.isDoor = true;
    } else if (noun.has(TraitType.CONTAINER)) {
      eventData.isContainer = true;
    }

    // Create the success event
    return [createEvent(IFEvents.CLOSED, eventData)];
  }
};

