import { IFActions } from '../../constants/if-actions';
import { IFEntity } from '../../world-model/if-entities/types';
import { Event } from '../../../core-imports';
import { ActionContext } from '../action-context';

/**
 * Definition for a command pattern that maps to an action.
 * This is used by the parser to identify commands and their requirements.
 */
export interface CommandDefinition {
  /** The primary verb ID used in the language file */
  verbId: string;

  /** The action this command maps to */
  mapsToAction: IFActions;

  /** Whether this command requires a direct object (noun) */
  requiresNoun?: boolean;

  /** Whether this command can have an indirect object */
  allowsIndirectObject?: boolean;

  /** Whether this command requires an indirect object */
  requiresIndirectObject?: boolean;

  /** Custom validation for the command pattern */
  validate?: (context: ParseContext) => boolean;
}

/**
 * Context provided to command validators
 */
export interface ParseContext {
  /** The matched verb */
  verb: string;

  /** The direct object if any */
  noun?: string;

  /** The indirect object if any */
  indirectObject?: string;

  /** Preposition used with indirect object */
  preposition?: string;

  /** Raw input from user */
  rawInput: string;
}

/**
 * Parsed command ready for execution
 */
export interface ParsedCommand {
  /** The action to execute */
  action: IFActions;

  /** The actor performing the action */
  actor: IFEntity;

  /** The direct object of the action */
  noun?: IFEntity;

  /** The indirect object of the action */
  indirectObject?: IFEntity;

  /** Any preposition used */
  preposition?: string;

  /** The original parse context */
  parseContext: ParseContext;
}

/**
 * Executor for an action.
 * This contains the actual logic for performing the action.
 */
export interface ActionExecutor {
  /** The action ID this executor handles */
  id: IFActions;

  /** Execute the action and return events */
  execute(command: ParsedCommand, context: ActionContext): Event[];

  /** Optional pre-execution validation */
  validate?: (command: ParsedCommand, context: ActionContext) => boolean;
}

/**
 * Registry entry combining command definition and executor
 */
export interface ActionRegistryEntry {
  /** Command patterns that trigger this action */
  commands: CommandDefinition[];

  /** The executor for this action */
  executor: ActionExecutor;
}

// packages/core/src/events/types.ts

import { EntityId } from '../types/entity';

/**
 * Represents a semantic event in the system
 */
export interface SemanticEvent {
  /**
   * Unique identifier for this event
   */
  id: string;

  /**
   * The type of event
   */
  type: string;

  /**
   * Timestamp when the event was created
   */
  timestamp: number;

  /**
   * Entity IDs relevant to this event
   */
  entities: {
    /**
     * The entity that initiated the event (often the player)
     */
    actor?: EntityId;

    /**
     * The primary entity that the event affects
     */
    target?: EntityId;

    /**
     * A secondary entity involved in the event
     */
    instrument?: EntityId;

    /**
     * A location where the event occurred
     */
    location?: EntityId;

    /**
     * Other relevant entities
     */
    others?: EntityId[];
  };

  /**
   * Additional data related to the event
   */
  payload?: Record<string, unknown>;

  /**
   * Tags for categorizing and filtering events
   */
  tags?: string[];

  /**
   * Priority of the event (higher numbers are more important)
   */
  priority?: number;

  /**
   * Whether this event should be narrated
   */
  narrate?: boolean;

  /**
   * Legacy support for data property (same as payload)
   */
  data?: Record<string, unknown>;

  /**
   * Legacy support for metadata property
   */
  metadata?: Record<string, unknown>;
}

/**
 * Interface for event source that collects and manages events
 */
export interface EventSource {
  /**
   * Add an event to the source
   */
  addEvent: (event: SemanticEvent) => void;

  /**
   * Get all events in the source
   */
  getAllEvents: () => SemanticEvent[];

  /**
   * Get events of a specific type
   */
  getEventsByType: (type: string) => SemanticEvent[];

  /**
   * Get events involving a specific entity
   */
  getEventsByEntity: (entityId: EntityId) => SemanticEvent[];

  /**
   * Get events with a specific tag
   */
  getEventsByTag: (tag: string) => SemanticEvent[];

  /**
   * Clear all events
   */
  clearEvents: () => void;

  /**
   * Apply a filter to the events
   */
  filter: (predicate: (event: SemanticEvent) => boolean) => SemanticEvent[];

  /**
   * Get the event emitter associated with this source
   */
  getEmitter: () => EventEmitter;
}

/**
 * Event listener for semantic events
 */
export type EventListener = (event: SemanticEvent) => void;

/**
 * Event emitter for semantic events
 */
export interface EventEmitter {
  /**
   * Add an event listener for a specific event type
   * Use '*' to listen to all events
   */
  on: (type: string, listener: EventListener) => void;

  /**
   * Remove an event listener
   */
  off: (type: string, listener: EventListener) => void;

  /**
   * Emit an event
   */
  emit: (event: SemanticEvent) => void;
}

/**
 * Configuration options for the event system
 */
export interface EventSystemOptions {
  /**
   * Maximum number of events to store in memory
   */
  maxEvents?: number;

  /**
   * Whether to emit events immediately when added
   */
  emitOnAdd?: boolean;

  /**
   * Custom filter for events that should be emitted
   */
  emitFilter?: (event: SemanticEvent) => boolean;
}

