// packages/stdlib/src/world-model/traits/openable/openableBehavior.ts

import { Behavior } from '../../behaviors/behavior';
import { IFEntity } from '../../if-entities/types';
import { TraitType } from '../trait-types';
import { OpenableTrait } from './openableTrait';
import { Event } from '../../../../core-imports';
import { IFEvents } from '../../../constants/if-events';
import { ActionFailureReason } from '../../../constants/action-failure-reason';

/**
 * Behavior for openable entities.
 * 
 * Handles the logic for opening and closing entities.
 */
export class OpenableBehavior extends Behavior {
  static requiredTraits = [TraitType.OPENABLE];

  /**
   * Check if an entity can be opened
   */
  canOpen(entity: IFEntity): boolean {
    const openable = this.require<OpenableTrait>(entity, TraitType.OPENABLE);
    return !openable.isOpen;
  }

  /**
   * Check if an entity can be closed
   */
  canClose(entity: IFEntity): boolean {
    const openable = this.require<OpenableTrait>(entity, TraitType.OPENABLE);
    return openable.isOpen && openable.canClose;
  }

  /**
   * Open the entity
   * @returns Events describing what happened
   */
  open(entity: IFEntity, actor: IFEntity): Event[] {
    const openable = this.require<OpenableTrait>(entity, TraitType.OPENABLE);

    if (openable.isOpen) {
      return [{
        type: IFEvents.ACTION_FAILED,
        entityId: entity.id,
        data: {
          action: 'open',
          reason: ActionFailureReason.ALREADY_OPEN,
          customMessage: openable.alreadyOpenMessage
        }
      }];
    }

    // Open it
    openable.isOpen = true;

    return [{
      type: IFEvents.OPENED,
      entityId: entity.id,
      data: {
        actor: actor.id,
        customMessage: openable.openMessage,
        sound: openable.openSound,
        revealsContents: openable.revealsContents
      }
    }];
  }

  /**
   * Close the entity
   * @returns Events describing what happened
   */
  close(entity: IFEntity, actor: IFEntity): Event[] {
    const openable = this.require<OpenableTrait>(entity, TraitType.OPENABLE);

    if (!openable.isOpen) {
      return [{
        type: IFEvents.ACTION_FAILED,
        entityId: entity.id,
        data: {
          action: 'close',
          reason: ActionFailureReason.ALREADY_CLOSED,
          customMessage: openable.alreadyClosedMessage
        }
      }];
    }

    if (!openable.canClose) {
      return [{
        type: IFEvents.ACTION_FAILED,
        entityId: entity.id,
        data: {
          action: 'close',
          reason: ActionFailureReason.CANT_DO_THAT,
          customMessage: "Once opened, it can't be closed."
        }
      }];
    }

    // Close it
    openable.isOpen = false;

    return [{
      type: IFEvents.CLOSED,
      entityId: entity.id,
      data: {
        actor: actor.id,
        customMessage: openable.closeMessage,
        sound: openable.closeSound
      }
    }];
  }

  /**
   * Toggle open/closed state
   * @returns Events from either open or close
   */
  toggle(entity: IFEntity, actor: IFEntity): Event[] {
    const openable = this.require<OpenableTrait>(entity, TraitType.OPENABLE);

    if (openable.isOpen) {
      return this.close(entity, actor);
    } else {
      return this.open(entity, actor);
    }
  }

  /**
   * Check if entity is currently open
   */
  isOpen(entity: IFEntity): boolean {
    const openable = this.require<OpenableTrait>(entity, TraitType.OPENABLE);
    return openable.isOpen;
  }

  /**
   * Check if opening reveals contents
   */
  revealsContents(entity: IFEntity): boolean {
    const openable = this.require<OpenableTrait>(entity, TraitType.OPENABLE);
    return openable.revealsContents;
  }
}

