// packages/core/src/events/index.ts

/**
 * Core event system
 * 
 * Provides a generic event bus and text processing system
 * without knowledge of specific event types
 */

export * from './types';
export * from './event-system';
export * from './text-processor';
export * from './standard-events';
// message-resolver moved to stdlib (uses GameContext)
// enhanced-text-processor moved to stdlib (uses GameContext) 
// message-builder moved to stdlib (uses GameContext)

// packages/core/src/events/event-system.ts

import { SemanticEvent, EventSource, EventEmitter, EventListener } from './types';
import { EntityId } from '../types/entity';

/**
 * Create a new semantic event
 */
export function createEvent(
  type: string,
  payload?: Record<string, unknown>,
  options: {
    actor?: EntityId;
    target?: EntityId;
    instrument?: EntityId;
    location?: EntityId;
    others?: EntityId[];
    tags?: string[];
    priority?: number;
    narrate?: boolean;
  } = {}
): SemanticEvent {
  return {
    id: generateEventId(),
    type,
    timestamp: Date.now(),
    entities: {
      actor: options.actor,
      target: options.target,
      instrument: options.instrument,
      location: options.location,
      others: options.others
    },
    payload,
    tags: options.tags || [],
    priority: options.priority ?? 0,
    narrate: options.narrate ?? true
  };
}

/**
 * Implementation of the EventSource interface
 */
export class EventSourceImpl implements EventSource {
  private events: SemanticEvent[] = [];
  private emitter: EventEmitterImpl;
  private lastProcessedIndex: number = 0;

  constructor() {
    this.emitter = new EventEmitterImpl();
  }

  /**
   * Add an event to the source
   */
  public addEvent(event: SemanticEvent): void {
    this.events.push(event);
    this.emitter.emit(event);
  }

  /**
   * Get all events in the source
   */
  public getAllEvents(): SemanticEvent[] {
    return [...this.events];
  }

  /**
   * Get events since a specific event ID
   */
  public getEventsSince(eventId?: string): SemanticEvent[] {
    if (!eventId) {
      return this.getAllEvents();
    }

    const index = this.events.findIndex(e => e.id === eventId);
    if (index === -1) {
      return this.getAllEvents();
    }

    return this.events.slice(index + 1);
  }

  /**
   * Get unprocessed events and mark them as processed
   */
  public getUnprocessedEvents(): SemanticEvent[] {
    const unprocessed = this.events.slice(this.lastProcessedIndex);
    this.lastProcessedIndex = this.events.length;
    return unprocessed;
  }

  /**
   * Get events of a specific type
   */
  public getEventsByType(type: string): SemanticEvent[] {
    return this.events.filter(event => event.type === type);
  }

  /**
   * Get events involving a specific entity
   */
  public getEventsByEntity(entityId: EntityId): SemanticEvent[] {
    return this.events.filter(event => {
      const entities = event.entities;
      return (
        entities.actor === entityId ||
        entities.target === entityId ||
        entities.instrument === entityId ||
        entities.location === entityId ||
        (entities.others && entities.others.includes(entityId))
      );
    });
  }

  /**
   * Get events with a specific tag
   */
  public getEventsByTag(tag: string): SemanticEvent[] {
    return this.events.filter(event => event.tags && event.tags.includes(tag));
  }

  /**
   * Apply a filter to the events
   */
  public filter(predicate: (event: SemanticEvent) => boolean): SemanticEvent[] {
    return this.events.filter(predicate);
  }

  /**
   * Clear all events
   */
  public clearEvents(): void {
    this.events = [];
    this.lastProcessedIndex = 0;
  }

  /**
   * Get the event emitter
   */
  public getEmitter(): EventEmitter {
    return this.emitter;
  }
}

/**
 * Implementation of the EventEmitter interface
 */
export class EventEmitterImpl implements EventEmitter {
  private listeners: Map<string, Set<EventListener>> = new Map();
  private globalListeners: Set<EventListener> = new Set();

  public on(type: string, listener: EventListener): void {
    if (type === '*') {
      this.globalListeners.add(listener);
      return;
    }

    if (!this.listeners.has(type)) {
      this.listeners.set(type, new Set());
    }
    this.listeners.get(type)!.add(listener);
  }

  public off(type: string, listener: EventListener): void {
    if (type === '*') {
      this.globalListeners.delete(listener);
      return;
    }

    const typeListeners = this.listeners.get(type);
    if (typeListeners) {
      typeListeners.delete(listener);
      if (typeListeners.size === 0) {
        this.listeners.delete(type);
      }
    }
  }

  public emit(event: SemanticEvent): void {
    const typeListeners = this.listeners.get(event.type);
    if (typeListeners) {
      for (const listener of typeListeners) {
        try {
          listener(event);
        } catch (error) {
          console.error(`Error in event listener for ${event.type}:`, error);
        }
      }
    }

    for (const listener of this.globalListeners) {
      try {
        listener(event);
      } catch (error) {
        console.error(`Error in global event listener for ${event.type}:`, error);
      }
    }
  }
}

/**
 * Generate a unique ID for an event
 */
function generateEventId(): string {
  return `event_${Date.now()}_${Math.random().toString(36).substring(2, 9)}`;
}

/**
 * Create a new event source
 */
export function createEventSource(): EventSource {
  return new EventSourceImpl();
}

/**
 * Create a new event emitter
 */
export function createEventEmitter(): EventEmitter {
  return new EventEmitterImpl();
}

/**
 * Closing action executor
 * 
 * Handles the logic for closing containers, doors, and other openable objects
 */

import { ActionExecutor, ParsedCommand } from '../../actions/types/command-types';
import { ActionContext } from '../../actions/types/action-context';
import { IFActions } from '../../constants/if-actions';
import { IFEvents } from '../../constants/if-events';
import { ActionFailureReason } from '../../constants/action-failure-reason';
import { TraitType } from '../../world-model/traits/trait-types';
import { createEvent, SemanticEvent } from '../../core-imports';
import { OpenableBehavior } from '../../world-model/traits/openable/openableBehavior';

/**
 * Executor for the closing action
 */
export const closingAction: ActionExecutor = {
  id: IFActions.CLOSING,

  execute(command: ParsedCommand, context: ActionContext): SemanticEvent[] {
    const { actor, noun } = command;

    // Validate we have a target
    if (!noun) {
      return [createEvent(IFEvents.ACTION_FAILED, {
        action: IFActions.CLOSING,
        reason: ActionFailureReason.INVALID_TARGET
      })];
    }

    // Check if openable
    if (!noun.has(TraitType.OPENABLE)) {
      return [createEvent(IFEvents.ACTION_FAILED, {
        action: IFActions.CLOSING,
        reason: ActionFailureReason.NOT_OPENABLE,
        target: noun.id
      })];
    }

    const openableBehavior = new OpenableBehavior();

    // Check if already closed
    if (!openableBehavior.isOpen(noun)) {
      return [createEvent(IFEvents.ACTION_FAILED, {
        action: IFActions.CLOSING,
        reason: ActionFailureReason.ALREADY_CLOSED,
        target: noun.id
      })];
    }

    // Check if reachable
    if (!context.canReach(noun)) {
      return [createEvent(IFEvents.ACTION_FAILED, {
        action: IFActions.CLOSING,
        reason: ActionFailureReason.NOT_REACHABLE,
        target: noun.id
      })];
    }

    // Actually close it
    openableBehavior.close(noun, actor);

    // Build event data
    const eventData: Record<string, unknown> = {
      target: noun.id,
      actor: actor.id,
    };

    // Add specific data based on what was closed
    if (noun.has(TraitType.DOOR)) {
      eventData.isDoor = true;
    } else if (noun.has(TraitType.CONTAINER)) {
      eventData.isContainer = true;
    }

    // Create the success event
    return [createEvent(IFEvents.CLOSED, eventData)];
  }
};

