/**
 * Opening action executor
 * 
 * Handles the logic for opening containers, doors, and other openable objects
 */

import { ActionExecutor, ParsedCommand } from '../../actions/types/command-types';
import { ActionContext } from '../../actions/types/action-context';
import { IFActions } from '../../constants/if-actions';
import { IFEvents } from '../../constants/if-events';
import { ActionFailureReason } from '../../constants/action-failure-reason';
import { TraitType } from '../../world-model/traits/trait-types';
import { createEvent, SemanticEvent } from '../../core-imports';
import { OpenableBehavior } from '../../world-model/traits/openable/openableBehavior';
import { LockableBehavior } from '../../world-model/traits/lockable/lockableBehavior';
import { DoorBehavior } from '../../world-model/traits/door/doorBehavior';

/**
 * Executor for the opening action
 */
export const openingAction: ActionExecutor = {
  id: IFActions.OPENING,

  execute(command: ParsedCommand, context: ActionContext): SemanticEvent[] {
    const { actor, noun } = command;

    // Validate we have a target
    if (!noun) {
      return [createEvent(IFEvents.ACTION_FAILED, {
        action: IFActions.OPENING,
        reason: ActionFailureReason.INVALID_TARGET
      })];
    }

    // Check if openable
    if (!noun.has(TraitType.OPENABLE)) {
      return [createEvent(IFEvents.ACTION_FAILED, {
        action: IFActions.OPENING,
        reason: ActionFailureReason.NOT_OPENABLE,
        target: noun.id
      })];
    }

    const openableBehavior = new OpenableBehavior();

    // Check if already open
    if (openableBehavior.isOpen(noun)) {
      return [createEvent(IFEvents.ACTION_FAILED, {
        action: IFActions.OPENING,
        reason: ActionFailureReason.ALREADY_OPEN,
        target: noun.id
      })];
    }

    // Check if locked
    if (noun.has(TraitType.LOCKABLE)) {
      const lockableBehavior = new LockableBehavior();
      if (lockableBehavior.isLocked(noun)) {
        return [createEvent(IFEvents.ACTION_FAILED, {
          action: IFActions.OPENING,
          reason: ActionFailureReason.LOCKED,
          target: noun.id
        })];
      }
    }

    // Check if reachable
    if (!context.world.canReach(actor, noun)) {
      return [createEvent(IFEvents.ACTION_FAILED, {
        action: IFActions.OPENING,
        reason: ActionFailureReason.NOT_REACHABLE,
        target: noun.id
      })];
    }

    // Actually open it
    openableBehavior.open(noun);

    // Build event data
    const eventData: Record<string, unknown> = {
      target: noun.id,
      actor: actor.id,
    };

    // Add specific data based on what was opened
    if (noun.has(TraitType.DOOR)) {
      eventData.isDoor = true;

      // Get connected room information
      const doorBehavior = new DoorBehavior();
      const currentRoom = context.world.getLocation(actor.id);
      if (currentRoom) {
        const otherRoom = doorBehavior.getOtherRoom(noun, currentRoom);
        if (otherRoom) {
          eventData.connectsTo = otherRoom;
        }
      }
    } else if (noun.has(TraitType.CONTAINER)) {
      eventData.isContainer = true;

      // Add contents information
      const contents = context.world.getContents(noun.id);
      eventData.contents = contents.map(item => item.id);
      eventData.isEmpty = contents.length === 0;
    }

    // Create the success event
    return [createEvent(IFEvents.OPENED, eventData)];
  }
};

