/**
 * Unlocking action executor
 * 
 * Handles unlocking containers, doors, and other lockable objects
 */

import { ActionExecutor, ParsedCommand } from '../../actions/types/command-types';
import { ActionContext } from '../../actions/types/action-context';
import { IFActions } from '../../constants/if-actions';
import { IFEvents } from '../../constants/if-events';
import { ActionFailureReason } from '../../constants/action-failure-reason';
import { TraitType } from '../../world-model/traits/trait-types';
import { createEvent, SemanticEvent } from '../../core-imports';
import { LockableBehavior } from '../../world-model/traits/lockable/lockableBehavior';
import { DoorBehavior } from '../../world-model/traits/door/doorBehavior';

/**
 * Executor for the unlocking action
 */
export const unlockingAction: ActionExecutor = {
  id: IFActions.UNLOCKING,

  execute(command: ParsedCommand, context: ActionContext): SemanticEvent[] {
    const { actor, noun, indirectObject } = command;

    // Validate we have a target
    if (!noun) {
      return [createEvent(IFEvents.ACTION_FAILED, {
        action: IFActions.UNLOCKING,
        reason: ActionFailureReason.INVALID_TARGET
      })];
    }

    // Check if lockable
    if (!noun.has(TraitType.LOCKABLE)) {
      return [createEvent(IFEvents.ACTION_FAILED, {
        action: IFActions.UNLOCKING,
        reason: ActionFailureReason.NOT_LOCKABLE,
        target: noun.id
      })];
    }

    const lockableBehavior = new LockableBehavior();

    // Check if already unlocked
    if (!lockableBehavior.isLocked(noun)) {
      return [createEvent(IFEvents.ACTION_FAILED, {
        action: IFActions.UNLOCKING,
        reason: ActionFailureReason.ALREADY_UNLOCKED,
        target: noun.id
      })];
    }

    // Check if reachable
    if (!context.world.canReach(actor, noun)) {
      return [createEvent(IFEvents.ACTION_FAILED, {
        action: IFActions.UNLOCKING,
        reason: ActionFailureReason.NOT_REACHABLE,
        target: noun.id
      })];
    }

    // Handle key requirement
    const requiredKeyIds = lockableBehavior.getRequiredKeys(noun);
    let keyUsed = indirectObject;

    if (requiredKeyIds.length > 0) {
      // Key required
      if (!keyUsed) {
        // Try to find a valid key in inventory
        const inventory = context.world.getContents(actor.id);
        keyUsed = inventory.find(item => 
          requiredKeyIds.includes(item.id)
        );

        if (!keyUsed) {
          return [createEvent(IFEvents.ACTION_FAILED, {
            action: IFActions.UNLOCKING,
            reason: ActionFailureReason.NO_KEY_SPECIFIED,
            target: noun.id
          })];
        }
      }

      // Verify it's the correct key
      if (!lockableBehavior.canUnlockWith(noun, keyUsed)) {
        return [createEvent(IFEvents.ACTION_FAILED, {
          action: IFActions.UNLOCKING,
          reason: ActionFailureReason.WRONG_KEY,
          target: noun.id,
          key: keyUsed.id
        })];
      }

      // Check if actor has the key
      const keyLocation = context.world.getLocation(keyUsed.id);
      if (keyLocation !== actor.id) {
        return [createEvent(IFEvents.ACTION_FAILED, {
          action: IFActions.UNLOCKING,
          reason: ActionFailureReason.NOT_IN_CONTAINER,
          target: keyUsed.id,
          expectedContainer: actor.id
        })];
      }
    }

    // Actually unlock it
    lockableBehavior.unlock(noun, keyUsed);

    // Build event data
    const eventData: Record<string, unknown> = {
      target: noun.id,
      actor: actor.id,
    };

    if (keyUsed) {
      eventData.key = keyUsed.id;
      eventData.usedKey = true;
    }

    // Add specific data based on what was unlocked
    if (noun.has(TraitType.DOOR)) {
      eventData.isDoor = true;

      // Get connected room information if possible
      const doorBehavior = new DoorBehavior();
      const currentRoom = context.world.getLocation(actor.id);
      if (currentRoom) {
        const otherRoom = doorBehavior.getOtherRoom(noun, currentRoom);
        if (otherRoom) {
          eventData.connectsTo = otherRoom;
        }
      }
    } else if (noun.has(TraitType.CONTAINER)) {
      eventData.isContainer = true;
    }

    // Create the success event
    const eventType = noun.has(TraitType.DOOR) ? IFEvents.DOOR_UNLOCKED : IFEvents.CONTAINER_UNLOCKED;
    return [createEvent(eventType, eventData)];
  }
};

