// packages/stdlib/src/world-model/traits/register-all.ts

import { registerInteractiveTraits } from './interactive/register';
import { registerAdvancedTraits } from './advanced/register';

/**
 * Register all built-in traits with the trait registry.
 * This should be called during application initialization.
 * 
 * Note: Standard traits have been reorganized into their own folders
 * and are now registered through the implementations.ts file.
 */
export function registerAllTraits(): void {
  // Standard traits are now in their own folders and don't need separate registration
  // They are available through the TRAIT_IMPLEMENTATIONS map

  registerInteractiveTraits();
  registerAdvancedTraits();
}

