// packages/stdlib/src/world-model/traits/interactive/readable.ts

import { ValidatedTrait, validators } from '../validation';

/**
 * Readable trait for entities that have text to read.
 * Used for books, signs, notes, inscriptions, etc.
 */
export class ReadableTrait extends ValidatedTrait {
  static readonly type = 'readable';
  readonly type = 'readable';

  /** The main text content */
  text: string = '';

  /** Short preview shown in descriptions */
  preview?: string;

  /** Whether the text is currently visible */
  isReadable: boolean = true;

  /** Language of the text (for multi-language games) */
  language: string = 'common';

  /** Whether reading requires a specific skill or item */
  requiresAbility: boolean = false;

  /** Ability/item needed to read (if requiresAbility) */
  requiredAbility?: string;

  /** Message when unable to read */
  cannotReadMessage?: string;

  /** Whether this has been read by the player */
  hasBeenRead: boolean = false;

  /** Type of readable (book, sign, note, inscription, etc.) */
  readableType: string = 'text';

  /** Number of pages (for books) */
  pages?: number;

  /** Current page (for multi-page items) */
  currentPage?: number;

  /** Content per page (for multi-page items) */
  pageContent?: string[];

  constructor(data?: Partial<ReadableTrait>) {
    super();
    if (data) {
      Object.assign(this, data);
    }

    // Initialize current page for multi-page items
    if (this.pageContent && this.pageContent.length > 0 && !this.currentPage) {
      this.currentPage = 1;
    }
  }

  validate(): void {
    validators.required(this.text, this.type, 'text');
    validators.string(this.text, this.type, 'text');

    if (this.preview !== undefined) {
      validators.string(this.preview, this.type, 'preview');
    }

    validators.boolean(this.isReadable, this.type, 'isReadable');
    validators.boolean(this.requiresAbility, this.type, 'requiresAbility');
    validators.boolean(this.hasBeenRead, this.type, 'hasBeenRead');

    validators.string(this.language, this.type, 'language');
    validators.notEmpty(this.language, this.type, 'language');

    validators.string(this.readableType, this.type, 'readableType');
    validators.notEmpty(this.readableType, this.type, 'readableType');

    if (this.requiresAbility && !this.requiredAbility) {
      throw new Error(`${this.type}: requiredAbility is required when requiresAbility is true`);
    }

    if (this.requiredAbility !== undefined) {
      validators.string(this.requiredAbility, this.type, 'requiredAbility');
    }

    if (this.cannotReadMessage !== undefined) {
      validators.string(this.cannotReadMessage, this.type, 'cannotReadMessage');
    }

    // Validate page-related fields
    if (this.pages !== undefined) {
      validators.number(this.pages, this.type, 'pages');
      validators.range(this.pages, 1, Number.MAX_SAFE_INTEGER, this.type, 'pages');
    }

    if (this.currentPage !== undefined) {
      validators.number(this.currentPage, this.type, 'currentPage');
      if (this.pages) {
        validators.range(this.currentPage, 1, this.pages, this.type, 'currentPage');
      }
    }

    if (this.pageContent !== undefined) {
      if (!Array.isArray(this.pageContent)) {
        throw new Error(`${this.type}.pageContent: must be an array`);
      }

      for (let i = 0; i < this.pageContent.length; i++) {
        validators.string(this.pageContent[i], this.type, `pageContent[${i}]`);
      }

      // Update pages count to match content
      this.pages = this.pageContent.length;
    }
  }

  /**
   * Get the current readable text
   */
  getText(): string {
    if (this.pageContent && this.currentPage) {
      return this.pageContent[this.currentPage - 1] || this.text;
    }
    return this.text;
  }

  /**
   * Mark as read
   */
  markAsRead(): void {
    this.hasBeenRead = true;
  }

  /**
   * Check if can be read with given ability
   */
  canRead(ability?: string): boolean {
    if (!this.isReadable) {
      return false;
    }

    if (this.requiresAbility) {
      return ability === this.requiredAbility;
    }

    return true;
  }

  /**
   * Turn to a specific page
   */
  turnToPage(page: number): boolean {
    if (!this.pageContent || !this.pages) {
      return false;
    }

    if (page < 1 || page > this.pages) {
      return false;
    }

    this.currentPage = page;
    return true;
  }

  /**
   * Turn to next page
   */
  nextPage(): boolean {
    if (!this.currentPage || !this.pages) {
      return false;
    }

    return this.turnToPage(this.currentPage + 1);
  }

  /**
   * Turn to previous page
   */
  previousPage(): boolean {
    if (!this.currentPage || !this.pages) {
      return false;
    }

    return this.turnToPage(this.currentPage - 1);
  }

  /**
   * Get a preview of the text
   */
  getPreview(): string {
    if (this.preview) {
      return this.preview;
    }

    // Generate preview from main text
    const text = this.getText();
    if (text.length <= 50) {
      return text;
    }

    // Find a good break point
    const preview = text.substring(0, 50);
    const lastSpace = preview.lastIndexOf(' ');

    if (lastSpace > 30) {
      return preview.substring(0, lastSpace) + '...';
    }

    return preview + '...';
  }
}

// packages/stdlib/src/world-model/traits/interactive/light-source.ts

import { ValidatedTrait, validators } from '../validation';

/**
 * Light source trait for entities that provide illumination.
 * Used for torches, lamps, candles, flashlights, etc.
 */
export class LightSourceTrait extends ValidatedTrait {
  static readonly type = 'light-source';
  readonly type = 'light-source';

  /** Whether the light is currently providing light */
  isLit: boolean = false;

  /** Whether the light starts lit */
  startsLit: boolean = false;

  /** Light intensity (0-100) */
  intensity: number = 75;

  /** Light radius (in rooms/spaces) */
  radius: number = 0;

  /** Color of the light */
  color: string = 'white';

  /** Whether this uses fuel */
  usesFuel: boolean = false;

  /** Current fuel level (0-100) */
  fuelLevel: number = 100;

  /** Fuel consumption per turn */
  fuelConsumption: number = 1;

  /** Whether this requires power (electric) */
  requiresPower: boolean = false;

  /** Whether this is portable when lit */
  portableWhenLit: boolean = true;

  /** Custom message when lighting */
  lightMessage?: string;

  /** Custom message when extinguishing */
  extinguishMessage?: string;

  /** Custom message when out of fuel */
  noFuelMessage?: string;

  /** Description of the light effect */
  lightDescription?: string;

  /** Whether this can be relit after going out */
  relightable: boolean = true;

  /** Whether this is flickering */
  flickering: boolean = false;

  constructor(data?: Partial<LightSourceTrait>) {
    super();
    if (data) {
      Object.assign(this, data);
    }

    // Set initial state
    if (this.startsLit) {
      this.isLit = true;
    }
  }

  validate(): void {
    validators.boolean(this.isLit, this.type, 'isLit');
    validators.boolean(this.startsLit, this.type, 'startsLit');
    validators.boolean(this.usesFuel, this.type, 'usesFuel');
    validators.boolean(this.requiresPower, this.type, 'requiresPower');
    validators.boolean(this.portableWhenLit, this.type, 'portableWhenLit');
    validators.boolean(this.relightable, this.type, 'relightable');
    validators.boolean(this.flickering, this.type, 'flickering');

    validators.number(this.intensity, this.type, 'intensity');
    validators.range(this.intensity, 0, 100, this.type, 'intensity');

    validators.number(this.radius, this.type, 'radius');
    validators.range(this.radius, 0, 10, this.type, 'radius');

    validators.string(this.color, this.type, 'color');
    validators.notEmpty(this.color, this.type, 'color');

    if (this.usesFuel) {
      validators.number(this.fuelLevel, this.type, 'fuelLevel');
      validators.range(this.fuelLevel, 0, 100, this.type, 'fuelLevel');

      validators.number(this.fuelConsumption, this.type, 'fuelConsumption');
      validators.range(this.fuelConsumption, 0, 100, this.type, 'fuelConsumption');
    }

    // Validate messages
    const stringFields = [
      'lightMessage', 'extinguishMessage', 'noFuelMessage', 'lightDescription'
    ];

    for (const field of stringFields) {
      const value = (this as any)[field];
      if (value !== undefined) {
        validators.string(value, this.type, field);
      }
    }
  }

  /**
   * Light the light source
   */
  light(): boolean {
    if (this.isLit) {
      return false; // Already lit
    }

    if (this.usesFuel && this.fuelLevel <= 0) {
      return false; // No fuel
    }

    if (!this.relightable && this.fuelLevel < 100) {
      return false; // Cannot relight partially used sources
    }

    this.isLit = true;
    return true;
  }

  /**
   * Extinguish the light source
   */
  extinguish(): boolean {
    if (!this.isLit) {
      return false; // Already extinguished
    }

    this.isLit = false;
    return true;
  }

  /**
   * Toggle lit state
   */
  toggle(): boolean {
    return this.isLit ? this.extinguish() : this.light();
  }

  /**
   * Consume fuel (called each turn)
   */
  consumeFuel(): boolean {
    if (!this.isLit || !this.usesFuel) {
      return true; // Not consuming fuel
    }

    this.fuelLevel = Math.max(0, this.fuelLevel - this.fuelConsumption);

    if (this.fuelLevel <= 0) {
      this.extinguish();
      return false; // Ran out of fuel
    }

    return true;
  }

  /**
   * Refuel the light source
   */
  refuel(amount: number = 100): void {
    if (this.usesFuel) {
      this.fuelLevel = Math.min(100, this.fuelLevel + amount);
    }
  }

  /**
   * Get effective light intensity (considering fuel, flickering, etc.)
   */
  getEffectiveIntensity(): number {
    if (!this.isLit) {
      return 0;
    }

    let intensity = this.intensity;

    // Reduce intensity based on fuel
    if (this.usesFuel && this.fuelLevel < 20) {
      intensity = Math.floor(intensity * (this.fuelLevel / 20));
    }

    // Vary intensity if flickering
    if (this.flickering) {
      intensity = Math.floor(intensity * (0.7 + Math.random() * 0.3));
    }

    return intensity;
  }

  /**
   * Get time remaining before fuel runs out
   */
  getFuelTimeRemaining(): number {
    if (!this.usesFuel || this.fuelConsumption === 0) {
      return Infinity;
    }

    return Math.floor(this.fuelLevel / this.fuelConsumption);
  }

  /**
   * Get light description based on current state
   */
  getLightDescription(): string {
    if (!this.isLit) {
      return '';
    }

    if (this.lightDescription) {
      return this.lightDescription;
    }

    // Generate description based on properties
    let desc = `providing ${this.color} light`;

    if (this.flickering) {
      desc = `flickering with ${this.color} light`;
    }

    if (this.usesFuel && this.fuelLevel < 20) {
      desc += ' (dimming)';
    }

    return desc;
  }
}

// packages/stdlib/src/world-model/traits/interactive/light-sensitive.ts

import { ValidatedTrait, validators } from '../validation';

/**
 * Light sensitive trait for entities that behave differently based on light levels.
 * Used for creatures, plants, mechanisms that react to light, etc.
 */
export class LightSensitiveTrait extends ValidatedTrait {
  static readonly type = 'light-sensitive';
  readonly type = 'light-sensitive';

  /** Minimum light level required for normal interaction (0-100) */
  minimumLight: number = 10;

  /** Whether this entity is visible in low light */
  visibleInDark: boolean = false;

  /** Whether interactions are blocked in low light */
  requiresLightToInteract: boolean = true;

  /** Custom description in darkness */
  darkDescription?: string;

  /** Custom description in dim light */
  dimDescription?: string;

  /** Custom description in bright light */
  brightDescription?: string;

  /** Message when trying to interact in darkness */
  tooD

arkMessage?: string;

  /** Current perceived light level */
  currentLightLevel: number = 100;

  /** How this entity reacts to light changes */
  lightReaction: 'none' | 'avoids' | 'seeks' | 'transforms' = 'none';

  /** Whether this entity glows in the dark */
  glowsInDark: boolean = false;

  /** Glow color if glowsInDark */
  glowColor?: string;

  /** Glow intensity if glowsInDark (0-100) */
  glowIntensity: number = 10;

  constructor(data?: Partial<LightSensitiveTrait>) {
    super();
    if (data) {
      Object.assign(this, data);
    }
  }

  validate(): void {
    validators.number(this.minimumLight, this.type, 'minimumLight');
    validators.range(this.minimumLight, 0, 100, this.type, 'minimumLight');

    validators.boolean(this.visibleInDark, this.type, 'visibleInDark');
    validators.boolean(this.requiresLightToInteract, this.type, 'requiresLightToInteract');
    validators.boolean(this.glowsInDark, this.type, 'glowsInDark');

    validators.number(this.currentLightLevel, this.type, 'currentLightLevel');
    validators.range(this.currentLightLevel, 0, 100, this.type, 'currentLightLevel');

    validators.oneOf(
      this.lightReaction,
      ['none', 'avoids', 'seeks', 'transforms'],
      this.type,
      'lightReaction'
    );

    if (this.glowsInDark) {
      validators.number(this.glowIntensity, this.type, 'glowIntensity');
      validators.range(this.glowIntensity, 0, 100, this.type, 'glowIntensity');

      if (this.glowColor !== undefined) {
        validators.string(this.glowColor, this.type, 'glowColor');
        validators.notEmpty(this.glowColor, this.type, 'glowColor');
      }
    }

    // Validate description strings
    const stringFields = [
      'darkDescription', 'dimDescription', 'brightDescription',
      'tooDarkMessage', 'glowColor'
    ];

    for (const field of stringFields) {
      const value = (this as any)[field];
      if (value !== undefined) {
        validators.string(value, this.type, field);
      }
    }
  }

  /**
   * Update the current light level
   */
  updateLightLevel(lightLevel: number): void {
    this.currentLightLevel = Math.max(0, Math.min(100, lightLevel));
  }

  /**
   * Check if there's enough light to see this entity
   */
  isVisible(ambientLight: number = this.currentLightLevel): boolean {
    if (this.visibleInDark) {
      return true;
    }

    if (this.glowsInDark && ambientLight < 30) {
      return true; // Self-illuminating
    }

    return ambientLight >= this.minimumLight;
  }

  /**
   * Check if there's enough light to interact
   */
  canInteract(ambientLight: number = this.currentLightLevel): boolean {
    if (!this.requiresLightToInteract) {
      return true;
    }

    return ambientLight >= this.minimumLight;
  }

  /**
   * Get appropriate description based on light level
   */
  getLightDescription(lightLevel: number = this.currentLightLevel): string | undefined {
    if (lightLevel < 10 && this.darkDescription) {
      return this.darkDescription;
    }

    if (lightLevel < 50 && this.dimDescription) {
      return this.dimDescription;
    }

    if (lightLevel >= 80 && this.brightDescription) {
      return this.brightDescription;
    }

    return undefined; // Use default description
  }

  /**
   * Get the light category for current conditions
   */
  getLightCategory(lightLevel: number = this.currentLightLevel): 'dark' | 'dim' | 'normal' | 'bright' {
    if (lightLevel < 10) return 'dark';
    if (lightLevel < 50) return 'dim';
    if (lightLevel < 80) return 'normal';
    return 'bright';
  }

  /**
   * Calculate effective glow contribution
   */
  getGlowContribution(): number {
    if (!this.glowsInDark || this.currentLightLevel >= 30) {
      return 0; // Only glows in darkness
    }

    // Stronger glow in darker conditions
    const darknessFactor = 1 - (this.currentLightLevel / 30);
    return Math.floor(this.glowIntensity * darknessFactor);
  }

  /**
   * Get message for interaction failure due to darkness
   */
  getTooDarkMessage(): string {
    return this.tooDarkMessage || "It's too dark to do that.";
  }

  /**
   * Process light-based reactions
   */
  reactToLight(newLightLevel: number): string | undefined {
    const oldCategory = this.getLightCategory(this.currentLightLevel);
    const newCategory = this.getLightCategory(newLightLevel);

    this.updateLightLevel(newLightLevel);

    if (oldCategory === newCategory || this.lightReaction === 'none') {
      return undefined;
    }

    // Generate reaction messages based on light reaction type
    switch (this.lightReaction) {
      case 'avoids':
        if (newCategory === 'bright' && oldCategory !== 'bright') {
          return 'It recoils from the bright light!';
        }
        break;

      case 'seeks':
        if (newCategory === 'bright' && oldCategory !== 'bright') {
          return 'It moves toward the light.';
        }
        if (newCategory === 'dark' && oldCategory !== 'dark') {
          return 'It seems distressed by the darkness.';
        }
        break;

      case 'transforms':
        if (newCategory !== oldCategory) {
          return `It changes as the light ${newCategory === 'bright' ? 'increases' : 'fades'}.`;
        }
        break;
    }

    return undefined;
  }
}

