import { ValidatedTrait, validators } from '../validation';

/**
 * Valuable trait for entities that have monetary or trade value.
 * Used for treasure, coins, gems, trade goods, etc.
 */
export class ValuableTrait extends ValidatedTrait {
  static readonly type = 'valuable';
  readonly type = 'valuable';

  /** Base monetary value */
  value: number = 1;

  /** Currency type (gold, silver, credits, etc.) */
  currency: string = 'gold';

  /** Whether this is currency itself (coins, bills) */
  isCurrency: boolean = false;

  /** Whether value is hidden until appraised */
  requiresAppraisal: boolean = false;

  /** Whether this has been appraised */
  isAppraised: boolean = false;

  /** Apparent value before appraisal */
  apparentValue?: number;

  /** Quality level (0-100) affecting value */
  quality: number = 100;

  /** Rarity level (common, uncommon, rare, legendary) */
  rarity: 'common' | 'uncommon' | 'rare' | 'legendary' = 'common';

  /** Whether this can be sold to merchants */
  tradeable: boolean = true;

  /** Special merchant types that accept this */
  acceptedBy?: string[];

  /** Special merchant types that pay more */
  valuedBy?: string[];

  /** Multiplier for valued merchants */
  valuedMultiplier: number = 1.5;

  /** Whether this is stolen (affects trading) */
  isStolen: boolean = false;

  /** Whether this is a quest item (usually can't be sold) */
  isQuestItem: boolean = false;

  /** Custom appraisal message */
  appraisalMessage?: string;

  constructor(data?: Partial<ValuableTrait>) {
    super();
    if (data) {
      Object.assign(this, data);
    }

    // Set apparent value if not specified
    if (this.requiresAppraisal && this.apparentValue === undefined) {
      this.apparentValue = Math.floor(this.value * 0.5); // Default to 50% of true value
    }
  }

  validate(): void {
    validators.number(this.value, this.type, 'value');
    validators.range(this.value, 0, Number.MAX_SAFE_INTEGER, this.type, 'value');

    validators.string(this.currency, this.type, 'currency');
    validators.notEmpty(this.currency, this.type, 'currency');

    validators.boolean(this.isCurrency, this.type, 'isCurrency');
    validators.boolean(this.requiresAppraisal, this.type, 'requiresAppraisal');
    validators.boolean(this.isAppraised, this.type, 'isAppraised');
    validators.boolean(this.tradeable, this.type, 'tradeable');
    validators.boolean(this.isStolen, this.type, 'isStolen');
    validators.boolean(this.isQuestItem, this.type, 'isQuestItem');

    validators.number(this.quality, this.type, 'quality');
    validators.range(this.quality, 0, 100, this.type, 'quality');

    validators.oneOf(
      this.rarity,
      ['common', 'uncommon', 'rare', 'legendary'],
      this.type,
      'rarity'
    );

    validators.number(this.valuedMultiplier, this.type, 'valuedMultiplier');
    validators.range(this.valuedMultiplier, 0.1, 10, this.type, 'valuedMultiplier');

    if (this.apparentValue !== undefined) {
      validators.number(this.apparentValue, this.type, 'apparentValue');
      validators.range(this.apparentValue, 0, Number.MAX_SAFE_INTEGER, this.type, 'apparentValue');
    }

    if (this.acceptedBy !== undefined) {
      if (!Array.isArray(this.acceptedBy)) {
        throw new Error(`${this.type}.acceptedBy: must be an array`);
      }
      for (let i = 0; i < this.acceptedBy.length; i++) {
        validators.string(this.acceptedBy[i], this.type, `acceptedBy[${i}]`);
      }
    }

    if (this.valuedBy !== undefined) {
      if (!Array.isArray(this.valuedBy)) {
        throw new Error(`${this.type}.valuedBy: must be an array`);
      }
      for (let i = 0; i < this.valuedBy.length; i++) {
        validators.string(this.valuedBy[i], this.type, `valuedBy[${i}]`);
      }
    }

    if (this.appraisalMessage !== undefined) {
      validators.string(this.appraisalMessage, this.type, 'appraisalMessage');
    }
  }

  /**
   * Get the current known value
   */
  getKnownValue(): number {
    if (this.requiresAppraisal && !this.isAppraised) {
      return this.apparentValue || 0;
    }

    return this.getActualValue();
  }

  /**
   * Get the actual value (considering quality)
   */
  getActualValue(): number {
    const qualityModifier = this.quality / 100;
    const rarityMultiplier = this.getRarityMultiplier();

    return Math.floor(this.value * qualityModifier * rarityMultiplier);
  }

  /**
   * Get value for a specific merchant type
   */
  getValueForMerchant(merchantType: string): number {
    let value = this.getKnownValue();

    // Apply merchant preferences
    if (this.valuedBy && this.valuedBy.includes(merchantType)) {
      value = Math.floor(value * this.valuedMultiplier);
    }

    // Stolen goods penalty (unless fence)
    if (this.isStolen && merchantType !== 'fence') {
      value = Math.floor(value * 0.1); // 10% value for stolen goods
    }

    return value;
  }

  /**
   * Check if a merchant type will accept this item
   */
  canSellTo(merchantType: string): boolean {
    if (!this.tradeable || this.isQuestItem) {
      return false;
    }

    if (this.isStolen && merchantType !== 'fence') {
      return false; // Only fences buy stolen goods
    }

    if (this.acceptedBy && this.acceptedBy.length > 0) {
      return this.acceptedBy.includes(merchantType);
    }

    return true; // Accepted by all merchants by default
  }

  /**
   * Appraise the item
   */
  appraise(): boolean {
    if (!this.requiresAppraisal || this.isAppraised) {
      return false; // Already appraised or doesn't need it
    }

    this.isAppraised = true;
    return true;
  }

  /**
   * Get rarity multiplier
   */
  private getRarityMultiplier(): number {
    switch (this.rarity) {
      case 'common': return 1;
      case 'uncommon': return 2;
      case 'rare': return 5;
      case 'legendary': return 10;
      default: return 1;
    }
  }

  /**
   * Get value description
   */
  getValueDescription(): string {
    const value = this.getKnownValue();

    if (this.isCurrency) {
      return `${value} ${this.currency}`;
    }

    if (this.requiresAppraisal && !this.isAppraised) {
      return `appears to be worth about ${value} ${this.currency}`;
    }

    return `worth ${value} ${this.currency}`;
  }

  /**
   * Mark as stolen
   */
  markAsStolen(): void {
    this.isStolen = true;
    this.tradeable = true; // Can still trade, but only with fences
  }

  /**
   * Clear stolen status (e.g., after laundering)
   */
  clearStolenStatus(): void {
    this.isStolen = false;
  }
}

