// packages/stdlib/src/world-model/traits/readable/readableTrait.ts

import { Trait } from '../trait';
import { TraitType } from '../trait-types';

/**
 * Readable trait for entities that have text to read.
 * Used for books, signs, notes, inscriptions, etc.
 * 
 * This trait contains only data - all behavior is in ReadableBehavior.
 */
export class ReadableTrait implements Trait {
  static readonly type = TraitType.READABLE;
  readonly type = TraitType.READABLE;

  /** The main text content */
  text: string = '';

  /** Short preview shown in descriptions */
  preview?: string;

  /** Whether the text is currently visible */
  isReadable: boolean = true;

  /** Language of the text (for multi-language games) */
  language: string = 'common';

  /** Whether reading requires a specific skill or item */
  requiresAbility: boolean = false;

  /** Ability/item needed to read (if requiresAbility) */
  requiredAbility?: string;

  /** Message when unable to read */
  cannotReadMessage?: string;

  /** Whether this has been read by the player */
  hasBeenRead: boolean = false;

  /** Type of readable (book, sign, note, inscription, etc.) */
  readableType: string = 'text';

  /** Number of pages (for books) */
  pages?: number;

  /** Current page (for multi-page items) */
  currentPage?: number;

  /** Content per page (for multi-page items) */
  pageContent?: string[];

  constructor(data?: Partial<ReadableTrait>) {
    if (data) {
      Object.assign(this, data);
    }

    // Initialize current page for multi-page items
    if (this.pageContent && this.pageContent.length > 0 && !this.currentPage) {
      this.currentPage = 1;
      this.pages = this.pageContent.length;
    }
  }
}

// packages/stdlib/src/world-model/traits/readable/readableBehavior.ts

import { IFEntity } from '../if-entity';
import { TraitType } from '../trait-types';
import { ReadableTrait } from './readableTrait';
import { Event, createEvent } from '../../../core-imports';
import { IFEvents } from '../../../constants/events';

/**
 * Behavior for readable entities.
 * Handles all logic related to reading text, turning pages, etc.
 */
export class ReadableBehavior {
  static readonly requiredTraits = [TraitType.READABLE];

  /**
   * Get the current readable text
   */
  static getText(entity: IFEntity): string {
    const trait = entity.get(TraitType.READABLE) as ReadableTrait;
    if (!trait) return '';

    if (trait.pageContent && trait.currentPage) {
      return trait.pageContent[trait.currentPage - 1] || trait.text;
    }
    return trait.text;
  }

  /**
   * Mark entity as read
   */
  static markAsRead(entity: IFEntity): Event[] {
    const trait = entity.get(TraitType.READABLE) as ReadableTrait;
    if (!trait || trait.hasBeenRead) return [];

    trait.hasBeenRead = true;

    return [
      createEvent(IFEvents.READ, {
        reader: 'player', // TODO: pass in actual reader
        readable: entity.id,
        firstTime: true
      })
    ];
  }

  /**
   * Check if entity can be read with given ability
   */
  static canRead(entity: IFEntity, ability?: string): boolean {
    const trait = entity.get(TraitType.READABLE) as ReadableTrait;
    if (!trait) return false;

    if (!trait.isReadable) {
      return false;
    }

    if (trait.requiresAbility) {
      return ability === trait.requiredAbility;
    }

    return true;
  }

  /**
   * Turn to a specific page
   */
  static turnToPage(entity: IFEntity, page: number): Event[] {
    const trait = entity.get(TraitType.READABLE) as ReadableTrait;
    if (!trait || !trait.pageContent || !trait.pages) {
      return [];
    }

    if (page < 1 || page > trait.pages) {
      return [
        createEvent(IFEvents.ACTION_FAILED, {
          action: 'turn_page',
          reason: 'invalid_page',
          entity: entity.id
        })
      ];
    }

    const oldPage = trait.currentPage || 1;
    trait.currentPage = page;

    return [
      createEvent(IFEvents.PAGE_TURNED, {
        readable: entity.id,
        fromPage: oldPage,
        toPage: page,
        totalPages: trait.pages
      })
    ];
  }

  /**
   * Turn to next page
   */
  static nextPage(entity: IFEntity): Event[] {
    const trait = entity.get(TraitType.READABLE) as ReadableTrait;
    if (!trait || !trait.currentPage || !trait.pages) {
      return [];
    }

    return this.turnToPage(entity, trait.currentPage + 1);
  }

  /**
   * Turn to previous page
   */
  static previousPage(entity: IFEntity): Event[] {
    const trait = entity.get(TraitType.READABLE) as ReadableTrait;
    if (!trait || !trait.currentPage || !trait.pages) {
      return [];
    }

    return this.turnToPage(entity, trait.currentPage - 1);
  }

  /**
   * Get a preview of the text
   */
  static getPreview(entity: IFEntity): string {
    const trait = entity.get(TraitType.READABLE) as ReadableTrait;
    if (!trait) return '';

    if (trait.preview) {
      return trait.preview;
    }

    // Generate preview from main text
    const text = this.getText(entity);
    if (text.length <= 50) {
      return text;
    }

    // Find a good break point
    const preview = text.substring(0, 50);
    const lastSpace = preview.lastIndexOf(' ');

    if (lastSpace > 30) {
      return preview.substring(0, lastSpace) + '...';
    }

    return preview + '...';
  }

  /**
   * Read the entity
   */
  static read(reader: IFEntity, readable: IFEntity): Event[] {
    const trait = readable.get(TraitType.READABLE) as ReadableTrait;
    if (!trait) {
      return [
        createEvent(IFEvents.ACTION_FAILED, {
          action: 'read',
          reason: 'not_readable',
          actor: reader.id,
          target: readable.id
        })
      ];
    }

    // Check if can read
    if (!this.canRead(readable)) {
      return [
        createEvent(IFEvents.ACTION_FAILED, {
          action: 'read',
          reason: trait.requiresAbility ? 'requires_ability' : 'cannot_read',
          actor: reader.id,
          target: readable.id,
          requiredAbility: trait.requiredAbility,
          message: trait.cannotReadMessage
        })
      ];
    }

    const events: Event[] = [];

    // Mark as read if first time
    if (!trait.hasBeenRead) {
      events.push(...this.markAsRead(readable));
    }

    // Main read event
    events.push(
      createEvent(IFEvents.READ, {
        reader: reader.id,
        readable: readable.id,
        text: this.getText(readable),
        page: trait.currentPage,
        totalPages: trait.pages,
        language: trait.language,
        readableType: trait.readableType
      })
    );

    return events;
  }
}

