        createEvent(IFEvents.ACTION_FAILED, {
          action: 'read',
          reason: trait.requiresAbility ? 'requires_ability' : 'cannot_read',
          actor: reader.id,
          target: readable.id,
          requiredAbility: trait.requiredAbility,
          message: trait.cannotReadMessage
        })
      ];
    }

    const events: Event[] = [];

    // Mark as read if first time
    if (!trait.hasBeenRead) {
      events.push(...this.markAsRead(readable));
    }

    // Main read event
    events.push(
      createEvent(IFEvents.READ, {
        reader: reader.id,
        readable: readable.id,
        text: this.getText(readable),
        page: trait.currentPage,
        totalPages: trait.pages,
        language: trait.language,
        readableType: trait.readableType
      })
    );

    return events;
  }
}

// packages/stdlib/src/world-model/traits/light-source/lightSourceTrait.ts

import { Trait } from '../trait';
import { TraitType } from '../trait-types';

/**
 * Light source trait for entities that provide illumination.
 * Used for torches, lamps, candles, flashlights, etc.
 * 
 * This trait contains only data - all behavior is in LightSourceBehavior.
 */
export class LightSourceTrait implements Trait {
  static readonly type = TraitType.LIGHT_SOURCE;
  readonly type = TraitType.LIGHT_SOURCE;

  /** Whether the light is currently providing light */
  isLit: boolean = false;

  /** Whether the light starts lit */
  startsLit: boolean = false;

  /** Light intensity (0-100) */
  intensity: number = 75;

  /** Light radius (in rooms/spaces) */
  radius: number = 0;

  /** Color of the light */
  color: string = 'white';

  /** Whether this uses fuel */
  usesFuel: boolean = false;

  /** Current fuel level (0-100) */
  fuelLevel: number = 100;

  /** Fuel consumption per turn */
  fuelConsumption: number = 1;

  /** Whether this requires power (electric) */
  requiresPower: boolean = false;

  /** Whether this is portable when lit */
  portableWhenLit: boolean = true;

  /** Custom message when lighting */
  lightMessage?: string;

  /** Custom message when extinguishing */
  extinguishMessage?: string;

  /** Custom message when out of fuel */
  noFuelMessage?: string;

  /** Description of the light effect */
  lightDescription?: string;

  /** Whether this can be relit after going out */
  relightable: boolean = true;

  /** Whether this is flickering */
  flickering: boolean = false;

  constructor(data?: Partial<LightSourceTrait>) {
    if (data) {
      Object.assign(this, data);
    }

    // Set initial state
    if (this.startsLit) {
      this.isLit = true;
    }
  }
}

// packages/stdlib/src/world-model/traits/light-source/lightSourceBehavior.ts

import { IFEntity } from '../if-entity';
import { TraitType } from '../trait-types';
import { LightSourceTrait } from './lightSourceTrait';
import { Event, createEvent } from '../../../core-imports';
import { IFEvents } from '../../../constants/events';

/**
 * Behavior for light source entities.
 * Handles all logic related to lighting, fuel consumption, etc.
 */
export class LightSourceBehavior {
  static readonly requiredTraits = [TraitType.LIGHT_SOURCE];

  /**
   * Light the light source
   */
  static light(entity: IFEntity, actor?: IFEntity): Event[] {
    const trait = entity.get(TraitType.LIGHT_SOURCE) as LightSourceTrait;
    if (!trait) return [];

    if (trait.isLit) {
      return [
        createEvent(IFEvents.ACTION_FAILED, {
          action: 'light',
          reason: 'already_lit',
          entity: entity.id,
          actor: actor?.id
        })
      ];
    }

    if (trait.usesFuel && trait.fuelLevel <= 0) {
      return [
        createEvent(IFEvents.ACTION_FAILED, {
          action: 'light',
          reason: 'no_fuel',
          entity: entity.id,
          actor: actor?.id,
          message: trait.noFuelMessage
        })
      ];
    }

    if (!trait.relightable && trait.fuelLevel < 100) {
      return [
        createEvent(IFEvents.ACTION_FAILED, {
          action: 'light',
          reason: 'cannot_relight',
          entity: entity.id,
          actor: actor?.id
        })
      ];
    }

    trait.isLit = true;

    return [
      createEvent(IFEvents.LIGHT_CHANGED, {
        entity: entity.id,
        isLit: true,
        actor: actor?.id,
        message: trait.lightMessage
      })
    ];
  }

  /**
   * Extinguish the light source
   */
  static extinguish(entity: IFEntity, actor?: IFEntity): Event[] {
    const trait = entity.get(TraitType.LIGHT_SOURCE) as LightSourceTrait;
    if (!trait) return [];

    if (!trait.isLit) {
      return [
        createEvent(IFEvents.ACTION_FAILED, {
          action: 'extinguish',
          reason: 'not_lit',
          entity: entity.id,
          actor: actor?.id
        })
      ];
    }

    trait.isLit = false;

    return [
      createEvent(IFEvents.LIGHT_CHANGED, {
        entity: entity.id,
        isLit: false,
        actor: actor?.id,
        message: trait.extinguishMessage
      })
    ];
  }

  /**
   * Toggle lit state
   */
  static toggle(entity: IFEntity, actor?: IFEntity): Event[] {
    const trait = entity.get(TraitType.LIGHT_SOURCE) as LightSourceTrait;
    if (!trait) return [];

    return trait.isLit 
      ? this.extinguish(entity, actor) 
      : this.light(entity, actor);
  }

  /**
   * Consume fuel (called each turn)
   */
  static consumeFuel(entity: IFEntity): Event[] {
    const trait = entity.get(TraitType.LIGHT_SOURCE) as LightSourceTrait;
    if (!trait || !trait.isLit || !trait.usesFuel) {
      return [];
    }

    trait.fuelLevel = Math.max(0, trait.fuelLevel - trait.fuelConsumption);

    if (trait.fuelLevel <= 0) {
      trait.isLit = false;
      return [
        createEvent(IFEvents.FUEL_DEPLETED, {
          entity: entity.id,
          message: trait.noFuelMessage
        }),
        createEvent(IFEvents.LIGHT_CHANGED, {
          entity: entity.id,
          isLit: false,
          reason: 'fuel_depleted'
        })
      ];
    }

    // Warn when fuel is low
    if (trait.fuelLevel <= 10) {
      return [
        createEvent(IFEvents.FUEL_LOW, {
          entity: entity.id,
          fuelLevel: trait.fuelLevel
        })
      ];
    }

    return [];
  }

  /**
   * Refuel the light source
   */
  static refuel(entity: IFEntity, amount: number = 100): Event[] {
    const trait = entity.get(TraitType.LIGHT_SOURCE) as LightSourceTrait;
    if (!trait || !trait.usesFuel) {
      return [];
    }

    const oldLevel = trait.fuelLevel;
    trait.fuelLevel = Math.min(100, trait.fuelLevel + amount);

    return [
      createEvent(IFEvents.REFUELED, {
        entity: entity.id,
        oldLevel,
        newLevel: trait.fuelLevel,
        amount
      })
    ];
  }

  /**
   * Get effective light intensity (considering fuel, flickering, etc.)
   */
  static getEffectiveIntensity(entity: IFEntity): number {
    const trait = entity.get(TraitType.LIGHT_SOURCE) as LightSourceTrait;
    if (!trait || !trait.isLit) {
      return 0;
    }

    let intensity = trait.intensity;

    // Reduce intensity based on fuel
    if (trait.usesFuel && trait.fuelLevel < 20) {
      intensity = Math.floor(intensity * (trait.fuelLevel / 20));
    }

    // Vary intensity if flickering
    if (trait.flickering) {
      intensity = Math.floor(intensity * (0.7 + Math.random() * 0.3));
    }

    return intensity;
  }

  /**
   * Get time remaining before fuel runs out
   */
  static getFuelTimeRemaining(entity: IFEntity): number {
    const trait = entity.get(TraitType.LIGHT_SOURCE) as LightSourceTrait;
    if (!trait || !trait.usesFuel || trait.fuelConsumption === 0) {
      return Infinity;
    }

    return Math.floor(trait.fuelLevel / trait.fuelConsumption);
  }

  /**
   * Check if entity provides usable light
   */
  static providesLight(entity: IFEntity): boolean {
    const trait = entity.get(TraitType.LIGHT_SOURCE) as LightSourceTrait;
    return trait ? trait.isLit : false;
  }

  /**
   * Get all light sources in a container (including nested)
   */
  static getLightSourcesIn(container: IFEntity, world: any): IFEntity[] {
    // TODO: Implement recursive search through container
    // This would query the world model for all entities in the container
    // that have the LIGHT_SOURCE trait
    return [];
  }

  /**
   * Calculate total light level from multiple sources
   */
  static calculateCombinedLight(sources: IFEntity[]): number {
    let totalIntensity = 0;

    for (const source of sources) {
      totalIntensity += this.getEffectiveIntensity(source);
    }

    // Cap at 100
    return Math.min(100, totalIntensity);
  }
}

