/**
 * Centralized trait type definitions
 * 
 * All trait types used in the world model system
 */

/**
 * Enum of all available trait types
 */
export enum TraitType {
  // Standard traits
  IDENTITY = 'identity',
  CONTAINER = 'container',
  SUPPORTER = 'supporter',
  ROOM = 'room',
  WEARABLE = 'wearable',
  EDIBLE = 'edible',
  SCENERY = 'scenery',

  // Interactive traits
  OPENABLE = 'openable',
  LOCKABLE = 'lockable',
  SWITCHABLE = 'switchable',
  READABLE = 'readable',
  LIGHT_SOURCE = 'lightSource',
  LIGHT_SENSITIVE = 'lightSensitive',
  VALUABLE = 'valuable',

  // Advanced traits
  SCRIPTABLE = 'scriptable',
  NPC = 'npc',
  DIALOGUE = 'dialogue',
  MERCHANT = 'merchant',
  PLAYER = 'player',
  DOOR = 'door'
}

/**
 * Trait categories for organization
 */
export enum TraitCategory {
  STANDARD = 'standard',
  INTERACTIVE = 'interactive',
  ADVANCED = 'advanced'
}

/**
 * Map trait types to categories
 */
export const TRAIT_CATEGORIES: Record<TraitType, TraitCategory> = {
  // Standard
  [TraitType.IDENTITY]: TraitCategory.STANDARD,
  [TraitType.CONTAINER]: TraitCategory.STANDARD,
  [TraitType.SUPPORTER]: TraitCategory.STANDARD,
  [TraitType.ROOM]: TraitCategory.STANDARD,
  [TraitType.WEARABLE]: TraitCategory.STANDARD,
  [TraitType.EDIBLE]: TraitCategory.STANDARD,
  [TraitType.SCENERY]: TraitCategory.STANDARD,

  // Interactive
  [TraitType.OPENABLE]: TraitCategory.INTERACTIVE,
  [TraitType.LOCKABLE]: TraitCategory.INTERACTIVE,
  [TraitType.SWITCHABLE]: TraitCategory.INTERACTIVE,
  [TraitType.READABLE]: TraitCategory.INTERACTIVE,
  [TraitType.LIGHT_SOURCE]: TraitCategory.INTERACTIVE,
  [TraitType.LIGHT_SENSITIVE]: TraitCategory.INTERACTIVE,
  [TraitType.VALUABLE]: TraitCategory.INTERACTIVE,

  // Advanced
  [TraitType.SCRIPTABLE]: TraitCategory.ADVANCED,
  [TraitType.NPC]: TraitCategory.ADVANCED,
  [TraitType.DIALOGUE]: TraitCategory.ADVANCED,
  [TraitType.MERCHANT]: TraitCategory.ADVANCED,
  [TraitType.PLAYER]: TraitCategory.ADVANCED,
  [TraitType.DOOR]: TraitCategory.ADVANCED
};

/**
 * Helper to check if a trait type exists
 */
export function isValidTraitType(type: string): type is TraitType {
  return Object.values(TraitType).includes(type as TraitType);
}

/**
 * Get trait category
 */
export function getTraitCategory(type: TraitType): TraitCategory {
  return TRAIT_CATEGORIES[type];
}

/**
 * Get all trait types in a category
 */
export function getTraitsByCategory(category: TraitCategory): TraitType[] {
  return Object.entries(TRAIT_CATEGORIES)
    .filter(([_, cat]) => cat === category)
    .map(([type, _]) => type as TraitType);
}

/**
 * Trait implementations map
 * 
 * Maps trait types to their implementation classes
 */

import { TraitType } from './trait-types';
import { TraitConstructor } from './trait';

// Import all trait implementations from their new locations
import { IdentityTrait } from './identity/identityTrait';
import { ContainerTrait } from './container/containerTrait';
import { SupporterTrait } from './supporter/supporterTrait';
import { RoomTrait } from './room/roomTrait';
import { WearableTrait } from './wearable/wearableTrait';
import { EdibleTrait } from './edible/edibleTrait';
import { SceneryTrait } from './scenery/sceneryTrait';

import { OpenableTrait } from './openable/openableTrait';
import { LockableTrait } from './lockable/lockableTrait';
import { SwitchableTrait } from './switchable/switchableTrait';
import { ReadableTrait } from './interactive/readable';
import { LightSourceTrait } from './interactive/light-source';
import { LightSensitiveTrait } from './interactive/light-sensitive';
import { ValuableTrait } from './interactive/valuable';

import { ScriptableTrait } from './advanced/scriptable';
import { NPCTrait } from './advanced/npc';
import { DialogueTrait } from './dialogue/dialogueTrait';
import { MerchantTrait } from './advanced/merchant';
import { PlayerTrait } from './advanced/player';
import { DoorTrait } from './advanced/door';

/**
 * Map of trait types to their constructors
 */
export const TRAIT_IMPLEMENTATIONS: Record<TraitType, TraitConstructor> = {
  // Core traits (organized in their own folders)
  [TraitType.IDENTITY]: IdentityTrait,
  [TraitType.CONTAINER]: ContainerTrait,
  [TraitType.SUPPORTER]: SupporterTrait,
  [TraitType.ROOM]: RoomTrait,
  [TraitType.WEARABLE]: WearableTrait,
  [TraitType.EDIBLE]: EdibleTrait,
  [TraitType.SCENERY]: SceneryTrait,

  // Interactive traits
  [TraitType.OPENABLE]: OpenableTrait,
  [TraitType.LOCKABLE]: LockableTrait,
  [TraitType.SWITCHABLE]: SwitchableTrait,
  [TraitType.READABLE]: ReadableTrait,
  [TraitType.LIGHT_SOURCE]: LightSourceTrait,
  [TraitType.LIGHT_SENSITIVE]: LightSensitiveTrait,
  [TraitType.VALUABLE]: ValuableTrait,

  // Advanced traits
  [TraitType.SCRIPTABLE]: ScriptableTrait,
  [TraitType.NPC]: NPCTrait,
  [TraitType.DIALOGUE]: DialogueTrait,
  [TraitType.MERCHANT]: MerchantTrait,
  [TraitType.PLAYER]: PlayerTrait,
  [TraitType.DOOR]: DoorTrait,

  // Deprecated traits - these are handled differently now:
  // LOCATION - tracked by world model internally
  // PORTABLE - objects are takeable by default
  // FIXED - replaced by SCENERY
};

/**
 * Get trait implementation by type
 */
export function getTraitImplementation(type: TraitType): TraitConstructor | undefined {
  return TRAIT_IMPLEMENTATIONS[type];
}

/**
 * Create trait instance by type
 */
export function createTrait(type: TraitType, data?: any): InstanceType<TraitConstructor> {
  const TraitClass = TRAIT_IMPLEMENTATIONS[type];
  if (!TraitClass) {
    throw new Error(`Unknown trait type: ${type}`);
  }
  return new TraitClass(data);
}

// Re-export individual implementations
export {
  // Core traits
  IdentityTrait,
  ContainerTrait,
  SupporterTrait,
  RoomTrait,
  WearableTrait,
  EdibleTrait,
  SceneryTrait,

  // Interactive
  OpenableTrait,
  LockableTrait,
  SwitchableTrait,
  ReadableTrait,
  LightSourceTrait,
  LightSensitiveTrait,
  ValuableTrait,

  // Advanced
  ScriptableTrait,
  NPCTrait,
  DialogueTrait,
  MerchantTrait,
  PlayerTrait,
  DoorTrait
};

