import { LightSensitiveTrait } from './light-sensitive';
import { ValuableTrait } from './valuable';

/**
 * Register all interactive traits with the trait registry.
 * Call this during initialization to make traits available.
 */
export function registerInteractiveTraits(): void {
  TraitRegistry.register(OpenableTrait);
  TraitRegistry.register(LockableTrait);
  TraitRegistry.register(SwitchableTrait);
  TraitRegistry.register(ReadableTrait);
  TraitRegistry.register(LightSourceTrait);
  TraitRegistry.register(LightSensitiveTrait);
  TraitRegistry.register(ValuableTrait);
}

/**
 * Helper to create interactive traits from type strings
 */
export function createInteractiveTrait(type: string, data?: any): any {
  switch (type) {
    case OpenableTrait.type:
      return new OpenableTrait(data);
    case LockableTrait.type:
      return new LockableTrait(data);
    case SwitchableTrait.type:
      return new SwitchableTrait(data);
    case ReadableTrait.type:
      return new ReadableTrait(data);
    case LightSourceTrait.type:
      return new LightSourceTrait(data);
    case LightSensitiveTrait.type:
      return new LightSensitiveTrait(data);
    case ValuableTrait.type:
      return new ValuableTrait(data);
    default:
      throw new Error(`Unknown interactive trait type: ${type}`);
  }
}

