// packages/stdlib/src/world-model/traits/entry/entryTrait.ts

import { Trait } from '../trait';
import { TraitType } from '../trait-types';

/**
 * Entry trait for entities that can be entered by actors.
 * Used for vehicles, chairs, beds, closets, or any enterable space.
 * 
 * This trait contains only data - all behavior is in EntryBehavior.
 */
export class EntryTrait implements Trait {
  static readonly type = TraitType.ENTRY;
  readonly type = TraitType.ENTRY;

  /** Whether actors can currently enter this */
  canEnter: boolean = true;

  /** Preposition used when entering (in, on, under, behind) */
  preposition: string = 'in';

  /** Maximum number of occupants (undefined = unlimited) */
  maxOccupants?: number;

  /** Current occupants (entity IDs) */
  occupants: string[] = [];

  /** Whether occupants are visible from outside */
  occupantsVisible: boolean = true;

  /** Whether occupants can see outside */
  canSeeOut: boolean = true;

  /** Whether sound passes through */
  soundproof: boolean = false;

  /** Custom message when entering */
  enterMessage?: string;

  /** Custom message when exiting */
  exitMessage?: string;

  /** Custom message when full */
  fullMessage?: string;

  /** Custom message when entry is blocked */
  blockedMessage?: string;

  /** Whether this moves with its parent (like a vehicle) */
  mobile: boolean = false;

  /** Whether entering requires standing/sitting/lying */
  posture?: 'standing' | 'sitting' | 'lying';

  constructor(data?: Partial<EntryTrait>) {
    if (data) {
      if (data.canEnter !== undefined) this.canEnter = data.canEnter;
      if (data.preposition !== undefined) this.preposition = data.preposition;
      if (data.maxOccupants !== undefined) this.maxOccupants = data.maxOccupants;
      if (data.occupants !== undefined) this.occupants = data.occupants;
      if (data.occupantsVisible !== undefined) this.occupantsVisible = data.occupantsVisible;
      if (data.canSeeOut !== undefined) this.canSeeOut = data.canSeeOut;
      if (data.soundproof !== undefined) this.soundproof = data.soundproof;
      if (data.enterMessage !== undefined) this.enterMessage = data.enterMessage;
      if (data.exitMessage !== undefined) this.exitMessage = data.exitMessage;
      if (data.fullMessage !== undefined) this.fullMessage = data.fullMessage;
      if (data.blockedMessage !== undefined) this.blockedMessage = data.blockedMessage;
      if (data.mobile !== undefined) this.mobile = data.mobile;
      if (data.posture !== undefined) this.posture = data.posture;
    }
  }
}

// packages/stdlib/src/world-model/traits/entry/entryBehavior.ts

import { IFEntity } from '../if-entity';
import { TraitType } from '../trait-types';
import { EntryTrait } from './entryTrait';
import { Event, createEvent } from '../../../core-imports';
import { IFEvents } from '../../../constants/events';

/**
 * Behavior for enterable entities.
 * Handles all logic related to entering and exiting objects.
 */
export class EntryBehavior {
  static readonly requiredTraits = [TraitType.ENTRY];

  /**
   * Check if an actor can enter
   */
  static canEnter(entity: IFEntity, actor: IFEntity): boolean {
    const trait = entity.get(TraitType.ENTRY) as EntryTrait;
    if (!trait) return false;

    // Basic check
    if (!trait.canEnter) {
      return false;
    }

    // Check occupancy limit
    if (trait.maxOccupants !== undefined && trait.occupants.length >= trait.maxOccupants) {
      return false;
    }

    // Check if already inside
    if (trait.occupants.includes(actor.id)) {
      return false;
    }

    // Check if entity is accessible (open if openable)
    if (entity.has(TraitType.OPENABLE)) {
      const openable = entity.get(TraitType.OPENABLE);
      if (openable && !openable.isOpen) {
        return false;
      }
    }

    return true;
  }

  /**
   * Enter an entity
   */
  static enter(entity: IFEntity, actor: IFEntity): Event[] {
    const trait = entity.get(TraitType.ENTRY) as EntryTrait;
    if (!trait) {
      return [
        createEvent(IFEvents.ACTION_FAILED, {
          action: 'enter',
          reason: 'not_enterable',
          target: entity.id,
          actor: actor.id
        })
      ];
    }

    if (!this.canEnter(entity, actor)) {
      const reason = this.getBlockedReason(entity, actor);
      return [
        createEvent(IFEvents.ACTION_FAILED, {
          action: 'enter',
          reason,
          target: entity.id,
          actor: actor.id,
          message: reason === 'full' ? trait.fullMessage : trait.blockedMessage
        })
      ];
    }

    // Add actor to occupants
    trait.occupants.push(actor.id);

    const events: Event[] = [];

    // Custom enter message
    if (trait.enterMessage) {
      events.push(
        createEvent(IFEvents.CUSTOM_MESSAGE, {
          message: trait.enterMessage,
          actor: actor.id
        })
      );
    }

    // Enter event
    events.push(
      createEvent(IFEvents.ENTERED, {
        actor: actor.id,
        target: entity.id,
        preposition: trait.preposition,
        posture: trait.posture
      })
    );

    return events;
  }

  /**
   * Exit an entity
   */
  static exit(entity: IFEntity, actor: IFEntity): Event[] {
    const trait = entity.get(TraitType.ENTRY) as EntryTrait;
    if (!trait) {
      return [
        createEvent(IFEvents.ACTION_FAILED, {
          action: 'exit',
          reason: 'not_enterable',
          target: entity.id,
          actor: actor.id
        })
      ];
    }

    // Check if actor is inside
    const index = trait.occupants.indexOf(actor.id);
    if (index === -1) {
      return [
        createEvent(IFEvents.ACTION_FAILED, {
          action: 'exit',
          reason: 'not_inside',
          target: entity.id,
          actor: actor.id
        })
      ];
    }

    // Remove actor from occupants
    trait.occupants.splice(index, 1);

    const events: Event[] = [];

    // Custom exit message
    if (trait.exitMessage) {
      events.push(
        createEvent(IFEvents.CUSTOM_MESSAGE, {
          message: trait.exitMessage,
          actor: actor.id
        })
      );
    }

    // Exit event
    events.push(
      createEvent(IFEvents.EXITED, {
        actor: actor.id,
        target: entity.id,
        preposition: trait.preposition
      })
    );

    return events;
  }

  /**
   * Get reason why entry is blocked
   */
  static getBlockedReason(entity: IFEntity, actor: IFEntity): string {
    const trait = entity.get(TraitType.ENTRY) as EntryTrait;
    if (!trait) return 'not_enterable';

    if (!trait.canEnter) {
      return 'entry_blocked';
    }

    if (trait.occupants.includes(actor.id)) {
      return 'already_inside';
    }

    if (trait.maxOccupants !== undefined && trait.occupants.length >= trait.maxOccupants) {
      return 'full';
    }

    if (entity.has(TraitType.OPENABLE)) {
      const openable = entity.get(TraitType.OPENABLE);
      if (openable && !openable.isOpen) {
        return 'closed';
      }
    }

    return 'cannot_enter';
  }

  /**
   * Get all occupants
   */
  static getOccupants(entity: IFEntity): string[] {
    const trait = entity.get(TraitType.ENTRY) as EntryTrait;
    return trait ? [...trait.occupants] : [];
  }

  /**
   * Check if entity has occupants
   */
  static hasOccupants(entity: IFEntity): boolean {
    const trait = entity.get(TraitType.ENTRY) as EntryTrait;
    return trait ? trait.occupants.length > 0 : false;
  }

  /**
   * Check if a specific actor is inside
   */
  static contains(entity: IFEntity, actorId: string): boolean {
    const trait = entity.get(TraitType.ENTRY) as EntryTrait;
    return trait ? trait.occupants.includes(actorId) : false;
  }

  /**
   * Remove all occupants (used when entity is destroyed, etc.)
   */
  static evacuate(entity: IFEntity): Event[] {
    const trait = entity.get(TraitType.ENTRY) as EntryTrait;
    if (!trait || trait.occupants.length === 0) {
      return [];
    }

    const events: Event[] = [];
    const evacuees = [...trait.occupants];

    // Clear occupants
    trait.occupants = [];

    // Generate events for each evacuee
    for (const actorId of evacuees) {
      events.push(
        createEvent(IFEvents.EVACUATED, {
          actor: actorId,
          from: entity.id,
          reason: 'entity_evacuated'
        })
      );
    }

    return events;
  }

  /**
   * Get description of occupants (for external viewing)
   */
  static describeOccupants(entity: IFEntity, world: any): string | undefined {
    const trait = entity.get(TraitType.ENTRY) as EntryTrait;
    if (!trait || !trait.occupantsVisible || trait.occupants.length === 0) {
      return undefined;
    }

    // TODO: Look up entity names from world
    // For now, just return count
    const count = trait.occupants.length;
    if (count === 1) {
      return `Someone is ${trait.preposition} it.`;
    } else {
      return `${count} people are ${trait.preposition} it.`;
    }
  }

  /**
   * Check if occupants can perceive outside
   */
  static canSeeOut(entity: IFEntity): boolean {
    const trait = entity.get(TraitType.ENTRY) as EntryTrait;
    return trait ? trait.canSeeOut : false;
  }

  /**
   * Check if sound passes through
   */
  static isSoundproof(entity: IFEntity): boolean {
    const trait = entity.get(TraitType.ENTRY) as EntryTrait;
    return trait ? trait.soundproof : false;
  }
}

