 * Use events/behaviors for scripting and DialogueTrait for merchants.
 */
export function registerAdvancedTraits(): void {
  TraitRegistry.register(NPCTrait);
  TraitRegistry.register(DialogueTrait);
  TraitRegistry.register(PlayerTrait);
  TraitRegistry.register(DoorTrait);
}

