import { EntityId } from '../../../core-imports';
import { Trait } from '../trait';
import { TraitType } from '../trait-types';

/**
 * Container trait allows an entity to contain other entities.
 * Used for rooms, boxes, bags, etc.
 * 
 * This is a pure data structure - all validation and logic
 * should be handled by ContainerBehavior.
 */
export class ContainerTrait implements Trait {
  static readonly type = TraitType.CONTAINER;
  readonly type = TraitType.CONTAINER;

  /** Maximum number of items (undefined = unlimited) */
  capacity?: number;

  /** Maximum total weight (undefined = unlimited) */
  maxWeight?: number;

  /** Maximum total volume (undefined = unlimited) */
  maxVolume?: number;

  /** Whether contents are visible without opening */
  transparent: boolean = true;

  /** Whether the container is currently accessible */
  accessible: boolean = true;

  /** Preposition used when describing contents ("in", "on", etc.) */
  preposition: string = 'in';

  /** IDs of entities currently contained */
  contents: EntityId[] = [];

  constructor(data?: Partial<ContainerTrait>) {
    if (data) {
      Object.assign(this, data);
    }
  }
}

