// packages/stdlib/src/world-model/traits/container/containerBehavior.ts

import { Behavior } from '../../behaviors/behavior';
import { IFEntity } from '../if-entity';
import { TraitType } from '../trait-types';
import { ContainerTrait } from './containerTrait';
import { OpenableTrait } from '../openable/openableTrait';
import { IdentityTrait } from '../identity/identityTrait';
import { SemanticEvent, createEvent, EntityId } from '../../../core-imports';
import { IFEvents } from '../../../constants/if-events';
import { ActionFailureReason } from '../../../constants/action-failure-reason';

/**
 * Behavior for container entities.
 * 
 * Handles the logic for adding and removing items from containers.
 */
export class ContainerBehavior extends Behavior {
  static requiredTraits = [TraitType.CONTAINER];

  /**
   * Check if container can accept another item
   * @param entity The container entity
   * @param item The item to check (optional - used for weight/volume checks)
   */
  canAccept(entity: IFEntity, item?: IFEntity): boolean {
    const container = this.require<ContainerTrait>(entity, TraitType.CONTAINER);

    // Check if accessible
    if (!container.accessible) return false;

    // Check if container is open (if it's openable)
    const openable = this.optional<OpenableTrait>(entity, TraitType.OPENABLE);
    if (openable && !openable.isOpen) return false;

    // Check capacity
    if (container.capacity !== undefined && container.contents.length >= container.capacity) {
      return false;
    }

    // Check weight and volume constraints if item provided
    if (item) {
      const itemIdentity = item.get(TraitType.IDENTITY);
      if (itemIdentity) {
        // Check weight limit
        if (container.maxWeight !== undefined && itemIdentity.weight !== undefined) {
          const currentWeight = this.getTotalWeight(entity);
          if (currentWeight + itemIdentity.weight > container.maxWeight) {
            return false;
          }
        }

        // Check volume limit
        if (container.maxVolume !== undefined && itemIdentity.volume !== undefined) {
          const currentVolume = this.getTotalVolume(entity);
          if (currentVolume + itemIdentity.volume > container.maxVolume) {
            return false;
          }
        }
      }
    }

    return true;
  }

  /**
   * Add an entity to container
   * @returns Events describing what happened
   */
  addItem(container: IFEntity, item: IFEntity, actor: IFEntity): SemanticEvent[] {
    const containerTrait = this.require<ContainerTrait>(container, TraitType.CONTAINER);

    // Check if we can accept
    if (!this.canAccept(container, item)) {
      const openable = this.optional<OpenableTrait>(container, TraitType.OPENABLE);

      if (openable && !openable.isOpen) {
        return [createEvent(
          IFEvents.ACTION_FAILED,
          {
            action: 'put',
            reason: ActionFailureReason.CONTAINER_CLOSED,
            item: item.id
          },
          { target: container.id, actor: actor.id }
        )];
      }

      if (containerTrait.capacity !== undefined && containerTrait.contents.length >= containerTrait.capacity) {
        return [createEvent(
          IFEvents.ACTION_FAILED,
          {
            action: 'put',
            reason: ActionFailureReason.CONTAINER_FULL,
            item: item.id
          },
          { target: container.id, actor: actor.id }
        )];
      }

      // Check if it's a weight issue
      const itemIdentity = item.get(TraitType.IDENTITY) as IdentityTrait | undefined;
      if (containerTrait.maxWeight !== undefined && itemIdentity?.weight !== undefined) {
        const currentWeight = this.getTotalWeight(container);
        if (currentWeight + itemIdentity.weight > containerTrait.maxWeight) {
          return [createEvent(
            IFEvents.ACTION_FAILED,
            {
              action: 'put',
              reason: ActionFailureReason.TOO_HEAVY,
              item: item.id,
              maxWeight: containerTrait.maxWeight,
              currentWeight,
              itemWeight: itemIdentity.weight
            },
            { target: container.id, actor: actor.id }
          )];
        }
      }

      // Check if it's a volume issue
      if (containerTrait.maxVolume !== undefined && itemIdentity?.volume !== undefined) {
        const currentVolume = this.getTotalVolume(container);
        if (currentVolume + itemIdentity.volume > containerTrait.maxVolume) {
          return [createEvent(
            IFEvents.ACTION_FAILED,
            {
              action: 'put',
              reason: ActionFailureReason.CONTAINER_FULL, // Could add TOO_BULKY reason
              item: item.id,
              maxVolume: containerTrait.maxVolume,
              currentVolume,
              itemVolume: itemIdentity.volume
            },
            { target: container.id, actor: actor.id }
          )];
        }
      }

      return [createEvent(
        IFEvents.ACTION_FAILED,
        {
          action: 'put',
          reason: ActionFailureReason.CANT_DO_THAT,
          item: item.id
        },
        { target: container.id, actor: actor.id }
      )];
    }

    // Add to contents if not already there
    if (!containerTrait.contents.includes(item.id)) {
      containerTrait.contents.push(item.id);
    }

    return [createEvent(
      IFEvents.ITEM_PUT_IN,
      {
        container: container.id,
        preposition: containerTrait.preposition
      },
      { target: item.id, actor: actor.id }
    )];
  }

  /**
   * Remove an entity from container
   * @returns Events describing what happened
   */
  removeItem(container: IFEntity, item: IFEntity, actor: IFEntity): SemanticEvent[] {
    const containerTrait = this.require<ContainerTrait>(container, TraitType.CONTAINER);

    const index = containerTrait.contents.indexOf(item.id);
    if (index === -1) {
      return [createEvent(
        IFEvents.ACTION_FAILED,
        {
          action: 'remove',
          reason: ActionFailureReason.INVALID_TARGET,
          item: item.id
        },
        { target: container.id, actor: actor.id }
      )];
    }

    // Remove from contents
    containerTrait.contents.splice(index, 1);

    return [createEvent(
      IFEvents.ITEM_REMOVED_FROM,
      {
        container: container.id
      },
      { target: item.id, actor: actor.id }
    )];
  }

  /**
   * Check if entity is directly contained
   */
  contains(container: IFEntity, itemId: EntityId): boolean {
    const containerTrait = this.require<ContainerTrait>(container, TraitType.CONTAINER);
    return containerTrait.contents.includes(itemId);
  }

  /**
   * Get all contents
   */
  getContents(container: IFEntity): EntityId[] {
    const containerTrait = this.require<ContainerTrait>(container, TraitType.CONTAINER);
    return [...containerTrait.contents];
  }

  /**
   * Check if container is empty
   */
  isEmpty(container: IFEntity): boolean {
    const containerTrait = this.require<ContainerTrait>(container, TraitType.CONTAINER);
    return containerTrait.contents.length === 0;
  }

  /**
   * Get the preposition used for this container
   */
  getPreposition(container: IFEntity): string {
    const containerTrait = this.require<ContainerTrait>(container, TraitType.CONTAINER);
    return containerTrait.preposition;
  }

  /**
   * Check if contents are visible
   */
  areContentsVisible(container: IFEntity): boolean {
    const containerTrait = this.require<ContainerTrait>(container, TraitType.CONTAINER);

    // Check transparency
    if (containerTrait.transparent) return true;

    // Check if open (for non-transparent containers)
    const openable = this.optional<OpenableTrait>(container, TraitType.OPENABLE);
    if (openable && openable.isOpen) return true;

    return false;
  }

  /**
   * Empty all contents
   * @returns Events for each item removed
   */
  empty(container: IFEntity, actor: IFEntity): SemanticEvent[] {
    const containerTrait = this.require<ContainerTrait>(container, TraitType.CONTAINER);
    const events: SemanticEvent[] = [];

    // Create events for each item
    for (const itemId of containerTrait.contents) {
      events.push(createEvent(
        IFEvents.ITEM_REMOVED_FROM,
        {
          container: container.id
        },
        { target: itemId, actor: actor.id }
      ));
    }

    // Clear contents
    containerTrait.contents = [];

    // Add container emptied event
    events.push(createEvent(
      IFEvents.CONTAINER_EMPTIED,
      {},
      { target: container.id, actor: actor.id }
    ));

    return events;
  }

  /**
   * Calculate total weight of contents
   */
  getTotalWeight(container: IFEntity): number {
    const containerTrait = this.require<ContainerTrait>(container, TraitType.CONTAINER);
    let totalWeight = 0;

    if (this.context?.world) {
      for (const itemId of containerTrait.contents) {
        const item = this.context.world.getEntity(itemId);
        if (item) {
          const identity = item.get(TraitType.IDENTITY);
          if (identity?.weight !== undefined) {
            totalWeight += identity.weight;
          }
        }
      }
    }

    return totalWeight;
  }

  /**
   * Calculate total volume of contents
   */
  getTotalVolume(container: IFEntity): number {
    const containerTrait = this.require<ContainerTrait>(container, TraitType.CONTAINER);
    let totalVolume = 0;

    if (this.context?.world) {
      for (const itemId of containerTrait.contents) {
        const item = this.context.world.getEntity(itemId);
        if (item) {
          const identity = item.get(TraitType.IDENTITY);
          if (identity?.volume !== undefined) {
            totalVolume += identity.volume;
          }
        }
      }
    }

    return totalVolume;
  }
}

