 */
export class ContainerBehavior {
  static requiredTraits = [TraitType.CONTAINER];

  /**
   * Check if container can accept another item
   */
  static canAccept(entity: IFEntity, item?: IFEntity): boolean {
    const container = entity.get(TraitType.CONTAINER) as ContainerTrait;
    if (!container) return false;

    // Check if accessible
    if (!container.accessible) return false;

    // Check if container is open (if it's openable)
    if (entity.has(TraitType.OPENABLE)) {
      const openable = entity.get(TraitType.OPENABLE) as any;
      if (openable && !openable.isOpen) return false;
    }

    // Check capacity
    if (container.capacity !== undefined && container.contents.length >= container.capacity) {
      return false;
    }

    // TODO: Check weight and volume constraints

    return true;
  }

  /**
   * Add an entity to container
   */
  static addItem(container: IFEntity, item: IFEntity, actor: IFEntity): Event[] {
    const containerTrait = container.get(TraitType.CONTAINER) as ContainerTrait;
    if (!containerTrait) return [];

    // Check if we can accept
    if (!this.canAccept(container, item)) {
      // TODO: Return proper failure events
      return [];
    }

    // Add to contents if not already there
    if (!containerTrait.contents.includes(item.id)) {
      containerTrait.contents.push(item.id);
    }

    // TODO: Return proper success event
    return [];
  }

  /**
   * Remove an entity from container
   */
  static removeItem(container: IFEntity, item: IFEntity, actor: IFEntity): Event[] {
    const containerTrait = container.get(TraitType.CONTAINER) as ContainerTrait;
    if (!containerTrait) return [];

    const index = containerTrait.contents.indexOf(item.id);
    if (index === -1) {
      return [];
    }

    // Remove from contents
    containerTrait.contents.splice(index, 1);

    // TODO: Return proper success event
    return [];
  }

  /**
   * Check if entity is directly contained
   */
  static contains(container: IFEntity, itemId: EntityId): boolean {
    const containerTrait = container.get(TraitType.CONTAINER) as ContainerTrait;
    return containerTrait ? containerTrait.contents.includes(itemId) : false;
  }

  /**
   * Get all contents
   */
  static getContents(container: IFEntity): EntityId[] {
    const containerTrait = container.get(TraitType.CONTAINER) as ContainerTrait;
    return containerTrait ? [...containerTrait.contents] : [];
  }

  /**
   * Check if container is empty
   */
  static isEmpty(container: IFEntity): boolean {
    const containerTrait = container.get(TraitType.CONTAINER) as ContainerTrait;
    return containerTrait ? containerTrait.contents.length === 0 : true;
  }

  /**
   * Get the preposition used for this container
   */
  static getPreposition(container: IFEntity): string {
    const containerTrait = container.get(TraitType.CONTAINER) as ContainerTrait;
    return containerTrait ? containerTrait.preposition : 'in';
  }

  /**
   * Check if contents are visible
   */
  static areContentsVisible(container: IFEntity): boolean {
    const containerTrait = container.get(TraitType.CONTAINER) as ContainerTrait;
    if (!containerTrait) return false;

    // Check transparency
    if (containerTrait.transparent) return true;

    // Check if open (for non-transparent containers)
    if (container.has(TraitType.OPENABLE)) {
      const openable = container.get(TraitType.OPENABLE) as any;
      if (openable && openable.isOpen) return true;
    }

    return false;
  }

  /**
   * Empty all contents
   */
  static empty(container: IFEntity, actor: IFEntity): Event[] {
    const containerTrait = container.get(TraitType.CONTAINER) as ContainerTrait;
    if (!containerTrait) return [];

    const events: Event[] = [];

    // TODO: Create events for each item

    // Clear contents
    containerTrait.contents = [];

    return events;
  }
}

