/**
 * Trait implementations map
 * 
 * Maps trait types to their implementation classes
 */

import { TraitType } from './trait-types';
import { TraitConstructor } from './trait';

// Import all trait implementations from their new locations
import { IdentityTrait } from './identity/identityTrait';
import { ContainerTrait } from './container/containerTrait';
import { SupporterTrait } from './supporter/supporterTrait';
import { RoomTrait } from './room/roomTrait';
import { WearableTrait } from './wearable/wearableTrait';
import { EdibleTrait } from './edible/edibleTrait';
import { SceneryTrait } from './scenery/sceneryTrait';

import { OpenableTrait } from './openable/openableTrait';
import { LockableTrait } from './lockable/lockableTrait';
import { SwitchableTrait } from './switchable/switchableTrait';
import { ReadableTrait } from './readable/readableTrait';
import { LightSourceTrait } from './light-source/lightSourceTrait';

import { NPCTrait } from './advanced/npc';
import { DialogueTrait } from './dialogue/dialogueTrait';
import { PlayerTrait } from './advanced/player';
import { DoorTrait } from './advanced/door';

// New traits
import { ExitTrait } from './exit/exitTrait';
import { EntryTrait } from './entry/entryTrait';

/**
 * Map of trait types to their constructors
 */
export const TRAIT_IMPLEMENTATIONS: Record<TraitType, TraitConstructor> = {
  // Core traits (organized in their own folders)
  [TraitType.IDENTITY]: IdentityTrait,
  [TraitType.CONTAINER]: ContainerTrait,
  [TraitType.SUPPORTER]: SupporterTrait,
  [TraitType.ROOM]: RoomTrait,
  [TraitType.WEARABLE]: WearableTrait,
  [TraitType.EDIBLE]: EdibleTrait,
  [TraitType.SCENERY]: SceneryTrait,

  // Interactive traits
  [TraitType.OPENABLE]: OpenableTrait,
  [TraitType.LOCKABLE]: LockableTrait,
  [TraitType.SWITCHABLE]: SwitchableTrait,
  [TraitType.READABLE]: ReadableTrait,
  [TraitType.LIGHT_SOURCE]: LightSourceTrait,

  // Advanced traits
  [TraitType.NPC]: NPCTrait,
  [TraitType.DIALOGUE]: DialogueTrait,
  [TraitType.PLAYER]: PlayerTrait,
  [TraitType.DOOR]: DoorTrait,

  // New traits
  [TraitType.EXIT]: ExitTrait,
  [TraitType.ENTRY]: EntryTrait,

  // Deprecated traits - these are handled differently now:
  // LOCATION - tracked by world model internally
  // PORTABLE - objects are takeable by default
  // FIXED - replaced by SCENERY
};

/**
 * Get trait implementation by type
 */
export function getTraitImplementation(type: TraitType): TraitConstructor | undefined {
  return TRAIT_IMPLEMENTATIONS[type];
}

/**
 * Create trait instance by type
 */
export function createTrait(type: TraitType, data?: any): InstanceType<TraitConstructor> {
  const TraitClass = TRAIT_IMPLEMENTATIONS[type];
  if (!TraitClass) {
    throw new Error(`Unknown trait type: ${type}`);
  }
  return new TraitClass(data);
}

// Re-export individual implementations
export {
  // Core traits
  IdentityTrait,
  ContainerTrait,
  SupporterTrait,
  RoomTrait,
  WearableTrait,
  EdibleTrait,
  SceneryTrait,

  // Interactive
  OpenableTrait,
  LockableTrait,
  SwitchableTrait,
  ReadableTrait,
  LightSourceTrait,

  // Advanced
  NPCTrait,
  DialogueTrait,
  PlayerTrait,
  DoorTrait,

  // New traits
  ExitTrait,
  EntryTrait
};

// packages/stdlib/src/world-model/traits/exit/exitTrait.ts

import { Trait } from '../trait';
import { TraitType } from '../trait-types';

/**
 * Exit trait for entities that represent passages between locations.
 * Used for doors, passages, portals, and any custom exits like "xyzzy".
 * 
 * This trait contains only data - all behavior is in ExitBehavior.
 */
export class ExitTrait implements Trait {
  static readonly type = TraitType.EXIT;
  readonly type = TraitType.EXIT;

  /** Source location ID */
  from: string;

  /** Destination location ID */
  to: string;

  /** Standard direction (north, south, up, etc.) - optional */
  direction?: string;

  /** Command to use this exit (e.g., "go north", "enter portal", "xyzzy") */
  command: string;

  /** Alternative commands that work for this exit */
  aliases?: string[];

  /** Whether this exit is visible to players */
  visible: boolean = true;

  /** Whether this exit appears in room descriptions */
  listed: boolean = true;

  /** Whether this exit works in reverse (bidirectional) */
  bidirectional: boolean = false;

  /** The reverse command if bidirectional */
  reverseCommand?: string;

  /** The reverse direction if bidirectional */
  reverseDirection?: string;

  /** Custom message when using this exit */
  useMessage?: string;

  /** Custom message when this exit is blocked */
  blockedMessage?: string;

  /** Whether this exit requires special conditions */
  conditional: boolean = false;

  /** Condition identifier (checked by behaviors) */
  conditionId?: string;

  constructor(data: Partial<ExitTrait>) {
    if (!data.from || !data.to || !data.command) {
      throw new Error('ExitTrait requires from, to, and command');
    }

    this.from = data.from;
    this.to = data.to;
    this.command = data.command;

    if (data.direction !== undefined) this.direction = data.direction;
    if (data.aliases !== undefined) this.aliases = data.aliases;
    if (data.visible !== undefined) this.visible = data.visible;
    if (data.listed !== undefined) this.listed = data.listed;
    if (data.bidirectional !== undefined) this.bidirectional = data.bidirectional;
    if (data.reverseCommand !== undefined) this.reverseCommand = data.reverseCommand;
    if (data.reverseDirection !== undefined) this.reverseDirection = data.reverseDirection;
    if (data.useMessage !== undefined) this.useMessage = data.useMessage;
    if (data.blockedMessage !== undefined) this.blockedMessage = data.blockedMessage;
    if (data.conditional !== undefined) this.conditional = data.conditional;
    if (data.conditionId !== undefined) this.conditionId = data.conditionId;
  }
}

