import { Behavior } from '../../behaviors/behavior';
import { IFEntity } from '../../if-entity';
import { TraitType } from '../trait-types';
import { WearableTrait } from './wearableTrait';
import { SemanticEvent, createEvent } from '@sharpee/core';
import { IFEvents } from '@sharpee/stdlib/constants/if-events';
import { ActionFailureReason } from '@sharpee/stdlib/constants/action-failure-reason';

/**
 * Behavior for wearable entities.
 * 
 * Handles the logic for wearing and removing wearable items.
 */
export class WearableBehavior extends Behavior {
  static requiredTraits = [TraitType.WEARABLE];

  /**
   * Check if an item can be worn by an actor
   */
  canWear(item: IFEntity, actor: IFEntity): boolean {
    const wearable = this.require<WearableTrait>(item, TraitType.WEARABLE);

    // Can't wear if already worn
    if (wearable.worn) return false;

    // TODO: Check if actor has conflicting items in same slot
    // TODO: Check if slots are blocked

    return true;
  }

  /**
   * Wear an item
   * @returns Events describing what happened
   */
  wear(item: IFEntity, actor: IFEntity): SemanticEvent[] {
    const wearable = this.require<WearableTrait>(item, TraitType.WEARABLE);

    if (wearable.worn) {
      if (wearable.wornBy === actor.id) {
        return [createEvent(
          IFEvents.ACTION_FAILED,
          {
            action: 'wear',
            reason: ActionFailureReason.ALREADY_WEARING
          },
          { target: item.id, actor: actor.id }
        )];
      } else {
        return [createEvent(
          IFEvents.ACTION_FAILED,
          {
            action: 'wear',
            reason: ActionFailureReason.WORN_BY_OTHER,
            wornBy: wearable.wornBy
          },
          { target: item.id, actor: actor.id }
        )];
      }
    }

    // TODO: Check slot conflicts

    // Wear it
    wearable.worn = true;
    wearable.wornBy = actor.id;

    return [createEvent(
      IFEvents.ITEM_WORN,
      {
        slot: wearable.slot,
        layer: wearable.layer,
        customMessage: wearable.wearMessage
      },
      { target: item.id, actor: actor.id }
    )];
  }

  /**
   * Remove (take off) a worn item
   * @returns Events describing what happened
   */
  remove(item: IFEntity, actor: IFEntity): SemanticEvent[] {
    const wearable = this.require<WearableTrait>(item, TraitType.WEARABLE);

    if (!wearable.worn) {
      return [createEvent(
        IFEvents.ACTION_FAILED,
        {
          action: 'remove',
          reason: ActionFailureReason.NOT_WEARING
        },
        { target: item.id, actor: actor.id }
      )];
    }

    if (wearable.wornBy !== actor.id) {
      return [createEvent(
        IFEvents.ACTION_FAILED,
        {
          action: 'remove',
          reason: ActionFailureReason.WORN_BY_OTHER,
          wornBy: wearable.wornBy
        },
        { target: item.id, actor: actor.id }
      )];
    }

    // TODO: Check if other items prevent removal

    // Remove it
    wearable.worn = false;
    wearable.wornBy = undefined;

    return [createEvent(
      IFEvents.ITEM_REMOVED,
      {
        customMessage: wearable.removeMessage
      },
      { target: item.id, actor: actor.id }
    )];
  }

  /**
   * Check if item is currently worn
   */
  isWorn(item: IFEntity): boolean {
    const wearable = this.require<WearableTrait>(item, TraitType.WEARABLE);
    return wearable.worn;
  }

  /**
   * Check who is wearing the item
   */
  getWearer(item: IFEntity): string | undefined {
    const wearable = this.require<WearableTrait>(item, TraitType.WEARABLE);
    return wearable.wornBy;
  }

  /**
   * Get the slot this item occupies
   */
  getSlot(item: IFEntity): string {
    const wearable = this.require<WearableTrait>(item, TraitType.WEARABLE);
    return wearable.slot;
  }

  /**
   * Get the layer for sorting
   */
  getLayer(item: IFEntity): number {
    const wearable = this.require<WearableTrait>(item, TraitType.WEARABLE);
    return wearable.layer;
  }

  /**
   * Check if this item blocks any slots
   */
  getBlockedSlots(item: IFEntity): string[] {
    const wearable = this.require<WearableTrait>(item, TraitType.WEARABLE);
    return [...wearable.blocksSlots];
  }
}

