    public readonly aliases: string[] = []
  ) {}

  pattern(...elements: PatternElement[]): Verb {
    this.patterns.push({ elements });
    return this;
  }

  getPatterns(): ReadonlyArray<Pattern> {
    return this.patterns;
  }
}

