    const verb = new Verb(name.toLowerCase(), aliases.map(a => a.toLowerCase()));
    this.verbs.push(verb);
    return verb;
  }

  static lit(text: string): PatternElement {
    return { type: 'literal', text: text.toLowerCase() };
  }

  static var(name: string): PatternElement {
    return { type: 'variable', name };
  }

  static choice(...options: PatternElement[]): PatternElement {
    return { type: 'choice', options };
  }

  static multi(): PatternElement {
    return { type: 'multi' };
  }

  static multiInside(): PatternElement {
    return { type: 'multiInside' };
  }

  static adj(): PatternElement {
    return { type: 'adjective' };
  }

  static defineCommonWords(): void {
    // Common adjectives
    const adjectives = [
      'big', 'small', 'heavy', 'light', 'long', 'short',
      'red', 'blue', 'green', 'old', 'new', 'rusty'
    ];
    adjectives.forEach(adj => this.adjectives.add(adj.toLowerCase()));

    // Common prepositions
    const prepositions = [
      'in', 'on', 'at', 'to', 'for', 'with', 'by', 'from'
    ];
    prepositions.forEach(prep => this.prepositions.add(prep.toLowerCase()));

    // Articles
    ['a', 'an', 'the'].forEach(art => this.articles.add(art.toLowerCase()));
  }

  static defineStandardGrammar(): void {
    // Movement
    this.define('go', 'move', 'walk')
      .pattern(this.var('direction'))
      .pattern(this.lit('to'), this.var('location'));

    // Object interaction
    this.define('take', 'get', 'pick up')
      .pattern(this.multi())
      .pattern(this.multi(), this.choice(this.lit('from'), this.lit('off')), this.var('container'));

    this.define('drop', 'put down')
      .pattern(this.multi());

    this.define('examine', 'x', 'look at')
      .pattern(this.multi());

    // Container interaction
    this.define('open')
      .pattern(this.var('container'))
      .pattern(this.var('container'), this.lit('with'), this.var('key'));

    this.define('close', 'shut')
      .pattern(this.var('container'));

    // ... more verb definitions
  }

  static isKnownAdjective(word: string): boolean {
    return this.adjectives.has(word.toLowerCase());
  }

  static isKnownPreposition(word: string): boolean {
    return this.prepositions.has(word.toLowerCase());
  }

  static isKnownArticle(word: string): boolean {
    return this.articles.has(word.toLowerCase());
  }

  static getAllVerbs(): ReadonlyArray<Verb> {
    return this.verbs;
  }
}
