            wordIndex++;
          }
          if (multiWords.length === 0) {
            return null;
          }
          result.set('multiInside', multiWords);
          break;
        }

        case 'adjective': {
          if (words[wordIndex].type !== WordType.Adjective) {
            return null;
          }
          result.set('adjective', [words[wordIndex]]);
          wordIndex++;
          break;
        }
      }
    }

    return wordIndex === words.length ? result : null;
  }

  private createParsedAction(verb: Verb, matchResult: MatchResult): ParsedAction {
    const action = new ParsedAction(verb.name);
    let currentPhrase = this.createEmptyActionPhrase();
    let currentPreposition: string | null = null;

    for (const [key, words] of matchResult.entries()) {
      switch (key) {
        case 'multi':
        case 'multiInside':
          this.handleMultiWords(words, currentPhrase);
          break;

        case 'choice':
          this.handleChoiceWords(words, currentPhrase);
          break;

        default:
          if (words.some(w => w.type === WordType.Preposition)) {
            this.handlePreposition(action, currentPhrase, currentPreposition, words);
            currentPhrase = this.createEmptyActionPhrase();
          } else {
            this.handleRegularWords(words, currentPhrase);
          }
      }
    }

    // Add any remaining phrase
    if (currentPhrase.nouns.length > 0) {
      if (currentPreposition) {
        action.addPreposition(currentPreposition, currentPhrase);
      } else {
        action.addPhrase(currentPhrase);
      }
    }

    return action;
  }

  private validateAction(action: ParsedAction): ValidationResult {
    // Check that all mentioned objects are in scope
    const mainPhrase = action.getMainPhrase();
    if (mainPhrase && !this.scope.isInScope(mainPhrase.getFullDescription())) {
      return {
        isValid: false,
        message: `I don't see any ${mainPhrase.getFullDescription()} here.`
      };
    }

    // Validate preposition phrases
    for (const prep of ['in', 'on', 'under', 'behind']) {
      const prepPhrase = action.getPhraseByPreposition(prep);
      if (prepPhrase && !this.scope.isInScope(prepPhrase.getFullDescription())) {
        return {
          isValid: false,
          message: `I don't see any ${prepPhrase.getFullDescription()} here.`
        };
      }
    }

    return { isValid: true, message: '' };
  }

  // Helper methods...
  private isKnownVerb(word: string): boolean {
    return this.verbs.some(v => 
      v.name === word || v.aliases.includes(word)
    );
  }

  private determineWordType(word: string): WordType {
    if (GrammarDefinitions.isKnownArticle(word)) return WordType.Article;
    if (GrammarDefinitions.isKnownAdjective(word)) return WordType.Adjective;
    if (GrammarDefinitions.isKnownPreposition(word)) return WordType.Preposition;
    return WordType.Noun;
  }

  private createEmptyActionPhrase(): ActionPhrase {
    return {
      articles: [],
      adjectives: [],
      nouns: [],
      getFullDescription(): string {
        return [...this.adjectives, ...this.nouns].join(' ');
      }
    };
  }

  private createErrorResult(message: string): ParseResult {
    return {
      action: new ParsedAction('unknown'),
      success: false,
      results: [{
        type: 'parse_error',
        data: { message }
      }]
    };
  }
}

type MatchResult = Map<string, Word[]>;

interface ValidationResult {
  isValid: boolean;
  message: string;
}

interface ParseResult {
  action: ParsedAction;
  success: boolean;
  results: Array<{
    type: string;
    data: Record<string, unknown>;
  }>;
}
