// Action handler type
type ActionHandler = (context: ActionContext) => Promise<ActionResult>;

// Action registry manages both standard and custom actions
class ActionRegistry {
  private standardHandlers = new Map<string, ActionHandler>();
  private customHandlers = new Map<string, ActionHandler>();

  registerStandardAction(verb: string, handler: ActionHandler): void {
    this.standardHandlers.set(verb.toLowerCase(), handler);
  }

  registerCustomAction(verb: string, handler: ActionHandler): void {
    this.customHandlers.set(verb.toLowerCase(), handler);
  }

  async executeAction(verb: string, context: ActionContext): Promise<ActionResult> {
    const customHandler = this.customHandlers.get(verb.toLowerCase());
    if (customHandler) {
      return customHandler(context);
    }

    const standardHandler = this.standardHandlers.get(verb.toLowerCase());
    if (standardHandler) {
      return standardHandler(context);
    }

    return {
      success: false,
      message: `I don't know how to ${verb}.`
    };
  }
}

// Standard library of actions
class StandardActions {
  static async take(context: ActionContext): Promise<ActionResult> {
    const { worldModel, actor, action, scope } = context;
    const mainPhrase = action.getMainPhrase();

    if (!mainPhrase) {
      return {
        success: false,
        message: "Take what?"
      };
    }

    const target = scope.resolveObject(mainPhrase);
    if (!target) {
      return {
        success: false,
        message: `I don't see any ${mainPhrase.getFullDescription()} here.`
      };
    }

    if (!target.canBeTaken()) {
      return {
        success: false,
        message: `You can't take the ${target.name}.`
      };
    }

    const currentLocation = worldModel.getObjectLocation(target.id);
    if (currentLocation?.type === 'inventory' && currentLocation.owner === actor.id) {
      return {
        success: false,
        message: `You're already holding the ${target.name}.`
      };
    }

    try {
      await worldModel.moveObject(target.id, { type: 'inventory', owner: actor.id });
      return {
        success: true,
        message: `You take the ${target.name}.`,
        effects: [{
          type: 'inventory_changed',
          data: {
            actor: actor.id,
            item: target.id,
            action: 'added'
          }
        }]
      };
    } catch (error) {
      return {
        success: false,
        message: `You can't take the ${target.name}.`
      };
    }
  }

