 Let's analyze the types of event groupings that would create natural IF-style turn output. I'll organize them by common IF interaction patterns:

<antThinking>This is a good candidate for an artifact as it demonstrates how to organize event types and their groupings for narrative generation in IF games.</antThinking>

<antArtifact identifier="if-event-groups" type="application/vnd.ant.code" language="typescript" title="IF Event Groupings">
// Define major event categories for IF interactions
type EventCategory = 
  | 'MOVEMENT'       // Moving between rooms
  | 'MANIPULATION'   // Physical interaction with objects
  | 'INVENTORY'      // Inventory management
  | 'OBSERVATION'    // Looking, examining
  | 'CONVERSATION'   // NPC interaction
  | 'STATE_CHANGE'   // Game state changes
  | 'CONTAINER'      // Container interactions
  | 'DOOR'          // Door interactions
  | 'META';         // Game management (save, restore, etc)

// Event groups represent common IF interaction patterns
interface EventGroup {
  category: EventCategory;
  primaryType: string;
  relatedTypes: string[];
  priority: number;      // Lower numbers print first
  suppressible: boolean; // Can this be omitted if part of larger action?
}

const ifEventGroups: EventGroup[] = [
  // Movement groups
  {
    category: 'MOVEMENT',
    primaryType: 'room_exit',
    relatedTypes: [
      'door_opened',
      'door_closed',
      'room_enter',
      'darkness_change',
      'items_revealed',
      'items_hidden'
    ],
    priority: 10,
    suppressible: false
  },

  // Container interaction groups
  {
    category: 'CONTAINER',
    primaryType: 'container_open',
    relatedTypes: [
      'items_revealed',
      'light_level_change',
      'container_state_change'
    ],
    priority: 20,
    suppressible: false
  },
  {
    category: 'CONTAINER',
    primaryType: 'container_close',
    relatedTypes: [
      'items_hidden',
      'light_level_change',
      'container_state_change'
    ],
    priority: 20,
    suppressible: false
  },

  // Inventory management groups
  {
    category: 'INVENTORY',
    primaryType: 'item_taken',
    relatedTypes: [
      'inventory_add',
      'container_contents_changed',
      'weight_changed',
      'light_level_change'
    ],
    priority: 30,
    suppressible: false
  },
  {
    category: 'INVENTORY',
    primaryType: 'item_dropped',
    relatedTypes: [
      'inventory_remove',
      'room_contents_changed',
      'weight_changed',
      'light_level_change'
    ],
    priority: 30,
    suppressible: false
  },

  // Observation groups
  {
    category: 'OBSERVATION',
    primaryType: 'examine_object',
    relatedTypes: [
      'secret_discovered',
      'writing_read',
      'memory_triggered'
    ],
    priority: 40,
    suppressible: false
  },
  {
    category: 'OBSERVATION',
    primaryType: 'room_look',
    relatedTypes: [
      'items_noticed',
      'exits_noticed',
      'ambient_events'
    ],
    priority: 50,
    suppressible: true
  },

  // State change groups
  {
    category: 'STATE_CHANGE',
    primaryType: 'puzzle_solved',
    relatedTypes: [
      'door_unlocked',
      'path_revealed',
      'item_transformed',
      'achievement_earned'
    ],
    priority: 15,
    suppressible: false
  },

  // Conversation groups
  {
    category: 'CONVERSATION',
    primaryType: 'npc_talk',
    relatedTypes: [
      'knowledge_gained',
      'quest_updated',
      'item_offered',
      'item_received'
    ],
    priority: 25,
    suppressible: false
  }
];

class EventGroupManager {
  groupEvents(events: StateEvent[]): StateEvent[][] {
    // First pass: Create initial groups based on related events
    const initialGroups = this.createInitialGroups(events);

    // Second pass: Merge groups that should be combined
    return this.mergeRelatedGroups(initialGroups);
  }

  private createInitialGroups(events: StateEvent[]): StateEvent[][] {
    const groups: StateEvent[][] = [];
    let currentGroup: StateEvent[] = [];

    for (const event of events) {
      if (this.shouldStartNewGroup(event, currentGroup)) {
        if (currentGroup.length > 0) {
          groups.push([...currentGroup]);
        }
        currentGroup = [event];
      } else {
        currentGroup.push(event);
      }
    }

    if (currentGroup.length > 0) {
      groups.push(currentGroup);
    }

    return groups;
  }

  private mergeRelatedGroups(groups: StateEvent[][]): StateEvent[][] {
    const mergedGroups: StateEvent[][] = [];
    let currentMergeGroup: StateEvent[] = [];

    for (const group of groups) {
      const primaryEvent = group[0];
      const eventGroup = this.findEventGroup(primaryEvent.type);

      if (!eventGroup) {
        mergedGroups.push(group);
        continue;
      }

      // Check if this group should be merged with the current one
      if (this.shouldMergeWithPrevious(group, currentMergeGroup)) {
        currentMergeGroup.push(...group);
      } else {
        if (currentMergeGroup.length > 0) {
          mergedGroups.push([...currentMergeGroup]);
        }
        currentMergeGroup = [...group];
      }
    }

    if (currentMergeGroup.length > 0) {
      mergedGroups.push(currentMergeGroup);
    }

    // Sort groups by priority
    return this.sortGroupsByPriority(mergedGroups);
  }

  private shouldMergeWithPrevious(newGroup: StateEvent[], currentGroup: StateEvent[]): boolean {
    if (currentGroup.length === 0) return false;

    const currentPrimaryEvent = currentGroup[0];
    const newPrimaryEvent = newGroup[0];

    // Check if these events are part of a natural sequence
    const naturalSequences = new Map([
      ['take', ['examine', 'inventory_add']],
      ['open_door', ['go', 'room_enter']],
