type EventCategory = 
  | 'MOVEMENT'       // Moving between rooms
  | 'MANIPULATION'   // Physical interaction with objects
  | 'INVENTORY'      // Inventory management
  | 'OBSERVATION'    // Looking, examining
  | 'CONVERSATION'   // NPC interaction
  | 'STATE_CHANGE'   // Game state changes
  | 'CONTAINER'      // Container interactions
  | 'DOOR'          // Door interactions
  | 'META';         // Game management (save, restore, etc)

// Event groups represent common IF interaction patterns
interface EventGroup {
  category: EventCategory;
  primaryType: string;
  relatedTypes: string[];
  priority: number;      // Lower numbers print first
  suppressible: boolean; // Can this be omitted if part of larger action?
}

const ifEventGroups: EventGroup[] = [
  // Movement groups
  {
    category: 'MOVEMENT',
    primaryType: 'room_exit',
    relatedTypes: [
      'door_opened',
      'door_closed',
      'room_enter',
      'darkness_change',
      'items_revealed',
      'items_hidden'
    ],
    priority: 10,
    suppressible: false
  },

  // Container interaction groups
  {
    category: 'CONTAINER',
    primaryType: 'container_open',
    relatedTypes: [
      'items_revealed',
      'light_level_change',
      'container_state_change'
    ],
    priority: 20,
    suppressible: false
  },
  {
    category: 'CONTAINER',
    primaryType: 'container_close',
    relatedTypes: [
      'items_hidden',
      'light_level_change',
      'container_state_change'
    ],
    priority: 20,
    suppressible: false
  },

  // Inventory management groups
  {
    category: 'INVENTORY',
    primaryType: 'item_taken',
    relatedTypes: [
      'inventory_add',
      'container_contents_changed',
      'weight_changed',
      'light_level_change'
    ],
    priority: 30,
    suppressible: false
  },
  {
    category: 'INVENTORY',
    primaryType: 'item_dropped',
    relatedTypes: [
      'inventory_remove',
      'room_contents_changed',
      'weight_changed',
      'light_level_change'
    ],
    priority: 30,
    suppressible: false
  },

  // Observation groups
  {
    category: 'OBSERVATION',
    primaryType: 'examine_object',
    relatedTypes: [
      'secret_discovered',
      'writing_read',
      'memory_triggered'
    ],
    priority: 40,
    suppressible: false
  },
  {
    category: 'OBSERVATION',
    primaryType: 'room_look',
    relatedTypes: [
      'items_noticed',
      'exits_noticed',
      'ambient_events'
    ],
    priority: 50,
    suppressible: true
  },

  // State change groups
  {
    category: 'STATE_CHANGE',
    primaryType: 'puzzle_solved',
    relatedTypes: [
      'door_unlocked',
      'path_revealed',
