// Types for localized content
type LocaleCode = string; // e.g., "en-US", "es-MX"
type TranslationKey = string;

interface LocalizedContent {
  key: TranslationKey;
  locale: LocaleCode;
  content: string;
  metadata?: Record<string, unknown>;
}

interface StoryTranslation {
  locale: LocaleCode;
  name: string;
  author: string;
  description: string;
  content: Map<TranslationKey, string>;
  templates: Map<string, NarrativeTemplate>;
}

// Main localization service
class LocalizationService {
  private translations = new Map<LocaleCode, StoryTranslation>();
  private currentLocale: LocaleCode;
  private fallbackLocale: LocaleCode;

  constructor(defaultLocale: LocaleCode, fallbackLocale: LocaleCode) {
    this.currentLocale = defaultLocale;
    this.fallbackLocale = fallbackLocale;
  }

  setLocale(locale: LocaleCode): void {
    if (!this.translations.has(locale)) {
      throw new Error(`Locale ${locale} not loaded`);
    }
    this.currentLocale = locale;
  }

  // Load translations for a locale
  loadTranslation(translation: StoryTranslation): void {
    this.translations.set(translation.locale, translation);
  }

  // Get text with variable substitution
  getText(key: TranslationKey, variables?: Record<string, string>): string {
    const translation = this.translations.get(this.currentLocale);
    const fallback = this.translations.get(this.fallbackLocale);

    let text = translation?.content.get(key) || fallback?.content.get(key) || key;

    if (variables) {
      text = this.substituteVariables(text, variables);
    }

    return text;
  }

  // Get narrative template for current locale
  getTemplate(templateKey: string): NarrativeTemplate | undefined {
    const translation = this.translations.get(this.currentLocale);
    return translation?.templates.get(templateKey);
  }

  private substituteVariables(text: string, variables: Record<string, string>): string {
    return text.replace(/\{(\w+)\}/g, (_, key) => variables[key] || `{${key}}`);
  }
}

// Localized narrative templates
interface LocalizedTemplate extends NarrativeTemplate {
  locale: LocaleCode;
  templateKey: string;
}

// Example of a localized template
class LocalizedTakeTemplate implements LocalizedTemplate {
  constructor(
    public locale: LocaleCode,
    public templateKey: string,
    private l10n: LocalizationService
  ) {}

  matches(events: StateEvent[]): boolean {
    return events[0]?.type === 'take';
  }

  generate(events: StateEvent[], context: NarrativeContext): string {
    const takeEvent = events[0];
    const item = context.worldModel.getObject(takeEvent.target!);

    if (!item) {
      return this.l10n.getText('take.generic');
    }

    const containerEvent = events.find(e => e.type === 'container_contents_changed');
    if (containerEvent) {
      const container = context.worldModel.getObject(containerEvent.target!);
      return this.l10n.getText('take.from_container', {
        item: item.getLocalizedName(this.locale),
        container: container?.getLocalizedName(this.locale) || ''
      });
    }

    return this.l10n.getText('take.basic', {
      item: item.getLocalizedName(this.locale)
    });
  }
}

// World model objects need to support localization
interface LocalizedWorldObject {
  id: string;
  getLocalizedName(locale: LocaleCode): string;
  getLocalizedDescription(locale: LocaleCode): string;
  getLocalizedProperties(locale: LocaleCode): Map<string, string>;
}

// Example of a localized game object
class GameObject implements LocalizedWorldObject {
  private names = new Map<LocaleCode, string>();
  private descriptions = new Map<LocaleCode, string>();
  private properties = new Map<LocaleCode, Map<string, string>>();

  constructor(
    public id: string,
    private l10n: LocalizationService
  ) {}

  setLocalizedContent(locale: LocaleCode, content: {
    name?: string;
    description?: string;
    properties?: Map<string, string>;
  }): void {
    if (content.name) this.names.set(locale, content.name);
    if (content.description) this.descriptions.set(locale, content.description);
    if (content.properties) this.properties.set(locale, content.properties);
  }

  getLocalizedName(locale: LocaleCode): string {
    return this.names.get(locale) || this.names.get(this.l10n.fallbackLocale) || this.id;
  }

  getLocalizedDescription(locale: LocaleCode): string {
    return this.descriptions.get(locale) || 
           this.descriptions.get(this.l10n.fallbackLocale) || 
           '';
  }

  getLocalizedProperties(locale: LocaleCode): Map<string, string> {
    return this.properties.get(locale) || 
           this.properties.get(this.l10n.fallbackLocale) || 
           new Map();
  }
}

// Example usage in a story
interface StoryContent {
  translations: {
    [locale: string]: {
      strings: Record<string, string>;
      templates: Record<string, LocalizedTemplate>;
      objects: Record<string, LocalizedContent>;
    }
  }
}

const storyContent: StoryContent = {
  translations: {
    "en-US": {
      strings: {
        "take.generic": "You take it.",
        "take.basic": "You take {item}.",
        "take.from_container": "You take {item} from {container}.",
        "item.brass_key.name": "brass key",
        "item.brass_key.desc": "A small brass key with ornate engravings.",
        // ... more strings
      },
      templates: {
        // ... templates for en-US
      },
      objects: {
        // ... object content for en-US
      }
    },
    "es-MX": {
      strings: {
        "take.generic": "Lo tomas.",
        "take.basic": "Tomas {item}.",
        "take.from_container": "Tomas {item} de {container}.",
        "item.brass_key.name": "llave de latn",
        "item.brass_key.desc": "Una pequea llave de latn con grabados ornamentales.",
        // ... more strings
      },
      templates: {
        // ... templates for es-MX
      },
      objects: {
        // ... object content for es-MX
      }
    }
  }
};

// Loading the story with localization
class Story {
  private l10n: LocalizationService;

  constructor() {
    this.l10n = new LocalizationService("en-US", "en-US");
    this.loadContent();
  }

  private loadContent() {
    // Load each translation
    Object.entries(storyContent.translations).forEach(([locale, content]) => {
      const translation: StoryTranslation = {
        locale: locale as LocaleCode,
        name: content.strings["story.name"],
        author: content.strings["story.author"],
        description: content.strings["story.description"],
        content: new Map(Object.entries(content.strings)),
        templates: new Map(Object.entries(content.templates))
      };

      this.l10n.loadTranslation(translation);
    });
  }

  changeLanguage(locale: LocaleCode) {
    this.l10n.setLocale(locale);
    // Refresh UI/current room description/etc
  }
}
