  // Add pre-action handler
  beforeAction(verb: string, handler: ActionHandler): this {
    const existingHandler = this.actionHandlers.get(verb);
    this.actionHandlers.set(verb, async (context) => {
      const result = await handler(context);
      if (!result.preventDefault && existingHandler) {
        return existingHandler(context);
      }
      return result;
    });
    return this;
  }

  // Add post-action handler
  afterAction(verb: string, handler: ActionHandler): this {
    const existingHandler = this.actionHandlers.get(verb);
    this.actionHandlers.set(verb, async (context) => {
      const result = existingHandler 
        ? await existingHandler(context)
        : { success: true };

      if (result.success) {
        const postResult = await handler(context);
        return {
          ...result,
          message: postResult.message 
            ? `${result.message || ''}\n${postResult.message}`
            : result.message
        };
      }
      return result;
    });
    return this;
  }

