<antArtifact identifier="datastore-tests" type="application/vnd.ant.code" language="csharp" title="DataStore Unit Tests">
using Microsoft.VisualStudio.TestTools.UnitTesting;
using DataStore;
using System;
using System.Linq;

namespace DataStore.Tests
{
    [TestClass]
    public class DataStoreTests
    {
        [TestMethod]
        public void Graph_AddNode_ShouldAddNodeSuccessfully()
        {
            var graph = new Graph();
            var node = new TestNode();

            graph.AddNode(node);

            Assert.IsTrue(graph.Nodes.ContainsKey(node.Id));
            Assert.AreEqual(node, graph.Nodes[node.Id]);
        }

        [TestMethod]
        public void Graph_RemoveNode_ShouldRemoveNodeAndConnectedEdges()
        {
            var graph = new Graph();
            var node1 = new TestNode();
            var node2 = new TestNode();
            graph.AddNode(node1);
            graph.AddNode(node2);

            var edge = graph.CreateEdge(node1.Id, node2.Id);

            graph.RemoveNode(node1.Id);

            Assert.IsFalse(graph.Nodes.ContainsKey(node1.Id));
            Assert.IsTrue(graph.Nodes.ContainsKey(node2.Id));
            Assert.IsFalse(node2.Edges.Contains(edge));
        }

        [TestMethod]
        public void Graph_CreateEdge_ShouldCreateEdgeSuccessfully()
        {
            var graph = new Graph();
            var node1 = new TestNode();
            var node2 = new TestNode();
            graph.AddNode(node1);
            graph.AddNode(node2);

            var edge = graph.CreateEdge(node1.Id, node2.Id);

            Assert.IsNotNull(edge);
            Assert.AreEqual(node1, edge.Source);
            Assert.AreEqual(node2, edge.Target);
            Assert.IsTrue(node1.Edges.Contains(edge));
            Assert.IsTrue(node2.Edges.Contains(edge));
        }

        [TestMethod]
        public void Graph_RemoveEdge_ShouldRemoveEdgeSuccessfully()
        {
            var graph = new Graph();
            var node1 = new TestNode();
            var node2 = new TestNode();
            graph.AddNode(node1);
            graph.AddNode(node2);

            var edge = graph.CreateEdge(node1.Id, node2.Id);
            graph.RemoveEdge(edge);

            Assert.IsFalse(node1.Edges.Contains(edge));
            Assert.IsFalse(node2.Edges.Contains(edge));
        }

        [TestMethod]
        public void Node_SetPropertyValue_ShouldSetPropertySuccessfully()
        {
            var node = new TestNode();
            node.SetPropertyValue("TestProp", "TestValue");

            Assert.AreEqual("TestValue", node.GetPropertyValue<string>("TestProp"));
        }

        [TestMethod]
        public void Node_PropertyChanged_ShouldRaiseEventOnPropertyChange()
        {
            var node = new TestNode();
            bool eventRaised = false;
            node.PropertyChanged += (sender, e) => 
            {
                eventRaised = true;
                Assert.AreEqual("TestProp", e.PropertyName);
                Assert.AreEqual(null, e.OldValue);
                Assert.AreEqual("TestValue", e.NewValue);
            };

            node.SetPropertyValue("TestProp", "TestValue");

            Assert.IsTrue(eventRaised);
        }

        [TestMethod]
        public void Edge_SetSourcePropertyValue_ShouldSetPropertySuccessfully()
        {
            var edge = new Edge();
            edge.SetSourcePropertyValue("TestProp", "TestValue");

            Assert.AreEqual("TestValue", edge.GetSourcePropertyValue<string>("TestProp"));
        }

        [TestMethod]
        public void Edge_SetTargetPropertyValue_ShouldSetPropertySuccessfully()
        {
            var edge = new Edge();
            edge.SetTargetPropertyValue("TestProp", "TestValue");

            Assert.AreEqual("TestValue", edge.GetTargetPropertyValue<string>("TestProp"));
        }

        [TestMethod]
        public void Edge_ConnectNodes_ShouldConnectNodesSuccessfully()
        {
            var node1 = new TestNode();
            var node2 = new TestNode();
            var edge = new Edge();

            edge.ConnectNodes(node1, node2);

            Assert.AreEqual(node1, edge.Source);
            Assert.AreEqual(node2, edge.Target);
            Assert.IsTrue(node1.Edges.Contains(edge));
            Assert.IsTrue(node2.Edges.Contains(edge));
        }

        [TestMethod]
        public void Edge_DisconnectNodes_ShouldDisconnectNodesSuccessfully()
        {
            var node1 = new TestNode();
            var node2 = new TestNode();
            var edge = new Edge(node1, node2);

            edge.DisconnectNodes();

            Assert.IsNull(edge.Source);
            Assert.IsNull(edge.Target);
            Assert.IsFalse(node1.Edges.Contains(edge));
            Assert.IsFalse(node2.Edges.Contains(edge));
        }

        [TestMethod]
        public void Property_GetValue_ShouldReturnCorrectValue()
        {
            var prop = new Property("TestProp", "TestValue");

            Assert.AreEqual("TestValue", prop.GetValue<string>());
        }

        [TestMethod]
        public void Property_SetValue_ShouldUpdateValue()
        {
            var prop = new Property("TestProp", "TestValue");
            prop.SetValue("NewValue");

            Assert.AreEqual("NewValue", prop.GetValue<string>());
        }

        [TestMethod]
        public void Queries_GetAdjacentNodes_ShouldReturnCorrectNodes()
        {
            var graph = new Graph();
            var node1 = new TestNode();
            var node2 = new TestNode();
            var node3 = new TestNode();
            graph.AddNode(node1);
            graph.AddNode(node2);
            graph.AddNode(node3);

            var edge1 = graph.CreateEdge(node1.Id, node2.Id);
            var edge2 = graph.CreateEdge(node1.Id, node3.Id);
            edge1.SetSourcePropertyValue("type", "friend");
            edge2.SetSourcePropertyValue("type", "enemy");

            var friendNodes = node1.GetAdjacentNodes(e => e.GetSourcePropertyValue<string>("type") == "friend").ToList();

            Assert.AreEqual(1, friendNodes.Count);
            Assert.AreEqual(node2, friendNodes[0]);
        }

        [TestMethod]
        public void Queries_GetShortestPath_ShouldReturnCorrectPath()
        {
            var graph = new Graph();
            var nodeA = new TestNode();
            var nodeB = new TestNode();
            var nodeC = new TestNode();
            var nodeD = new TestNode();
            graph.AddNode(nodeA);
            graph.AddNode(nodeB);
            graph.AddNode(nodeC);
            graph.AddNode(nodeD);

            graph.CreateEdge(nodeA.Id, nodeB.Id);
            graph.CreateEdge(nodeB.Id, nodeC.Id);
            graph.CreateEdge(nodeC.Id, nodeD.Id);
            graph.CreateEdge(nodeA.Id, nodeD.Id);

            var path = nodeA.GetShortestPath(nodeD, e => true).ToList();

            Assert.AreEqual(1, path.Count);
            Assert.AreEqual(nodeD, path[0].Target);
        }
    }
}
</antArtifact>

These unit tests cover the main functionality of our Data Store components:

1. Graph operations (adding/removing nodes and edges)
2. Node property management
3. Edge connections and property management
4. Property value getting and setting
5. Query methods (GetAdjacentNodes and GetShortestPath)

Some key points about these tests:

1. We're using a `TestNode` class, which should be a simple implementation of `INode` for testing purposes. You may need to create this class in your test project.

2. The tests cover both positive scenarios (successful operations) and some edge cases (like property change events).

3. We're testing the query methods to ensure they return the expected results based on the graph structure and edge properties.

4. We're using Assert statements to verify the expected outcomes of each operation.

To run these tests, you'll need to make sure you have the Microsoft.VisualStudio.TestTools.UnitTesting NuGet package installed in your test project.

These tests should help identify any issues in the core Data Store implementation. If any of these tests fail, it would indicate a problem in the corresponding functionality that needs to be addressed.

