<antArtifact identifier="common-tests" type="application/vnd.ant.code" language="csharp" title="Common Project Unit Tests">
using Microsoft.VisualStudio.TestTools.UnitTesting;
using Common;
using System;
using System.Linq;
using System.Collections.Generic;

namespace Common.Tests
{
    [TestClass]
    public class CommonTests
    {
        [TestMethod]
        public void ParsedAction_Constructor_ShouldSetVerbCorrectly()
        {
            var action = new ParsedAction("take");
            Assert.AreEqual("take", action.Verb);
        }

        [TestMethod]
        public void ParsedAction_AddNounGroup_ShouldAddNounsCorrectly()
        {
            var action = new ParsedAction("take");
            action.AddNounGroup("red", "ball");

            Assert.AreEqual("red", action.GetNoun(0, 0));
            Assert.AreEqual("ball", action.GetNoun(0, 1));
        }

        [TestMethod]
        public void ParsedAction_AddPreposition_ShouldAddPrepositionCorrectly()
        {
            var action = new ParsedAction("put");
            action.AddPreposition("on", "table");

            Assert.AreEqual("table", action.GetPrepositionTarget("on"));
        }

        [TestMethod]
        public void ParsedAction_GetNounGroup_ShouldReturnCorrectGroup()
        {
            var action = new ParsedAction("take");
            action.AddNounGroup("red", "ball");
            action.AddNounGroup("blue", "cube");

            CollectionAssert.AreEqual(new[] { "red", "ball" }, action.GetNounGroup(0));
            CollectionAssert.AreEqual(new[] { "blue", "cube" }, action.GetNounGroup(1));
        }

        [TestMethod]
        public void ParsedAction_GetAllNounGroups_ShouldReturnAllGroups()
        {
            var action = new ParsedAction("take");
            action.AddNounGroup("red", "ball");
            action.AddNounGroup("blue", "cube");

            var allGroups = action.GetAllNounGroups().ToList();
            Assert.AreEqual(2, allGroups.Count);
            CollectionAssert.AreEqual(new[] { "red", "ball" }, allGroups[0]);
            CollectionAssert.AreEqual(new[] { "blue", "cube" }, allGroups[1]);
        }

        [TestMethod]
        public void ParsedAction_HasPreposition_ShouldReturnCorrectResult()
        {
            var action = new ParsedAction("put");
            action.AddPreposition("on", "table");

            Assert.IsTrue(action.HasPreposition("on"));
            Assert.IsFalse(action.HasPreposition("in"));
        }

        [TestMethod]
        public void ParsedAction_GetAllPrepositions_ShouldReturnAllPrepositions()
        {
            var action = new ParsedAction("put");
            action.AddPreposition("on", "table");
            action.AddPreposition("near", "chair");

            var allPrepositions = action.GetAllPrepositions().ToList();
            Assert.AreEqual(2, allPrepositions.Count);
            Assert.AreEqual("on", allPrepositions[0].Key);
            Assert.AreEqual("table", allPrepositions[0].Value);
            Assert.AreEqual("near", allPrepositions[1].Key);
            Assert.AreEqual("chair", allPrepositions[1].Value);
        }

        [TestMethod]
        public void ParsedActionResult_Constructor_ShouldSetPropertiesCorrectly()
        {
            var action = new ParsedAction("take");
            var result = new ParsedActionResult(action, true);

            Assert.AreEqual(action, result.Action);
            Assert.IsTrue(result.Success);
        }

        [TestMethod]
        public void ParsedActionResult_AddResult_ShouldAddActionResultCorrectly()
        {
            var action = new ParsedAction("take");
            var parsedResult = new ParsedActionResult(action, true);
            var actionResult = new ActionResult("success");

            parsedResult.AddResult(actionResult);

            Assert.AreEqual(1, parsedResult.Results.Count);
            Assert.AreEqual(actionResult, parsedResult.Results[0]);
        }

        [TestMethod]
        public void ParsedActionResult_AddData_ShouldAddDataCorrectly()
        {
            var action = new ParsedAction("take");
            var result = new ParsedActionResult(action, true);

            result.AddData("item", "ball");

            Assert.AreEqual("ball", result.GetData<string>("item"));
        }

        [TestMethod]
        public void ParsedActionResult_GetData_ShouldReturnDefaultValueForMissingKey()
        {
            var action = new ParsedAction("take");
            var result = new ParsedActionResult(action, true);

            Assert.AreEqual("default", result.GetData("missingKey", "default"));
        }

        [TestMethod]
        public void ActionResult_Constructor_ShouldSetKeyCorrectly()
        {
            var result = new ActionResult("success");
            Assert.AreEqual("success", result.Key);
        }

        [TestMethod]
        public void ActionResult_AddParameter_ShouldAddParameterCorrectly()
        {
            var result = new ActionResult("success");
            result.AddParameter("item", "ball");

            Assert.IsTrue(result.Parameters.ContainsKey("item"));
            Assert.AreEqual("ball", result.Parameters["item"]);
        }

        [TestMethod]
        public void ActionResult_AddParameter_ShouldReturnSameInstance()
        {
            var result = new ActionResult("success");
            var returnedResult = result.AddParameter("item", "ball");

            Assert.AreEqual(result, returnedResult);
        }
    }
}
</antArtifact>

These unit tests cover the main functionality of our Common project components:

1. ParsedAction
   - Constructor
   - Adding noun groups
   - Adding prepositions
   - Retrieving noun groups and prepositions
   - Checking for prepositions

2. ParsedActionResult
   - Constructor
   - Adding ActionResults
   - Adding and retrieving additional data

3. ActionResult
   - Constructor
   - Adding parameters
   - Fluent interface (method chaining)

Key points about these tests:

1. We're testing both the basic functionality (e.g., setting and getting values) and more complex operations (e.g., retrieving all noun groups or prepositions).

2. We're verifying that the fluent interface works as expected, with methods returning the instance for chaining.

3. We're checking edge cases, such as getting data with a default value when the key doesn't exist.

4. We're using various Assert methods to verify the expected outcomes of each operation.

These tests should help ensure that the core classes in the Common project are working as intended. If any of these tests fail, it would indicate a problem in the corresponding functionality that needs to be addressed.

