        {
            var edge = _graph.CreateEdge(fromRoom.Id, toRoom.Id);
            edge.SetSourcePropertyValue("relationType", "exit");
            edge.SetSourcePropertyValue("direction", direction);
            edge.SetTargetPropertyValue("relationType", "deadend");
        }

        public void CreateBiDirectionalExits(Room room1, Room room2, string direction1To2, string direction2To1)
        {
            CreateExit(room1, room2, direction1To2);
            CreateExit(room2, room1, direction2To1);
        }

        public IEnumerable<(string Direction, Room Destination)> GetRoomExits(Room room)
        {
            return room.Edges
                .Where(e => e.GetSourcePropertyValue<string>("relationType") == "exit")
                .Select(e => (e.GetSourcePropertyValue<string>("direction"), e.Target as Room));
        }

        // ... (other methods remain the same)
    }
}
