using System.Collections.Generic;
using Language;

namespace ParserLibrary.Tests
{
    [TestClass]
    public class ParserTests
    {
        private Parser _parser;
        private WorldModel _worldModel;
        private Scope _scope;
        private Guid _playerId;
        private Room _testRoom;

        [TestInitialize]
        public void Initialize()
        {
            _worldModel = new WorldModel();

            // Initialize language manager
            LanguageManager.Initialize("en-US");

            // Set up a simple world for testing
            _testRoom = (Room)_worldModel.CreateRoom("Test Room", "A test room");
            var player = (Person)_worldModel.CreatePerson("Player", "The player", _testRoom);
            _playerId = player.Id;
            var ball = _worldModel.CreateThing("ball", "A red ball", _testRoom);

            // Ensure the player is in the room
            _worldModel.MoveItem(player, null, _testRoom);

            _scope = new Scope(_worldModel, _playerId);
            _parser = new Parser(_scope);

            _scope.UpdateScope();
        }

        [TestMethod]
        public void Parse_ValidLookCommand_ShouldReturnSuccessResult()
        {
            var result = _parser.Parse("look");

            Assert.IsTrue(result.Success);
            Assert.AreEqual("look", result.Action.Verb);
        }

        [TestMethod]
        public void Parse_ValidTakeCommand_ShouldReturnSuccessResult()
        {
            var result = _parser.Parse("take ball");

            Assert.IsTrue(result.Success);
            Assert.AreEqual("get", result.Action.Verb);
            Assert.AreEqual("ball", result.Action.GetNoun());
        }

        [TestMethod]
        public void Parse_InvalidCommand_ShouldReturnFailureResult()
        {
            var result = _parser.Parse("invalidcommand");

            Assert.IsFalse(result.Success);
            Assert.AreEqual("unknown", result.Action.Verb);
        }

        [TestMethod]
        public void Parse_ComplexCommand_ShouldParseCorrectly()
        {
            var result = _parser.Parse("put ball in box");

            Assert.IsTrue(result.Success);
            Assert.AreEqual("put", result.Action.Verb);
            Assert.AreEqual("ball", result.Action.GetNoun());
            Assert.AreEqual("box", result.Action.GetPrepositionTarget("in"));
        }

        [TestMethod]
        public void Parse_CommandWithAlias_ShouldRecognizeAlias()
        {
            var result = _parser.Parse("get ball");

            Assert.IsTrue(result.Success);
            Assert.AreEqual("get", result.Action.Verb);
            Assert.AreEqual("ball", result.Action.GetNoun());
        }

        [TestMethod]
        public void Parse_CommandWithMultipleNouns_ShouldParseAllNouns()
        {
            _worldModel.CreateThing("red book", "A red book", _testRoom);
            _scope.UpdateScope();

            var result = _parser.Parse("take red book");

            Assert.IsTrue(result.Success);
            Assert.AreEqual("get", result.Action.Verb);
            CollectionAssert.AreEqual(new[] { "red", "book" }, result.Action.GetNounGroup());
        }

        [TestMethod]
        public void Parse_CommandWithChoice_ShouldParseCorrectly()
        {
            var result = _parser.Parse("look in ball");

            Assert.IsTrue(result.Success);
            Assert.AreEqual("look", result.Action.Verb);
            Assert.AreEqual("in", result.Action.GetAllPrepositions().First().Key);
            Assert.AreEqual("ball", result.Action.GetAllPrepositions().First().Value);
        }

        [TestMethod]
        public void Parse_CommandWithOutOfScopeItem_ShouldReturnFailureResult()
        {
            var result = _parser.Parse("take sword");

            Assert.IsFalse(result.Success);
            StringAssert.Contains(result.Results[0].Parameters["message"].ToString(), "don't see any sword here");
        }

        [TestMethod]
        public void Parse_EmptyInput_ShouldReturnFailureResult()
        {
            var result = _parser.Parse("");

            Assert.IsFalse(result.Success);
            Assert.AreEqual("unknown", result.Action.Verb);
        }

        [TestMethod]
        public void Parse_InputWithExtraSpaces_ShouldParseCorrectly()
        {
            var result = _parser.Parse("  take   ball  ");

            Assert.IsTrue(result.Success);
            Assert.AreEqual("get", result.Action.Verb);
            Assert.AreEqual("ball", result.Action.GetNoun());
        }

        [TestMethod]
        public void Parse_CaseInsensitiveInput_ShouldParseCorrectly()
        {
            var result = _parser.Parse("TAKE ball");

            Assert.IsTrue(result.Success);
            Assert.AreEqual("get", result.Action.Verb);
            Assert.AreEqual("ball", result.Action.GetNoun());
        }

        [TestMethod]
        public void Parse_VerbSynonyms_ShouldNormalizeCorrectly()
        {
            var takeResult = _parser.Parse("take ball");
            var getResult = _parser.Parse("get ball");
            var pickUpResult = _parser.Parse("pick up ball");

            Assert.IsTrue(takeResult.Success);
            Assert.IsTrue(getResult.Success);
            Assert.IsTrue(pickUpResult.Success);

            Assert.AreEqual("get", takeResult.Action.Verb);
            Assert.AreEqual("get", getResult.Action.Verb);
            Assert.AreEqual("get", pickUpResult.Action.Verb);

            Assert.AreEqual("ball", takeResult.Action.GetNoun());
            Assert.AreEqual("ball", getResult.Action.GetNoun());
            Assert.AreEqual("ball", pickUpResult.Action.GetNoun());
        }

        [TestMethod]
        public void Parse_CommandWithMultiWordNounAndPreposition_ShouldParseCorrectly()
        {
            _worldModel.CreateThing("blue key", "A shiny blue key", _testRoom);
            _worldModel.CreateThing("wooden box", "A sturdy wooden box", _testRoom);
            _scope.UpdateScope();

            var result = _parser.Parse("put blue key in wooden box");

            Assert.IsTrue(result.Success);
            Assert.AreEqual("put", result.Action.Verb);
            CollectionAssert.AreEqual(new[] { "blue", "key" }, result.Action.GetNounGroup(0));
            Assert.AreEqual("in", result.Action.GetAllPrepositions().First().Key);
            Assert.AreEqual("wooden box", result.Action.GetAllPrepositions().First().Value);
        }

        [TestMethod]
        public void Parse_CommandWithMultiplePrepositions_ShouldParseCorrectly()
        {
            var result = _parser.Parse("look under ball on table");

            Assert.IsTrue(result.Success);
            Assert.AreEqual("look", result.Action.Verb);
            Assert.AreEqual("under", result.Action.GetAllPrepositions().First().Key);
            Assert.AreEqual("ball", result.Action.GetAllPrepositions().First().Value);
            Assert.AreEqual("on", result.Action.GetAllPrepositions().Skip(1).First().Key);
            Assert.AreEqual("table", result.Action.GetAllPrepositions().Skip(1).First().Value);
        }

        [TestMethod]
        public void Parse_DirectionCommand_ShouldParseCorrectly()
        {
            var result = _parser.Parse("go north");

            Assert.IsTrue(result.Success);
            Assert.AreEqual("go", result.Action.Verb);
            Assert.AreEqual("north", result.Action.GetNoun());
        }

        [TestMethod]
        public void Parse_InventoryCommand_ShouldParseCorrectly()
        {
            var result = _parser.Parse("inventory");

            Assert.IsTrue(result.Success);
            Assert.AreEqual("inventory", result.Action.Verb);
        }
    }
}
