{
    var parsedAction = new ParsedAction(verb.Name);

    // Handle multi-word nouns
    if (matchResult.TryGetValue("multi", out var multiWords))
    {
        parsedAction.AddNounGroup(multiWords.ToArray());
    }

    // Handle specific variables (like "container" for "look in")
    foreach (var kvp in matchResult.Where(kvp => kvp.Key != "multi" && kvp.Key != "choice" && !kvp.Key.StartsWith("preposition_")))
    {
        parsedAction.AddNounGroup(kvp.Value.ToArray());
    }

    // Handle prepositions
    var prepositions = matchResult.Where(kvp => kvp.Key.StartsWith("preposition_")).ToList();
    foreach (var prepKvp in prepositions)
    {
        string prepName = prepKvp.Key.Substring("preposition_".Length);
        string prepTarget = "";

        // Find the target of this preposition
        // It's usually the next non-preposition item in the match result
        var nextItem = matchResult.FirstOrDefault(kvp => 
            !kvp.Key.StartsWith("preposition_") && 
            matchResult.IndexOfKey(kvp.Key) > matchResult.IndexOfKey(prepKvp.Key));

        if (nextItem.Value != null)
        {
            prepTarget = string.Join(" ", nextItem.Value);
        }

        parsedAction.AddPreposition(prepName, prepTarget);
    }

    // Handle choice (if present)
    if (matchResult.TryGetValue("choice", out var choiceWord))
    {
        // The target of a choice is typically the last item in the multi group
        string choiceTarget = multiWords != null && multiWords.Any() ? multiWords.Last() : "";
        parsedAction.AddPreposition(choiceWord[0], choiceTarget);
    }

    return parsedAction;
}
