using System;
using System.Collections.Generic;
using System.Linq;
using DataStore;
using IFWorldModel.Builders;

namespace IFWorldModel
{
    public class WorldModel : IGraphOperations
    {
        private readonly IGraphOperations _graph;

        public WorldModel()
        {
            _graph = new Graph();
        }

        #region IGraphOperations Implementation
        public IReadOnlyDictionary<Guid, INode> Nodes => _graph.Nodes;

        public void AddNode(INode node) => _graph.AddNode(node);

        public void RemoveNode(Guid nodeId) => _graph.RemoveNode(nodeId);

        public IEdge CreateEdge(Guid sourceId, Guid targetId) => 
            _graph.CreateEdge(sourceId, targetId);

        public void RemoveEdge(IEdge edge) => _graph.RemoveEdge(edge);

        public void ConnectNodes(INode source, INode target) => 
            _graph.ConnectNodes(source, target);

        public void SetEdgeProperty(IEdge edge, string propertyName, object value) => 
            _graph.SetEdgeProperty(edge, propertyName, value);

        public void AddObserver(IGraphEventObserver observer) => 
            _graph.AddObserver(observer);

        public void RemoveObserver(IGraphEventObserver observer) => 
            _graph.RemoveObserver(observer);
        #endregion

        #region World Creation Methods
        public RoomBuilder CreateRoom(string name, string description)
        {
            var room = new Room(name, description);
            AddNode(room);
            return new RoomBuilder(this, room);
        }

        public ThingBuilder<Thing> CreateThing(string name, string description)
        {
            var thing = new Thing(name, description);
            AddNode(thing);
            return new ThingBuilder<Thing>(this, thing);
        }

        public ThingBuilder<T> CreateThing<T>(string name, string description) where T : Thing, new()
        {
            var thing = new T();
            thing.SetName(name).SetDescription(description);
            AddNode(thing);
            return new ThingBuilder<T>(this, thing);
        }

        public ContainerBuilder CreateContainer(string name, string description)
        {
            var container = new Container(name, description);
            AddNode(container);
            return new ContainerBuilder(this, container);
        }

        public PlayerBuilder CreatePlayer(string name, string description)
        {
            var player = new Person(name, description);
            AddNode(player);
            return new PlayerBuilder(this, player);
        }

        public DoorBuilder CreateDoor(string name, string description)
        {
            var door = new Door(name, description);
            AddNode(door);
            return new DoorBuilder(this, door);
        }

        public Person CreatePerson(string name, string description)
        {
            var person = new Person(name, description);
            AddNode(person);
            return person;
        }

        public ExitBuilder CreateExit(Room sourceRoom, string direction)
        {
            return new ExitBuilder(this, sourceRoom, direction);
        }
        #endregion

        #region World Query Methods
        public Room GetPlayerLocation(Guid playerId)
        {
            if (GetThingById(playerId) is Person player)
            {
                return player.Edges.FirstOrDefault()?.Target as Room;
            }
            return null;
        }

        public IEnumerable<Thing> GetRoomContents(Room room)
        {
            return Nodes.Values
                .OfType<Thing>()
                .Where(t => IsInRoom(t, room));
        }

        public IEnumerable<Thing> GetPlayerItems(Guid playerId)
        {
            if (GetThingById(playerId) is Person player)
            {
                return player.Inventory ?? Enumerable.Empty<Thing>();
            }
            return Enumerable.Empty<Thing>();
        }

        public INode GetThingById(Guid id)
        {
            return Nodes.TryGetValue(id, out var node) ? node : null;
        }

        public IEnumerable<(string Direction, Room Destination)> GetRoomExits(Room room)
        {
            if (room == null)
                throw new ArgumentNullException(nameof(room));

            return room.Edges
                .Where(IsValidExit)
                .Select(e => (
                    Direction: GetExitDirection(e),
                    Destination: GetExitDestination(room, e)
                ))
                .Where(exit => exit.Destination != null);
        }

        public string GetRoomDescription(Room room, Guid playerId)
        {
            var description = room.GetFullDescription();
            var contents = GetRoomContents(room).Where(t => t.Id != playerId && t.IsVisible());
            var exits = GetRoomExits(room);

            if (contents.Any())
                description += $"\n\nYou see: {string.Join(", ", contents.Select(t => t.Name))}";

            if (exits.Any())
                description += $"\n\nExits: {string.Join(", ", exits.Select(e => e.Direction))}";

            return description;
        }
        #endregion

        #region World Manipulation Methods
        public WorldModel PlaceItem(Thing item, Thing location)
        {
            MoveItem(item, null, location);
            return this;
        }

        public bool MoveItem(Thing item, Thing fromLocation, Thing toLocation)
        {
            try
            {
                if (fromLocation != null)
                {
                    var fromEdge = item.Edges.FirstOrDefault(e => e.Target == fromLocation);
                    if (fromEdge != null)
                    {
                        RemoveEdge(fromEdge);
                    }
                }

                if (toLocation != null)
                {
                    var edge = new Edge();
                    edge.ConnectNodes(item, toLocation);
                }

                return true;
            }
            catch (Exception)
            {
                return false;
            }
        }
        #endregion

        #region Helper Methods
        private bool IsInRoom(Thing thing, Room room)
        {
            return thing.Edges.Any(e => e.Target == room);
        }

        private bool IsValidExit(IEdge edge) =>
            edge.Target is Room || (edge.Target is Door door && door.IsOpen);

        private string GetExitDirection(IEdge edge) =>
            edge.GetSourcePropertyValue<string>("Direction");

        private Room GetExitDestination(Room sourceRoom, IEdge exit) =>
            exit.Target is Room room ? room :
            exit.Target is Door door && door.IsOpen ? GetRoomBeyondDoor(sourceRoom, door) :
            null;

        private Room GetRoomBeyondDoor(Room currentRoom, Door door) =>
            door.Edges
                .Where(e => e.Target is Room && e.Target != currentRoom)
                .Select(e => e.Target as Room)
                .FirstOrDefault();

