using System;
using System.Linq;
using System.Collections.Generic;
using DataStore;

namespace IFWorldModel.Builders
{
    public class RoomBuilder
    {
        private readonly IGraphOperations _graph;
        private readonly Room _room;
        private readonly Dictionary<string, Action<Room>> _delayedConnections;

        internal RoomBuilder(IGraphOperations graph, Room room)
        {
            _graph = graph ?? throw new ArgumentNullException(nameof(graph));
            _room = room ?? throw new ArgumentNullException(nameof(room));
            _delayedConnections = new Dictionary<string, Action<Room>>();
        }

        public RoomBuilder Dark()
        {
            _room.SetDark(true);
            return this;
        }

        public RoomBuilder Lit()
        {
            _room.SetDark(false);
            return this;
        }

        public RoomBuilder SetProperty(string propertyName, object value)
        {
            _room.SetPropertyValue(propertyName, value);
            return this;
        }

        public RoomBuilder WithExit(string direction, Room destination)
        {
            var edge = new Edge();
            edge.ConnectNodes(_room, destination);
            _graph.SetEdgeProperty(edge, "Direction", direction);
            return this;
        }

        public RoomBuilder WithDoor(string direction, Door door)
        {
            var edge = new Edge();
            edge.ConnectNodes(_room, door);
            _graph.SetEdgeProperty(edge, "Direction", direction);
            return this;
        }

        public RoomBuilder WithDelayedConnection(string direction, string roomName, Action<Room> connectionSetup = null)
        {
            _delayedConnections[direction] = connectionSetup ?? (r => WithExit(direction, r));
            return this;
        }

        public RoomBuilder ConnectTo(Room otherRoom, string direction, string returnDirection)
        {
            WithExit(direction, otherRoom);

            var returnEdge = new Edge();
            returnEdge.ConnectNodes(otherRoom, _room);
            _graph.SetEdgeProperty(returnEdge, "Direction", returnDirection);

            return this;
        }

        public RoomBuilder NorthOf(Room otherRoom)
        {
            return ConnectTo(otherRoom, "south", "north");
        }

        public RoomBuilder SouthOf(Room otherRoom)
        {
            return ConnectTo(otherRoom, "north", "south");
        }

        public RoomBuilder EastOf(Room otherRoom)
        {
            return ConnectTo(otherRoom, "west", "east");
        }

        public RoomBuilder WestOf(Room otherRoom)
        {
            return ConnectTo(otherRoom, "east", "west");
        }

        public RoomBuilder WithItem(Thing item)
        {
            var edge = new Edge();
            edge.ConnectNodes(item, _room);
            return this;
        }

        public RoomBuilder WithThing(string name, string description)
        {
            var thing = new Thing(name, description);
            _graph.AddNode(thing);
            return WithItem(thing);
        }

        public RoomBuilder WithContainer(string name, string description, Action<ContainerBuilder> setup = null)
        {
            var container = new Container(name, description);
            _graph.AddNode(container);

            setup?.Invoke(new ContainerBuilder(_graph, container));

            return WithItem(container);
        }

        public RoomBuilder WithScenery(string name, string description)
        {
            var scenery = new Scenery(name, description);
            _graph.AddNode(scenery);
            return WithItem(scenery);
        }

        public RoomBuilder WithDecoration(params (string Name, string Description)[] decorations)
        {
            foreach (var (name, description) in decorations)
            {
                WithScenery(name, description);
            }
            return this;
        }

        public RoomBuilder Configure(Action<Room> configuration)
        {
            configuration(_room);
            return this;
        }

        public RoomBuilder WithDescription(string description)
        {
            _room.SetDescription(description);
            return this;
        }

        public RoomBuilder AllowingActions(params string[] allowedActions)
        {
            _room.SetPropertyValue("AllowedActions", allowedActions);
            return this;
        }

        public RoomBuilder RequiringLight()
        {
            _room.SetPropertyValue("RequiresLight", true);
            return this;
        }

        public RoomBuilder WithCapacity(int capacity)
        {
            _room.SetPropertyValue("Capacity", capacity);
            return this;
        }

        public RoomBuilder WithParserHint(string syntax)
        {
            _room.SetParserHint(syntax);
            return this;
        }

        public Room Build()
        {
            return _room;
        }

        public Room BuildWithConnections(IDictionary<string, Room> namedRooms)
