<antArtifact identifier="room-builder" type="application/vnd.ant.code" language="csharp" title="Updated RoomBuilder with Direction System Support">
using System;
using System.Collections.Generic;
using DataStore;

namespace IFWorldModel.Builders
{
    public interface IDirectionSystem
    {
        string GetDirectionName(int directionId);
        string GetOppositeDirection(string direction);
        IEnumerable<string> GetAllDirections();
        bool IsValidDirection(string direction);
    }

    public class RoomBuilder
    {
        private readonly IGraphOperations _graph;
        private readonly Room _room;
        private readonly IDirectionSystem _directionSystem;

        internal RoomBuilder(IGraphOperations graph, Room room, IDirectionSystem directionSystem)
        {
            _graph = graph ?? throw new ArgumentNullException(nameof(graph));
            _room = room ?? throw new ArgumentNullException(nameof(room));
            _directionSystem = directionSystem ?? throw new ArgumentNullException(nameof(directionSystem));
        }

        #region Core Room Properties
        public RoomBuilder Dark()
        {
            _room.SetDark(true);
            return this;
        }

        public RoomBuilder Lit()
        {
            _room.SetDark(false);
            return this;
        }

        public RoomBuilder SetProperty(string propertyName, object value)
        {
            _room.SetPropertyValue(propertyName, value);
            return this;
        }
        #endregion

        #region Directional Connections
        public RoomBuilder ConnectingTo(Room otherRoom, string direction)
        {
            if (!_directionSystem.IsValidDirection(direction))
                throw new ArgumentException($"Invalid direction: {direction}", nameof(direction));

            var edge = new Edge();
            edge.ConnectNodes(_room, otherRoom);
            _graph.SetEdgeProperty(edge, "Direction", direction);

            // Create reverse connection
            var oppositeDirection = _directionSystem.GetOppositeDirection(direction);
            var reverseEdge = new Edge();
            reverseEdge.ConnectNodes(otherRoom, _room);
            _graph.SetEdgeProperty(reverseEdge, "Direction", oppositeDirection);

            return this;
        }

        public RoomBuilder WithoutConnection(string direction)
        {
            if (!_directionSystem.IsValidDirection(direction))
                throw new ArgumentException($"Invalid direction: {direction}", nameof(direction));

            var existingEdge = _room.Edges.FirstOrDefault(e => 
                e.GetSourcePropertyValue<string>("Direction")?.Equals(direction, StringComparison.OrdinalIgnoreCase) == true);

            if (existingEdge != null)
            {
                _graph.RemoveEdge(existingEdge);
            }

            return this;
        }

        public RoomBuilder WithOneWayConnectionTo(Room otherRoom, string direction)
        {
            if (!_directionSystem.IsValidDirection(direction))
                throw new ArgumentException($"Invalid direction: {direction}", nameof(direction));

            var edge = new Edge();
            edge.ConnectNodes(_room, otherRoom);
            _graph.SetEdgeProperty(edge, "Direction", direction);

            return this;
        }
        #endregion

        #region Contents
        public RoomBuilder WithItem(Thing item)
        {
            var edge = new Edge();
            edge.ConnectNodes(item, _room);
            return this;
        }

        public RoomBuilder WithThing(string name, string description)
        {
            var thing = new Thing(name, description);
            _graph.AddNode(thing);
            return WithItem(thing);
        }

        public RoomBuilder WithContainer(string name, string description, Action<ContainerBuilder> setup = null)
        {
            var container = new Container(name, description);
            _graph.AddNode(container);

            setup?.Invoke(new ContainerBuilder(_graph, container));

            return WithItem(container);
        }

        public RoomBuilder WithScenery(string name, string description)
        {
            var scenery = new Scenery(name, description);
            _graph.AddNode(scenery);
            return WithItem(scenery);
        }

        public RoomBuilder WithDecoration(params (string Name, string Description)[] decorations)
        {
            foreach (var (name, description) in decorations)
            {
                WithScenery(name, description);
            }
            return this;
        }
        #endregion

        #region Room Configuration
        public RoomBuilder Configure(Action<Room> configuration)
        {
            configuration(_room);
            return this;
        }

        public RoomBuilder WithDescription(string description)
        {
            _room.SetDescription(description);
            return this;
        }

        public RoomBuilder AllowingActions(params string[] allowedActions)
        {
            _room.SetPropertyValue("AllowedActions", allowedActions);
            return this;
        }

        public RoomBuilder RequiringLight()
        {
            _room.SetPropertyValue("RequiresLight", true);
            return this;
        }

        public RoomBuilder WithCapacity(int capacity)
        {
            _room.SetPropertyValue("Capacity", capacity);
            return this;
        }

        public RoomBuilder WithParserHint(string syntax)
        {
            _room.SetParserHint(syntax);
            return this;
        }
        #endregion

        #region Sensory Properties
        public RoomBuilder WithAtmosphere(string atmosphere)
        {
            _room.SetPropertyValue("Atmosphere", atmosphere);
            return this;
        }

        public RoomBuilder WithTemperature(string temperature)
        {
            _room.SetPropertyValue("Temperature", temperature);
            return this;
        }

        public RoomBuilder WithSound(string ambientSound)
        {
            _room.SetPropertyValue("AmbientSound", ambientSound);
            return this;
        }

        public RoomBuilder WithSmell(string smell)
        {
            _room.SetPropertyValue("Smell", smell);
            return this;
        }
        #endregion

        public Room Build()
        {
            return _room;
        }
    }
}
